      "imsdetailresponsecommon": {
        "type": "object",
        "required": [
          "topology"
        ],
        "properties": {
          "topology": {
            "type": "string",
            "description": "Routing topology for the DIA (Internet) service. Can be either 'A' (peer routing only) or 'B' (subnet routed behind another appliance. See link for more details on DIA (Internet) topologies.",
            "example": "A",
            "externalDocs": {
              "url": "https://dev.telstra.com/content/programmable-network-api/#section/Extra-Detail/Internet-Topologies"
            }
          },
          "serviceid": {
            "type": "string",
            "description": "UUID of the requested DIA (Internet) service",
            "example": "f3b4d131-9470-4858-acac-7504f38e8987"
          },
          "customerid": {
            "type": "string",
            "example": "eac5e374-b92b-4ae1-adca-68bb5f0024af",
            "description": "Internal use"
          },
          "customername": {
            "type": "string",
            "description": "Human-friendly name for the customer tenancy owning the DIA (Internet) service",
            "example": "Internet Service"
          },
          "gateway": {
            "type": "string",
            "description": "Device code for the Internet gateway device. This can be used to determine if a single point of failure exists when building redundant solutions.",
            "example": "agw1.khg8"
          },
          "gatewayint": {
            "type": "string",
            "description": "Interface name for the Internet gateway device. This can be used to determine if a single point of failure exists when building redundant solutions.",
            "example": "xe-0/0/44"
          },
          "vlan": {
            "type": "string",
            "description": "Internal use",
            "example": "22"
          },
          "bandwidth": {
            "type": "integer",
            "description": "maximum capacity of DIA Internet service",
            "example": 100
          },
          "ipv4": {
            "type": "boolean",
            "description": "Flag identifying whether this DIA (Internet) service support IPv4."
          }
        },
        "discriminator": {
          "propertyName": "topology",
          "mapping": {
            "A": "#/components/schemas/A-peer_routing_only",
            "B": "#/components/schemas/B-routed_subnet"
          }
        }
      },
      "A-peer_routing_only": {
        "allOf": [
          {
            "$ref": "#/components/schemas/imsdetailresponsecommon"
          },
          {
            "type": "object",
            "properties": {
              "inet": {
                "type": "object",
                "properties": {
                  "gatewayip": {
                    "type": "string",
                    "description": "IP address of the Internet gateway. This is the address which can be used to specify a static route next-hop address.",
                    "example": "61.87.145.29"
                  },
                  "gatewaycidr": {
                    "type": "integer",
                    "description": "CIDR subnet length for the transit subnet for the Internet gateway.",
                    "example": 30
                  },
                  "addroute": {
                    "type": "boolean",
                    "description": "Always 'false' for Topology type A"
                  }
                }
              }
            }
          }
        ]
      },
      "B-routed_subnet": {
        "allOf": [
          {
            "$ref": "#/components/schemas/imsdetailresponsecommon"
          },
          {
            "type": "object",
            "properties": {
              "inet": {
                "type": "object",
                "properties": {
                  "gatewayip": {
                    "type": "string",
                    "description": "IP address of the Internet gateway. This is the address which can be used to specify a static route next-hop address.",
                    "example": "61.87.145.29"
                  },
                  "gatewaycidr": {
                    "type": "integer",
                    "description": "CIDR subnet length for the transit subnet for the Internet gateway.",
                    "example": 30
                  },
                  "addroute": {
                    "type": "array",
                    "description": "List of routes configured at the DIA (Internet) gateway",
                    "items": {
                      "type": "object",
                      "properties": {
                        "nexthopip": {
                          "type": "string",
                          "description": "IP Address of the next hop for the route configured in the DIA (Internet) gateway",
                          "example": "61.87.145.30"
                        },
                        "subnet": {
                          "type": "array",
                          "items": {
                            "type": "string",
                            "description": "Subnets routed to the 'nexthop' by the DIA (Internet) gateway in CIDR format.",
                            "example": "61.87.177.88/29"
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        ]
      }
