    "/1.0.0/auth/generatetoken": {
      "post": {
        "operationId": "createToken",
        "summary": "Create token",
        "description": "Create an authentication token",
        "tags": [
          "authentication"
        ],
        "security": [],
        "requestBody": {
          "content": {
            "application/x-www-form-urlencoded": {
              "schema": {
                "type": "object",
                "properties": {
                  "grant_type": {
                    "type": "string",
                    "example": "password",
                    "default": "password"
                  },
                  "username": {
                    "type": "string"
                  },
                  "password": {
                    "type": "string"
                  },
                  "otp": {
                    "type": "string",
                    "example": "385923"
                  }
                },
                "required": [
                  "grant_type",
                  "username",
                  "password"
                ]
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "A JSON hash containing the bearer token",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "token_type": {
                      "type": "string",
                      "example": "bearer"
                    },
                    "expires_in": {
                      "type": "integer",
                      "example": 6900
                    },
                    "refresh_token": {
                      "type": "string",
                      "example": "6fdad23ccbc1f8e415fc2cf8cd245dae"
                    },
                    "access_token": {
                      "type": "string",
                      "example": "a8856a1a4e25b23d86fa9ae2ffce3b63"
                    }
                  }
                }
              }
            }
          },
          "412": {
            "description": "Provided authorization grant is invalid"
          }
        }
      }
    },
