            {
              "description": "Uniformly distributed random response delay.",
              "title": "Uniform",
              "type": "object",
              "properties": {
                "lower": {"type": "integer"},
                "upper": {"type": "integer"},
                "type": {
                  "type": "string",
                  "enum": ["uniform"]
                }
              }
            }
          ]},
          {
            "type": "object",
            "properties": {"fixedDelay": {"type": "number"}},
            "example": {"fixedDelay": 500}
          }
        ]}}},
        "required": true
      },
      "responses": {"200": {"description": "Settings successfully updated"}},
      "tags": ["System"]
    }},
