    "/api/services/app/Activity/GetAll": {
      "get": {
        "tags": [
          "Activity"
        ],
        "summary": "获取活动列表",
        "parameters": [
          {
            "name": "filter",
            "in": "query",
            "description": "过滤词",
            "schema": {
              "type": "string",
              "description": "过滤词",
              "nullable": true
            }
          },
          {
            "name": "activityTime",
            "in": "query",
            "description": "日历中某一天的活动",
            "schema": {
              "type": "string",
              "description": "日历中某一天的活动",
              "format": "date-time",
              "nullable": true
            }
          },
          {
            "name": "types",
            "in": "query",
            "description": "活动类型\n* `0` - Activity:活动\n* `1` - Article:文章\n* `2` - Video:视频\n* `3` - Meeting:会议",
            "schema": {
              "type": "array",
              "items": {
                "$ref": "#/components/schemas/ActivityType"
              },
              "description": "活动类型",
              "nullable": true
            },
            "x-enumNames": [
              "Activity",
              "Article",
              "Video",
              "Meeting"
            ]
          },
          {
            "name": "categories",
            "in": "query",
            "description": "活动分类",
            "schema": {
              "type": "array",
              "items": {
                "type": "integer",
                "format": "int32"
              },
              "description": "活动分类",
              "nullable": true
            }
          },
          {
            "name": "tags",
            "in": "query",
            "description": "关键字Id列表",
            "schema": {
              "type": "array",
              "items": {
                "type": "integer",
                "format": "int32"
              },
              "description": "关键字Id列表",
              "nullable": true
            }
          },
          {
            "name": "isSpecialColumn",
            "in": "query",
            "description": "是否专栏",
            "schema": {
              "type": "boolean",
              "description": "是否专栏"
            }
          },
          {
            "name": "isInterview",
            "in": "query",
            "description": "是否专访",
            "schema": {
              "type": "boolean",
              "description": "是否专访"
            }
          },
          {
            "name": "activityId",
            "in": "query",
            "description": "相关Id",
            "schema": {
              "type": "integer",
              "description": "相关Id",
              "format": "int64",
              "nullable": true
            }
          },
          {
            "name": "isApproved",
            "in": "query",
            "description": "是否包含审核",
            "schema": {
              "type": "boolean",
              "description": "是否包含审核",
              "nullable": true
            }
          },
          {
            "name": "isDeleted",
            "in": "query",
            "description": "是否包含删除",
            "schema": {
              "type": "boolean",
              "description": "是否包含删除",
              "nullable": true
            }
          },
          {
            "name": "isActive",
            "in": "query",
            "description": "是否取消",
            "schema": {
              "type": "boolean",
              "description": "是否取消",
              "nullable": true
            }
          },
          {
            "name": "from",
            "in": "query",
            "description": "开始日期",
            "schema": {
              "type": "string",
              "description": "开始日期",
              "format": "date-time",
              "nullable": true
            }
          },
          {
            "name": "to",
            "in": "query",
            "description": "结束日期",
            "schema": {
              "type": "string",
              "description": "结束日期",
              "format": "date-time",
              "nullable": true
            }
          },
          {
            "name": "activityTimeFrom",
            "in": "query",
            "description": "活动日期开始",
            "schema": {
              "type": "string",
              "description": "活动日期开始",
              "format": "date-time",
              "nullable": true
            }
          },
          {
            "name": "activityTimeFromTo",
            "in": "query",
            "description": "活动日期结束",
            "schema": {
              "type": "string",
              "description": "活动日期结束",
              "format": "date-time",
              "nullable": true
            }
          },
          {
            "name": "specialColumnId",
            "in": "query",
            "description": "指定的专栏Id",
            "schema": {
              "type": "integer",
              "description": "指定的专栏Id",
              "format": "int64",
              "nullable": true
            }
          },
          {
            "name": "isComing",
            "in": "query",
            "description": "是否获取即将发生的",
            "schema": {
              "type": "boolean",
              "description": "是否获取即将发生的"
            }
          },
          {
            "name": "userId",
            "in": "query",
            "description": "用户相关的Id",
            "schema": {
              "type": "integer",
              "description": "用户相关的Id",
              "format": "int64",
              "nullable": true
            }
          },
          {
            "name": "orderBy.type",
            "in": "query",
            "description": "排序方式\n* `0` - Latest:创建时间\n* `1` - Populartiy:热度\n* `2` - Distance:距离",
            "schema": {
              "$ref": "#/components/schemas/OrderByType"
            },
            "x-enumNames": [
              "Latest",
              "Populartiy",
              "Distance"
            ]
          },
          {
            "name": "orderBy.isASC",
            "in": "query",
            "description": "是否升序",
            "schema": {
              "type": "boolean",
              "description": "是否升序"
            }
          },
          {
            "name": "orderBy.longitude",
            "in": "query",
            "description": "经度X",
            "schema": {
              "type": "number",
              "description": "经度X",
              "format": "double",
              "nullable": true
            }
          },
          {
            "name": "orderBy.latitude",
            "in": "query",
            "description": "维度Y",
            "schema": {
              "type": "number",
              "description": "维度Y",
              "format": "double",
              "nullable": true
            }
          },
          {
            "name": "filters",
            "in": "query",
            "description": "按数字筛选\n按值筛选",
            "schema": {
              "type": "array",
              "items": {
                "$ref": "#/components/schemas/ActivityFilterExpression"
              },
              "description": "按数字筛选",
              "nullable": true
            }
          },
          {
            "name": "isUpcomingMeeting",
            "in": "query",
            "description": "是否即将开始的会议（7天内）",
            "schema": {
              "type": "boolean",
              "description": "是否即将开始的会议（7天内）"
            }
          },
          {
            "name": "hasAttachment",
            "in": "query",
            "description": "是否包含附件",
            "schema": {
              "type": "boolean",
              "description": "是否包含附件"
            }
          },
          {
            "name": "sorting",
            "in": "query",
            "description": "排序规则",
            "schema": {
              "type": "string",
              "description": "排序规则",
              "nullable": true
            }
          },
          {
            "name": "maxResultCount",
            "in": "query",
            "description": "最大数量",
            "schema": {
              "maximum": 1000,
              "minimum": 1,
              "type": "integer",
              "description": "最大数量",
              "format": "int32"
            }
          },
          {
            "name": "maxSubResultCount",
            "in": "query",
            "description": "子集的最大数量",
            "schema": {
              "maximum": 1000,
              "minimum": 1,
              "type": "integer",
              "description": "子集的最大数量",
              "format": "int32"
            }
          },
          {
            "name": "skipCount",
            "in": "query",
            "description": "跳过数量",
            "schema": {
              "maximum": 2147483647,
              "minimum": 0,
              "type": "integer",
              "description": "跳过数量",
              "format": "int32"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "text/plain": {
                "schema": {
                  "$ref": "#/components/schemas/ActivityInListDtoPagedResultDto"
                }
              },
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ActivityInListDtoPagedResultDto"
                }
              },
              "text/json": {
                "schema": {
                  "$ref": "#/components/schemas/ActivityInListDtoPagedResultDto"
                }
              }
            }
          }
        }
      }
    },
