        ]
      }
    },
    "/2/tweets/search/recent": {
      "get": {
        "description": "Returns Tweets from the last 7 days that match a search query.",
        "operationId": "tweetsRecentSearch",
        "parameters": [
          {
            "description": "One query/rule/filter for matching Tweets. Up to 512 characters.",
            "in": "query",
            "name": "query",
            "required": true,
