							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/MRPWorkCenter_1_000.json#/definitions/PagedMRPWorkCenter"
								}
							}
						}
					},
					"207": {
						"description": "Operação realizada parcialmente com sucesso",
