							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/MRPWorkCenter_1_000.json#/definitions/ListOfMRPWorkCenter"
							}
						}
					},
					"description": "Objeto do centro de trabalho do MRP que deve ser adicionado ou atualizado",
					"required": true
				}
			},
			"get": {
				"tags": [
					"mrpworkcenter"
				],
				"summary": "Lista de centros de trabalho do MRP",
				"description": "Retorna lista de centros de trabalho do MRP",
				"operationId": "getmrpworkcenter",
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Order"
					},
