					},
					{
						"name": "branchId",
						"in": "query",
						"description": "Código da filial",
						"required": false,
						"schema": {
							"type": "string"
						}
					},
					{
						"name": "workcenter",
						"in": "query",
						"description": "Código do centro de trabalho",
						"required": false,
						"schema": {
							"type": "string"
						}
					}
				],
				"responses": {
