				"summary": "Retorna um centro de trabalho do MRP",
				"description": "Retorna um centro de trabalho do MRP",
				"operationId": "getworkcenter",
				"parameters": [
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Fields"
					},
					{
						"$ref": "#/components/parameters/branchId"
					},
					{
						"$ref": "#/components/parameters/workcenter"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/MRPWorkCenter_1_000.json#/definitions/MRPWorkCenter"
								}
