		"parameters": {
			"branchId": {
				"name": "branchId",
				"in": "path",
				"description": "Codigo da filial do centro de trabalho do MRP",
				"required": true,
				"schema": {
					"type": "string",
					"format": "char(2)"
				}
			},
			"workcenter": {
				"name": "workcenter",
				"in": "path",
				"description": "Codigo do centro de trabalho do MRP",
				"required": true,
				"schema": {
					"type": "string",
					"format": "char(6)"
				}
			}
		},
		"schemas": {}
	}
