{
    "openapi": "3.0.3",
    "info": {
        "title": "Cloud PAW Management",
        "description": "Microsoft Endpoint Manager (Intune) Cloud Privileged Access Workstation (PAW) Lifecycle Management and Deployment App.",
        "contact": {
            "name": "Elliot Huffman",
            "email": "elliot.huffman@microsoft.com"
        },
        "license": {
            "name": "MIT",
            "url": "https://github.com/microsoft/Cloud-PAW-Management/blob/main/LICENSE"
        },
        "version": "appVersionFromConfigEngine"
    },
    "externalDocs": {
        "description": "Official Documentation",
        "url": "https://github.com/microsoft/Cloud-PAW-Management/wiki"
    },
    "servers": [
        {
            "url": "https://contoso.local:443/",
            "description": "A secured connection to the app, only secured connections should be used for production."
        },
        {
            "url": "http://contoso.local:80/",
            "description": "Used only for local dev, never host this app without network encryption."
        }
    ],
    "tags": [
        {
            "name": "Lifecycle Management",
            "description": "Device Management",
            "externalDocs": {
                "description": "Read the wiki page",
                "url": "https://github.com/microsoft/Cloud-PAW-Management/wiki/PAW-Lifecycle-Management-API-Endpoints"
            }
        },
        {
            "name": "Infrastructure Deployment",
            "description": "Coming soon!",
            "externalDocs": {
                "description": "Read the wiki page",
                "url": "https://github.com/microsoft/Cloud-PAW-Management/wiki/Infrastructure-Deployment-API-Endpoints"
            }
        },
        {
            "name": "Debug Mode",
            "description": "Endpoints that are exposed only in debug mode. You can use these to see as the app sees.",
            "externalDocs": {
                "description": "Read the wiki page",
                "url": "https://github.com/microsoft/Cloud-PAW-Management/wiki/Debug-API-Endpoints"
            }
        }
    ],
    "paths": {
        "/API/Lifecycle/AutopilotDevice": {
            "get": {
                "operationId": "lifecycleGetAutopilotDevice",
                "tags": [
                    "Lifecycle Management"
                ],
                "summary": "Gets all Autopilot Device(s)",
                "description": "Returns a list of all devices registered to the Windows Autopilot service in Endpoint Manager.",
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "minItems": 0,
                                    "items": {
                                        "$ref": "#/components/schemas/microsoft.graph.windowsAutopilotDeviceIdentity"
                                    }
                                }
                            }
                        }
                    },
                    "400": {
                        "description": "Invalid Input. This is usually due to not following content type or input validation constraints."
                    },
                    "500": {
                        "description": "Internal error. This is usually thrown because of a misconfiguration on the PAW architecture structure or some other internal error. See the server console logs/app insights for more info."
                    },
                    "520": {
                        "description": "App is starting still. Feature is not available. Please try again later."
                    },
                    "525": {
                        "description": "Not deployed. Please deploy the infra before using the app."
                    }
                }
            }
        },
        "/API/Lifecycle/PAW": {
            "get": {
                "operationId": "lifecycleGetPAWDevice",
                "tags": [
                    "Lifecycle Management"
                ],
                "summary": "Get all commissioned device(s).",
                "description": "Returns a list of all commissioned devices managed by the Cloud PAW Management app.",
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "minItems": 0,
                                    "items": {
                                        "$ref": "#/components/schemas/pawDevice"
                                    }
                                }
                            }
                        }
                    },
                    "520": {
                        "description": "App is starting still. Feature is not available. Please try again later."
                    },
                    "525": {
                        "description": "Not deployed. Please deploy the infra before using the app."
                    }
                }
            }
        },
        "/API/Lifecycle/PAW/{deviceID}/Commission": {
            "post": {
                "operationId": "lifecyclePostPAWCommission",
                "tags": [
                    "Lifecycle Management"
                ],
                "summary": "Commission a new device.",
                "description": "Commissions a new device, into the device hierarchy and appends appropriate metadata and initial policies. Appends required metadata to proper locations.",
                "parameters": [
                    {
                        "name": "deviceID",
                        "description": "The AAD Device ID of the autopilot device to commission into a managed device.",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "pattern": "^[0-9a-f]{8}-[0-9a-f]{4}-[0-5][0-9a-f]{3}-[089ab][0-9a-f]{3}-[0-9a-f]{12}$",
                            "maxLength": 36,
                            "minLength": 36,
                            "example": "75da7fa4-4a04-44c8-8f2c-c1b2fa29aa51"
                        }
                    }
                ],
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "type": "object",
                                "properties": {
                                    "type": {
                                        "type": "string",
                                        "enum": [
                                            "Privileged",
                                            "Developer",
                                            "Tactical-CR",
                                            "Tactical-RRR"
                                        ]
                                    }
                                }
                            },
                            "examples": {
                                "Privileged": {
                                    "summary": "Privileged",
                                    "description": "In this example, the target device in the URL path is commissioned as a PAW.",
                                    "value": "{\"type\": \"Privileged\"}"
                                },
                                "Developer": {
                                    "summary": "Developer",
                                    "description": "In this example, the target device in the URL path is commissioned as a developer workstation.",
                                    "value": "{\"type\": \"Developer\"}"
                                },
                                "Tactical-CR": {
                                    "summary": "Tactical: Compromise Recovery",
                                    "description": "In this example, the target device in the URL path is commissioned as a Tactical PAW for CR operations.",
                                    "value": "{\"type\": \"Tactical-CR\"}"
                                },
                                "Tactical-RRR": {
                                    "summary": "Tactical: Rapid Ransomware Response",
                                    "description": "In this example, the target device in the URL path is commissioned as a Tactical PAW for RRR operations.",
                                    "value": "{\"type\": \"Tactical-RRR\"}"
                                }
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/pawDevice"
                                }
                            }
                        }
                    },
                    "520": {
                        "description": "App is starting still. Feature is not available. Please try again later."
                    },
                    "525": {
                        "description": "Not deployed. Please deploy the infra before using the app."
                    }
                }
            },
            "delete": {
                "operationId": "lifecycleDeletePAWCommission",
                "tags": [
                    "Lifecycle Management"
                ],
                "summary": "Decommission specified device",
                "parameters": [
                    {
                        "name": "deviceID",
                        "description": "The AAD Device ID of the autopilot device to decommission into a enterprise/user class device.",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "pattern": "^[0-9a-f]{8}-[0-9a-f]{4}-[0-5][0-9a-f]{3}-[089ab][0-9a-f]{3}-[0-9a-f]{12}$",
                            "maxLength": 36,
                            "minLength": 36,
                            "example": "75da7fa4-4a04-44c8-8f2c-c1b2fa29aa51"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "boolean",
                                    "example": true
                                }
                            }
                        }
                    },
                    "520": {
                        "description": "App is starting still. Feature is not available. Please try again later."
                    },
                    "525": {
                        "description": "Not deployed. Please deploy the infra before using the app."
                    }
                }
            }
        },
        "/API/Lifecycle/PAW/{deviceID}/Assign": {
            "get": {
                "operationId": "lifecycleGetDeviceAssignment",
                "tags": [
                    "Lifecycle Management"
                ],
                "summary": "List user assignments on PAW",
                "description": "Lists all of the users that are currently assigned to the specified device.",
                "parameters": [
                    {
                        "name": "deviceID",
                        "description": "The AAD Device ID of the managed device that will have all the user assignments listed.",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "pattern": "^[0-9a-f]{8}-[0-9a-f]{4}-[0-5][0-9a-f]{3}-[089ab][0-9a-f]{3}-[0-9a-f]{12}$",
                            "maxLength": 36,
                            "minLength": 36,
                            "example": "75da7fa4-4a04-44c8-8f2c-c1b2fa29aa51"
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "minItems": 0,
                                    "items": {
                                        "$ref": "#/components/schemas/microsoft.graph.user"
                                    }
                                }
                            }
                        }
                    },
                    "520": {
                        "description": "App is starting still. Feature is not available. Please try again later."
                    },
                    "525": {
                        "description": "Not deployed. Please deploy the infra before using the app."
                    }
                }
            },
            "post": {
                "operationId": "lifecyclePostDeviceAssignment",
                "tags": [
                    "Lifecycle Management"
                ],
                "summary": "Replace assignment list",
                "description": "Replace the list of currently assigned users on the specified device.",
                "parameters": [
                    {
                        "name": "deviceID",
                        "description": "The AAD Device ID of the managed device that will have all the user assignments listed.",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "pattern": "^[0-9a-f]{8}-[0-9a-f]{4}-[0-5][0-9a-f]{3}-[089ab][0-9a-f]{3}-[0-9a-f]{12}$",
                            "maxLength": 36,
                            "minLength": 36,
                            "example": "75da7fa4-4a04-44c8-8f2c-c1b2fa29aa51"
                        }
                    }
                ],
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "type": "object",
                                "properties": {
                                    "userList": {
                                        "type": "array",
                                        "items": {
                                            "type": "string",
                                            "minLength": 5
                                        }
                                    }
                                }
                            },
                            "examples": {
                                "1:1 map": {
                                    "summary": "1:1 User Mapping",
                                    "description": "This example is the security best practice of having only one user mapped to a managed device.",
                                    "value": "{\"userList\": [\"user@contoso.com\"]}"
                                },
                                "Multi-User Managed Device": {
                                    "summary": "Multi-User Assignment",
                                    "description": "This example is the security best practice of having only one user mapped to a managed device.",
                                    "value": "{\"userList\": [\"user@contoso.com\",\"anotherUser@contoso.com\"]}"
                                }
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "minItems": 0,
                                    "items": {
                                        "$ref": "#/components/schemas/microsoft.graph.user"
                                    }
                                }
                            }
                        }
                    },
                    "520": {
                        "description": "App is starting still. Feature is not available. Please try again later."
                    },
                    "525": {
                        "description": "Not deployed. Please deploy the infra before using the app."
                    }
                }
            },
            "delete": {
                "operationId": "lifecycleDeleteDeviceAssignment",
                "tags": [
                    "Lifecycle Management"
                ],
                "summary": "Delete user assignment.",
                "description": "Remove the specified user from the device and if they were the last user, wipe the device for the next user. If multiple assignments exist and only part of the users are specified, all remaining users are un-touched and the device is not wiped.",
                "parameters": [
                    {
                        "name": "deviceID",
                        "description": "The AAD Device ID of the managed device that will have the user assignments deleted.",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "pattern": "^[0-9a-f]{8}-[0-9a-f]{4}-[0-5][0-9a-f]{3}-[089ab][0-9a-f]{3}-[0-9a-f]{12}$",
                            "maxLength": 36,
                            "minLength": 36,
                            "example": "75da7fa4-4a04-44c8-8f2c-c1b2fa29aa51"
                        }
                    }
                ],
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "type": "object",
                                "properties": {
                                    "userList": {
                                        "type": "array",
                                        "items": {
                                            "type": "string",
                                            "minLength": 5
                                        }
                                    }
                                }
                            },
                            "examples": {
                                "Single User": {
                                    "summary": "Unassign one user",
                                    "description": "Remove a single user assignment from a managed device.",
                                    "value": "{\"userList\": [\"user@contoso.com\"]}"
                                },
                                "Multiple Users": {
                                    "summary": "Unassign multiple users",
                                    "description": "Remove multiple user assignments from a managed device.",
                                    "value": "{\"userList\": [\"user@contoso.com\",\"anotherUser@contoso.com\"]}"
                                }
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "minItems": 0,
                                    "items": {
                                        "$ref": "#/components/schemas/microsoft.graph.user"
                                    }
                                }
                            }
                        }
                    },
                    "520": {
                        "description": "App is starting still. Feature is not available. Please try again later."
                    },
                    "525": {
                        "description": "Not deployed. Please deploy the infra before using the app."
                    }
                }
            }
        },
        "/Docs": {
            "get": {
                "operationId": "swaggerUI",
                "tags": [
                    "Debug Mode"
                ],
                "summary": "Render this Swagger UI",
                "description": "Runs the Swagger UI render to render the OpenAPI spec that you are viewing now.",
                "responses": {
                    "200": {
                        "description": "Returns the HTML for Swagger UI renderer's output"
                    }
                }
            }
        },
        "/Debug/accessToken": {
            "get": {
                "operationId": "debugAccessToken",
                "tags": [
                    "Debug Mode"
                ],
                "summary": "Returns an access token in the app's auth context",
                "description": "Returns the access token that the web server is using for authenticating to the MS Graph API.",
                "responses": {
                    "200": {
                        "description": "Successful Operation. You can securely decode the access token using https://jwt.ms",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/AccessToken"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/Debug/envVar": {
            "get": {
                "operationId": "debugEnvVar",
                "tags": [
                    "Debug Mode"
                ],
                "summary": "List Environmental Variables",
                "description": "Returns a list of the enviornmental variables accessible to the API server",
                "responses": {
                    "200": {
                        "description": "Successful Operation",
                        "content": {
                            "application/json": {
                                "example": {
                                    "debug": "true",
                                    "Scope_Tag": "Cloud-PAW-Mgmt",
                                    "port": "4000"
                                },
                                "schema": {
                                    "$ref": "#/components/schemas/EnvironmentalVariables"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/Debug/testPost": {
            "post": {
                "operationId": "debugPost",
                "tags": [
                    "Debug Mode"
                ],
                "summary": "Returns posted data to the caller.",
                "description": "Returns JSON data posted to the endpoint to test if the server is getting posts and can parse them.",
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "type": "object",
                                "additionalProperties": {
                                    "type": "object"
                                }
                            },
                            "examples": {
                                "Hello World": {
                                    "summary": "Generic Hello World",
                                    "description": "This example is a generic hello world example where the property name is hello and the value is world.",
                                    "value": "{\"Hello\": \"World!\"}"
                                },
                                "Multi-Property": {
                                    "summary": "Object with multiple properties",
                                    "description": "This example is an object with multiple properties.",
                                    "value": "{\"abc\": \"123\", \"def\": \"!@#\"}"
                                }
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object",
                                    "additionalProperties": {
                                        "type": "object"
                                    }
                                },
                                "examples": {
                                    "Hello World": {
                                        "summary": "Generic Hello World",
                                        "description": "This example is a generic hello world example where the property name is hello and the value is world.",
                                        "value": "{\"Hello\": \"World!\"}"
                                    },
                                    "Multi-Property": {
                                        "summary": "Object with multiple properties",
                                        "description": "This example is an object with multiple properties.",
                                        "value": "{\"abc\": \"123\", \"def\": \"!@#\"}"
                                    }
                                }
                            }
                        }
                    },
                    "400": {
                        "description": "Bad Request",
                        "content": {
                            "text/html": {
                                "schema": {
                                    "type": "string",
                                    "example": "<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n<meta charset=\"utf-8\">\n<title>Error</title>\n</head>\n<body>\n<pre>SyntaxError: Unexpected end of JSON input<br> &nbsp; &nbsp;at JSON.parse (&lt;anonymous&gt;)<br> &nbsp; &nbsp;at parse (json.js:89:19)<br></pre>\n</body>\n</html>"
                                }
                            }
                        }
                    }
                }
            }
        },
        "/Debug/roleScopeTag": {
            "post": {
                "operationId": "debugPostRoleScopeTag",
                "tags": [
                    "Debug Mode"
                ],
                "summary": "Create a new Role Scope Tag.",
                "description": "Creates a new Endpoint Manager role scope tag using the MS Graph API.",
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "type": "object",
                                "properties": {
                                    "name": {
                                        "type": "string",
                                        "maxLength": 128,
                                        "minLength": 1
                                    },
                                    "description": {
                                        "type": "string",
                                        "maxLength": 1024
                                    }
                                },
                                "required": [
                                    "name"
                                ]
                            },
                            "examples": {
                                "Name only": {
                                    "summary": "Name only tag creation",
                                    "description": "Create a role scope tag with a name only.",
                                    "value": "{\"name\": \"Cloud-PAW-Management\"}"
                                },
                                "Name and Description": {
                                    "summary": "Name and Description tag creation",
                                    "description": "Create a role scope tag with a name and a description field.",
                                    "value": "{\"name\": \"Cloud-PAW-Management\", \"description\": \"PAWSecGrp=9ed1e179-e179-9ed1-79e1-d19e79e1d19e\\nUsrSecGrp=9ed1e179-e179-9ed1-79e1-d19e79e1d19e\\nSiloRootGrp=9ed1e179-e179-9ed1-79e1-d19e79e1d19e\\nBrkGls=9ed1e179-e179-9ed1-79e1-d19e79e1d19e\\nUsrTag=9ed1e179-e179-9ed1-79e1-d19e79e1d19e\"}"
                                }
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object",
                                    "$ref": "#/components/schemas/roleScopeTag"
                                }
                            }
                        }
                    }
                }
            },
            "get": {
                "operationId": "debugGetRoleScopeTag",
                "tags": [
                    "Debug Mode"
                ],
                "summary": "Return array of role scope tags.",
                "description": "Returns an array of all the role scope tag objects from the MS Graph API.",
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "minItems": 0,
                                    "items": {
                                        "$ref": "#/components/schemas/roleScopeTag"
                                    }
                                }
                            }
                        }
                    }
                }
            }
        },
        "/Debug/roleScopeTag/{identifier}": {
            "get": {
                "operationId": "debugGetRoleScopeTagByIdentifier",
                "tags": [
                    "Debug Mode"
                ],
                "summary": "Get the specific role scope tag",
                "description": "Get the role scope tag by the name of the tag. Returns the first match, if more than one match exists. This is possible if a duplicate display name value is specified during scope tag creation via the Graph API.",
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "array",
                                    "maxItems": 1,
                                    "minItems": 0,
                                    "items": {
                                        "$ref": "#/components/schemas/roleScopeTag"
                                    }
                                }
                            }
                        }
                    }
                },
                "parameters": [
                    {
                        "name": "identifier",
                        "description": "The display name property of the role scope tag to retrieve.",
                        "in": "path",
                        "required": true,
                        "schema": {
                            "type": "string",
                            "maxLength": 128,
                            "minLength": 1,
                            "example": "Privileged-Security-Management"
                        }
                    }
                ]
            },
            "patch": {
                "operationId": "debugUpdateRoleScopeTagByIdentifier",
                "tags": [
                    "Debug Mode"
                ],
                "summary": "Update the specified role scope tag",
                "description": "Update the specified role scope tag's values. The ID field is read only but can be specified to update the name field's value while operating on the specified entity ID.",
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "type": "object",
                                "properties": {
                                    "description": {
                                        "type": "string",
                                        "maxLength": 1024,
                                        "minLength": 0
                                    },
                                    "ID": {
                                        "type": "number",
                                        "minimum": 1
                                    }
                                },
                                "required": [
                                    "description"
                                ]
                            },
                            "examples": {
                                "Update Description": {
                                    "summary": "Update description only",
                                    "description": "Updates the description field only as the name is used as the entity identifier.",
                                    "value": "{\"description\": \"Hello world!\"}"
                                },
                                "Update Name and Description": {
                                    "summary": "Update name and description",
                                    "description": "Updates the name and the description fields as the ID is used as the entity identifier, allowing the name parameter to be written as the new display name. The name's value is grabbed from the identifier parameter.",
                                    "value": "{\"ID\": 3, \"description\": \"PAWSecGrp=9ed1e179-e179-9ed1-79e1-d19e79e1d19e\\nUsrSecGrp=9ed1e179-e179-9ed1-79e1-d19e79e1d19e\\nSiloRootGrp=9ed1e179-e179-9ed1-79e1-d19e79e1d19e\\nBrkGls=9ed1e179-e179-9ed1-79e1-d19e79e1d19e\\nUsrTag=9ed1e179-e179-9ed1-79e1-d19e79e1d19e\"}"
                                }
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "OK",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object",
                                    "$ref": "#/components/schemas/roleScopeTag"
                                }
                            }
                        }
                    }
                },
                "parameters": [
                    {
                        "name": "identifier",
                        "description": "The display name property of the role scope tag to update.",
                        "in": "path",
                        "required": true,
                        "schema": {
