              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/TooManyRequestsException"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "flowArn",
            "in": "path",
            "required": true,
            "description": "The flow that you want to add outputs to.",
            "schema": { "type": "string" }
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["outputs"],
                "properties": {
                  "outputs": {
                    "description": "A list of outputs that you want to add.",
                    "type": "array",
                    "items": { "$ref": "#/components/schemas/AddOutputRequest" }
                  }
                }
              }
            }
          }
        },
        "summary": "Add Flow Outputs"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ]
    },
    "/v1/flows/{flowArn}/source": {
      "post": {
        "operationId": "AddFlowSources",
        "description": "Adds Sources to flow",
        "responses": {
          "201": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/AddFlowSourcesResponse"
                }
              }
            }
          },
          "480": {
            "description": "BadRequestException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/BadRequestException" }
              }
            }
          },
          "481": {
            "description": "InternalServerErrorException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalServerErrorException"
                }
              }
            }
          },
          "482": {
            "description": "ForbiddenException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ForbiddenException" }
              }
            }
          },
          "483": {
            "description": "NotFoundException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/NotFoundException" }
              }
            }
          },
          "484": {
            "description": "ServiceUnavailableException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/ServiceUnavailableException"
                }
              }
            }
          },
          "485": {
            "description": "TooManyRequestsException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/TooManyRequestsException"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "flowArn",
            "in": "path",
            "required": true,
            "description": "The flow that you want to mutate.",
            "schema": { "type": "string" }
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["sources"],
                "properties": {
                  "sources": {
                    "description": "A list of sources that you want to add.",
                    "type": "array",
                    "items": { "$ref": "#/components/schemas/SetSourceRequest" }
                  }
                }
              }
            }
          }
        },
        "summary": "Add Flow Sources"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ]
    },
    "/v1/flows/{flowArn}/vpcInterfaces": {
      "post": {
        "operationId": "AddFlowVpcInterfaces",
        "description": "Adds VPC interfaces to flow",
        "responses": {
          "201": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/AddFlowVpcInterfacesResponse"
                }
              }
            }
