    "/developer": {
      "get": {
        "summary": "Get Logged-in Developer",
        "tags": [
          "developer"
        ],
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "description": "",
                  "type": "object",
                  "properties": {
                    "updated_at": {
                      "type": "number"
                    },
                    "meta": {
                      "type": "string",
                      "minLength": 1
                    },
                    "created_at": {
                      "type": "number"
                    },
                    "email": {
                      "type": "string",
                      "minLength": 1
                    },
                    "consumer": {
                      "type": "object",
                      "properties": {
                        "id": {
                          "type": "string",
                          "minLength": 1
                        }
                      },
                      "required": [
                        "id"
                      ]
                    },
                    "id": {
                      "type": "string",
                      "minLength": 1
                    },
                    "status": {
                      "type": "number"
                    }
                  },
                  "required": [
                    "updated_at",
                    "meta",
                    "created_at",
                    "email",
                    "consumer",
                    "id",
                    "status"
                  ],
                  "x-examples": {
                    "example-1": {
                      "updated_at": 1622818516,
                      "meta": "{\"full_name\":\"User\"}",
                      "created_at": 1622818473,
                      "email": "user@example.com",
                      "consumer": {
                        "id": "06e5ea74-211e-4454-92cb-3f99bce4db10"
                      },
                      "id": "7581dec6-3f16-4509-86ef-b5c65179a532",
                      "status": 0
                    }
                  }
                },
                "examples": {
                  "success": {
                    "value": {
                      "updated_at": 1622818516,
                      "meta": "{\"full_name\":\"User\"}",
                      "created_at": 1622818473,
                      "email": "user@example.com",
                      "consumer": {
