{
  "openapi": "3.0.0",
  "info": {
    "version": "2016-11-28",
    "x-release": "v4",
    "title": "Amazon Lex Runtime Service",
    "description": "Amazon Lex provides both build and runtime endpoints. Each endpoint provides a set of operations (API). Your conversational bot uses the runtime API to understand user utterances (user input text or voice). For example, suppose a user says \"I want pizza\", your bot sends this input to Amazon Lex using the runtime API. Amazon Lex recognizes that the user request is for the OrderPizza intent (one of the intents defined in the bot). Then Amazon Lex engages in user conversation on behalf of the bot to elicit required information (slot values, such as pizza size and crust type), and then performs fulfillment activity (that you configured when you created the bot). You use the build-time API to create and manage your Amazon Lex bot. For a list of build-time operations, see the build-time API, .",
    "x-categories": ["machine-learning"],
    "x-logo": {
      "url": "https://raw.githubusercontent.com/chainjet/assets/master/integrations/aws/Arch_Amazon-Lex_64.svg"
    },
    "termsOfService": "https://aws.amazon.com/service-terms/",
    "contact": {
      "name": "Mike Ralphson",
      "email": "mike.ralphson@gmail.com",
      "url": "https://github.com/mermade/aws2openapi",
      "x-twitter": "PermittedSoc"
    },
    "license": {
      "name": "Apache 2.0 License",
      "url": "http://www.apache.org/licenses/"
    },
    "x-providerName": "amazonaws.com",
    "x-serviceName": "runtime.lex",
    "x-origin": [
      {
        "contentType": "application/json",
        "url": "https://raw.githubusercontent.com/aws/aws-sdk-js/master/apis/runtime.lex-2016-11-28.normal.json",
        "converter": {
          "url": "https://github.com/mermade/aws2openapi",
          "version": "1.0.0"
        },
        "x-apisguru-driver": "external"
      }
    ],
    "x-apiClientRegistration": {
      "url": "https://portal.aws.amazon.com/gp/aws/developer/registration/index.html?nc2=h_ct"
    },
    "x-apisguru-categories": ["cloud"],
    "x-preferred": true
  },
  "externalDocs": {
    "description": "Amazon Web Services documentation",
    "url": "https://docs.aws.amazon.com/lex/"
  },
  "servers": [
    {
      "url": "http://runtime.lex.{region}.amazonaws.com",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": [
            "us-east-1",
            "us-east-2",
            "us-west-1",
            "us-west-2",
            "us-gov-west-1",
            "us-gov-east-1",
            "ca-central-1",
            "eu-north-1",
            "eu-west-1",
            "eu-west-2",
            "eu-west-3",
            "eu-central-1",
            "eu-south-1",
            "af-south-1",
            "ap-northeast-1",
            "ap-northeast-2",
            "ap-northeast-3",
            "ap-southeast-1",
            "ap-southeast-2",
            "ap-east-1",
            "ap-south-1",
            "sa-east-1",
            "me-south-1"
          ],
          "default": "us-east-1"
        }
      },
      "description": "The Amazon Lex Runtime Service multi-region endpoint"
    },
    {
      "url": "https://runtime.lex.{region}.amazonaws.com",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": [
            "us-east-1",
            "us-east-2",
            "us-west-1",
            "us-west-2",
            "us-gov-west-1",
            "us-gov-east-1",
            "ca-central-1",
            "eu-north-1",
            "eu-west-1",
            "eu-west-2",
            "eu-west-3",
            "eu-central-1",
            "eu-south-1",
            "af-south-1",
            "ap-northeast-1",
            "ap-northeast-2",
            "ap-northeast-3",
            "ap-southeast-1",
            "ap-southeast-2",
            "ap-east-1",
            "ap-south-1",
            "sa-east-1",
            "me-south-1"
          ],
          "default": "us-east-1"
        }
      },
      "description": "The Amazon Lex Runtime Service multi-region endpoint"
    },
    {
      "url": "http://runtime.lex.{region}.amazonaws.com.cn",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": ["cn-north-1", "cn-northwest-1"],
          "default": "cn-north-1"
        }
      },
      "description": "The Amazon Lex Runtime Service endpoint for China (Beijing) and China (Ningxia)"
    },
    {
      "url": "https://runtime.lex.{region}.amazonaws.com.cn",
      "variables": {
        "region": {
          "description": "The AWS region",
          "enum": ["cn-north-1", "cn-northwest-1"],
          "default": "cn-north-1"
        }
      },
      "description": "The Amazon Lex Runtime Service endpoint for China (Beijing) and China (Ningxia)"
    }
  ],
  "paths": {
    "/bot/{botName}/alias/{botAlias}/user/{userId}/session": {
      "delete": {
        "operationId": "DeleteSession",
        "description": "Removes session information for a specified bot, alias, and user ID. ",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/DeleteSessionResponse"
                }
              }
            }
          },
          "480": {
            "description": "NotFoundException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/NotFoundException" }
              }
            }
          },
          "481": {
            "description": "BadRequestException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/BadRequestException" }
              }
            }
          },
          "482": {
            "description": "LimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/LimitExceededException"
                }
              }
            }
          },
          "483": {
            "description": "InternalFailureException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalFailureException"
                }
              }
            }
          },
          "484": {
            "description": "ConflictException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ConflictException" }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "botName",
            "in": "path",
            "required": true,
            "description": "The name of the bot that contains the session data.",
            "schema": { "type": "string" }
          },
          {
            "name": "botAlias",
            "in": "path",
            "required": true,
            "description": "The alias in use for the bot that contains the session data.",
            "schema": { "type": "string" }
          },
          {
            "name": "userId",
            "in": "path",
            "required": true,
            "description": "The identifier of the user associated with the session data.",
            "schema": {
              "type": "string",
              "pattern": "[0-9a-zA-Z._:-]+",
              "minLength": 2,
              "maxLength": 100
            }
          }
        ],
        "summary": "Delete Session"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ],
      "post": {
        "operationId": "PutSession",
        "description": "<p>Creates a new session or modifies an existing session with an Amazon Lex bot. Use this operation to enable your application to set the state of the bot.</p> <p>For more information, see <a href=\"https://docs.aws.amazon.com/lex/latest/dg/how-session-api.html\">Managing Sessions</a>.</p>",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/PutSessionResponse" }
              }
            }
          },
          "480": {
            "description": "NotFoundException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/NotFoundException" }
              }
            }
          },
          "481": {
            "description": "BadRequestException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/BadRequestException" }
              }
            }
          },
          "482": {
            "description": "LimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/LimitExceededException"
                }
              }
            }
          },
          "483": {
            "description": "InternalFailureException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalFailureException"
                }
              }
            }
          },
          "484": {
            "description": "ConflictException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ConflictException" }
              }
            }
          },
          "485": {
            "description": "NotAcceptableException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/NotAcceptableException"
                }
              }
            }
          },
          "486": {
            "description": "DependencyFailedException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/DependencyFailedException"
                }
              }
            }
          },
          "487": {
            "description": "BadGatewayException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/BadGatewayException" }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "botName",
            "in": "path",
            "required": true,
            "description": "The name of the bot that contains the session data.",
            "schema": { "type": "string" }
          },
          {
            "name": "botAlias",
            "in": "path",
            "required": true,
            "description": "The alias in use for the bot that contains the session data.",
            "schema": { "type": "string" }
          },
          {
            "name": "userId",
            "in": "path",
            "required": true,
            "description": "The ID of the client application user. Amazon Lex uses this to identify a user's conversation with your bot. ",
            "schema": {
              "type": "string",
              "pattern": "[0-9a-zA-Z._:-]+",
              "minLength": 2,
              "maxLength": 100
            }
          },
          {
            "name": "Accept",
            "in": "header",
            "required": false,
            "description": "<p>The message that Amazon Lex returns in the response can be either text or speech based depending on the value of this field.</p> <ul> <li> <p>If the value is <code>text/plain; charset=utf-8</code>, Amazon Lex returns text in the response.</p> </li> <li> <p>If the value begins with <code>audio/</code>, Amazon Lex returns speech in the response. Amazon Lex uses Amazon Polly to generate the speech in the configuration that you specify. For example, if you specify <code>audio/mpeg</code> as the value, Amazon Lex returns speech in the MPEG format.</p> </li> <li> <p>If the value is <code>audio/pcm</code>, the speech is returned as <code>audio/pcm</code> in 16-bit, little endian format.</p> </li> <li> <p>The following are the accepted values:</p> <ul> <li> <p> <code>audio/mpeg</code> </p> </li> <li> <p> <code>audio/ogg</code> </p> </li> <li> <p> <code>audio/pcm</code> </p> </li> <li> <p> <code>audio/*</code> (defaults to mpeg)</p> </li> <li> <p> <code>text/plain; charset=utf-8</code> </p> </li> </ul> </li> </ul>",
            "schema": { "type": "string" }
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "sessionAttributes": {
                    "description": "Map of key/value pairs representing the session-specific context information. It contains application information passed between Amazon Lex and a client application.",
                    "type": "object",
                    "additionalProperties": {
                      "$ref": "#/components/schemas/String"
                    }
                  },
                  "dialogAction": {
                    "description": "Describes the next action that the bot should take in its interaction with the user and provides information about the context in which the action takes place. Use the <code>DialogAction</code> data type to set the interaction to a specific state, or to return the interaction to a previous state.",
                    "type": "object",
                    "properties": {
                      "type": {
                        "allOf": [
                          { "$ref": "#/components/schemas/DialogActionType" },
                          {
                            "description": "<p>The next action that the bot should take in its interaction with the user. The possible values are:</p> <ul> <li> <p> <code>ConfirmIntent</code> - The next action is asking the user if the intent is complete and ready to be fulfilled. This is a yes/no question such as \"Place the order?\"</p> </li> <li> <p> <code>Close</code> - Indicates that the there will not be a response from the user. For example, the statement \"Your order has been placed\" does not require a response.</p> </li> <li> <p> <code>Delegate</code> - The next action is determined by Amazon Lex.</p> </li> <li> <p> <code>ElicitIntent</code> - The next action is to determine the intent that the user wants to fulfill.</p> </li> <li> <p> <code>ElicitSlot</code> - The next action is to elicit a slot value from the user.</p> </li> </ul>"
                          }
                        ]
                      },
                      "intentName": {
                        "allOf": [
                          { "$ref": "#/components/schemas/IntentName" },
                          { "description": "The name of the intent." }
                        ]
                      },
                      "slots": {
                        "allOf": [
                          { "$ref": "#/components/schemas/StringMap" },
                          {
                            "description": "Map of the slots that have been gathered and their values. "
                          }
                        ]
                      },
                      "slotToElicit": {
                        "allOf": [
                          { "$ref": "#/components/schemas/String" },
                          {
                            "description": "The name of the slot that should be elicited from the user."
                          }
                        ]
                      },
                      "fulfillmentState": {
                        "allOf": [
                          { "$ref": "#/components/schemas/FulfillmentState" },
                          {
                            "description": "<p>The fulfillment state of the intent. The possible values are:</p> <ul> <li> <p> <code>Failed</code> - The Lambda function associated with the intent failed to fulfill the intent.</p> </li> <li> <p> <code>Fulfilled</code> - The intent has fulfilled by the Lambda function associated with the intent. </p> </li> <li> <p> <code>ReadyForFulfillment</code> - All of the information necessary for the intent is present and the intent ready to be fulfilled by the client application.</p> </li> </ul>"
                          }
                        ]
                      },
                      "message": {
                        "allOf": [
                          { "$ref": "#/components/schemas/Text" },
                          {
                            "description": "The message that should be shown to the user. If you don't specify a message, Amazon Lex will use the message configured for the intent."
                          }
                        ]
                      },
                      "messageFormat": {
                        "allOf": [
                          { "$ref": "#/components/schemas/MessageFormatType" },
                          {
                            "description": "<ul> <li> <p> <code>PlainText</code> - The message contains plain UTF-8 text.</p> </li> <li> <p> <code>CustomPayload</code> - The message is a custom format for the client.</p> </li> <li> <p> <code>SSML</code> - The message contains text formatted for voice output.</p> </li> <li> <p> <code>Composite</code> - The message contains an escaped JSON object containing one or more messages. For more information, see <a href=\"https://docs.aws.amazon.com/lex/latest/dg/howitworks-manage-prompts.html\">Message Groups</a>. </p> </li> </ul>"
                          }
                        ]
                      }
                    }
                  },
                  "recentIntentSummaryView": {
                    "description": "<p>A summary of the recent intents for the bot. You can use the intent summary view to set a checkpoint label on an intent and modify attributes of intents. You can also use it to remove or add intent summary objects to the list.</p> <p>An intent that you modify or add to the list must make sense for the bot. For example, the intent name must be valid for the bot. You must provide valid values for:</p> <ul> <li> <p> <code>intentName</code> </p> </li> <li> <p>slot names</p> </li> <li> <p> <code>slotToElict</code> </p> </li> </ul> <p>If you send the <code>recentIntentSummaryView</code> parameter in a <code>PutSession</code> request, the contents of the new summary view replaces the old summary view. For example, if a <code>GetSession</code> request returns three intents in the summary view and you call <code>PutSession</code> with one intent in the summary view, the next call to <code>GetSession</code> will only return one intent.</p>",
                    "type": "array",
                    "items": { "$ref": "#/components/schemas/IntentSummary" },
                    "minItems": 0,
                    "maxItems": 3
                  },
                  "activeContexts": {
                    "description": "<p>A list of contexts active for the request. A context can be activated when a previous intent is fulfilled, or by including the context in the request,</p> <p>If you don't specify a list of contexts, Amazon Lex will use the current list of contexts for the session. If you specify an empty list, all contexts for the session are cleared.</p>",
                    "type": "array",
                    "items": { "$ref": "#/components/schemas/ActiveContext" },
                    "minItems": 0,
                    "maxItems": 20
                  }
                }
              }
            }
          }
        },
        "summary": "Put Session"
      }
    },
    "/bot/{botName}/alias/{botAlias}/user/{userId}/session/": {
      "get": {
        "operationId": "GetSession",
        "description": "Returns session information for a specified bot, alias, and user ID.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/GetSessionResponse" }
              }
            }
          },
          "480": {
            "description": "NotFoundException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/NotFoundException" }
              }
            }
          },
          "481": {
            "description": "BadRequestException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/BadRequestException" }
              }
            }
          },
          "482": {
            "description": "LimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/LimitExceededException"
                }
              }
            }
          },
          "483": {
            "description": "InternalFailureException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalFailureException"
                }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "botName",
            "in": "path",
            "required": true,
            "description": "The name of the bot that contains the session data.",
            "schema": { "type": "string" }
          },
          {
            "name": "botAlias",
            "in": "path",
            "required": true,
            "description": "The alias in use for the bot that contains the session data.",
            "schema": { "type": "string" }
          },
          {
            "name": "userId",
            "in": "path",
            "required": true,
            "description": "The ID of the client application user. Amazon Lex uses this to identify a user's conversation with your bot. ",
            "schema": {
              "type": "string",
              "pattern": "[0-9a-zA-Z._:-]+",
              "minLength": 2,
              "maxLength": 100
            }
          },
          {
            "name": "checkpointLabelFilter",
            "in": "query",
            "required": false,
            "description": "<p>A string used to filter the intents returned in the <code>recentIntentSummaryView</code> structure. </p> <p>When you specify a filter, only intents with their <code>checkpointLabel</code> field set to that string are returned.</p>",
            "schema": {
              "type": "string",
              "pattern": "[a-zA-Z0-9-]+",
              "minLength": 1,
              "maxLength": 255
            }
          }
        ],
        "summary": "Get Session"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ]
    },
    "/bot/{botName}/alias/{botAlias}/user/{userId}/content#Content-Type": {
      "post": {
        "operationId": "PostContent",
        "description": "<p> Sends user input (text or speech) to Amazon Lex. Clients use this API to send text and audio requests to Amazon Lex at runtime. Amazon Lex interprets the user input using the machine learning model that it built for the bot. </p> <p>The <code>PostContent</code> operation supports audio input at 8kHz and 16kHz. You can use 8kHz audio to achieve higher speech recognition accuracy in telephone audio applications. </p> <p> In response, Amazon Lex returns the next message to convey to the user. Consider the following example messages: </p> <ul> <li> <p> For a user input \"I would like a pizza,\" Amazon Lex might return a response with a message eliciting slot data (for example, <code>PizzaSize</code>): \"What size pizza would you like?\". </p> </li> <li> <p> After the user provides all of the pizza order information, Amazon Lex might return a response with a message to get user confirmation: \"Order the pizza?\". </p> </li> <li> <p> After the user replies \"Yes\" to the confirmation prompt, Amazon Lex might return a conclusion statement: \"Thank you, your cheese pizza has been ordered.\". </p> </li> </ul> <p> Not all Amazon Lex messages require a response from the user. For example, conclusion statements do not require a response. Some messages require only a yes or no response. In addition to the <code>message</code>, Amazon Lex provides additional context about the message in the response that you can use to enhance client behavior, such as displaying the appropriate client user interface. Consider the following examples: </p> <ul> <li> <p> If the message is to elicit slot data, Amazon Lex returns the following context information: </p> <ul> <li> <p> <code>x-amz-lex-dialog-state</code> header set to <code>ElicitSlot</code> </p> </li> <li> <p> <code>x-amz-lex-intent-name</code> header set to the intent name in the current context </p> </li> <li> <p> <code>x-amz-lex-slot-to-elicit</code> header set to the slot name for which the <code>message</code> is eliciting information </p> </li> <li> <p> <code>x-amz-lex-slots</code> header set to a map of slots configured for the intent with their current values </p> </li> </ul> </li> <li> <p> If the message is a confirmation prompt, the <code>x-amz-lex-dialog-state</code> header is set to <code>Confirmation</code> and the <code>x-amz-lex-slot-to-elicit</code> header is omitted. </p> </li> <li> <p> If the message is a clarification prompt configured for the intent, indicating that the user intent is not understood, the <code>x-amz-dialog-state</code> header is set to <code>ElicitIntent</code> and the <code>x-amz-slot-to-elicit</code> header is omitted. </p> </li> </ul> <p> In addition, Amazon Lex also returns your application-specific <code>sessionAttributes</code>. For more information, see <a href=\"https://docs.aws.amazon.com/lex/latest/dg/context-mgmt.html\">Managing Conversation Context</a>. </p>",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/PostContentResponse" }
              }
            }
          },
          "480": {
            "description": "NotFoundException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/NotFoundException" }
              }
            }
          },
          "481": {
            "description": "BadRequestException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/BadRequestException" }
              }
            }
          },
          "482": {
            "description": "LimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/LimitExceededException"
                }
              }
            }
          },
          "483": {
            "description": "InternalFailureException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InternalFailureException"
                }
              }
            }
          },
          "484": {
            "description": "ConflictException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ConflictException" }
              }
            }
          },
          "485": {
            "description": "UnsupportedMediaTypeException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/UnsupportedMediaTypeException"
