{
    "openapi": "3.0.0",
    "info": {
      "title": "Travel Restrictions",
      "version": "1.0.2",
      "description": "<h3 style=\"color:red\">This version will be decommissioned the 26th of January 2023. Please use the [latest version](https://developers.amadeus.com/self-service/category/covid-19-and-travel-safety/api-doc/travel-restrictions/api-reference)</h3>\n\nBefore using this API, we recommend you read our **[Authorization Guide](https://developers.amadeus.com/self-service/apis-docs/guides/authorization-262)** for more information on how to generate an access token."
    },
    "servers": [
      {
        "url": "https://test.api.amadeus.com/v1"
      }
    ],
    "paths": {
      "/duty-of-care/diseases/covid19-area-report": {
        "parameters": [],
        "get": {
          "summary": "Returns Covid-19 restrictions on targeted area",
          "operationId": "GETCovidReport",
          "parameters": [
            {
              "schema": {
                "type": "string",
                "minLength": 2,
                "maxLength": 2
              },
              "in": "query",
              "example": "US",
              "name": "countryCode",
              "required": true,
              "description": "[ISO 3166](https://en.wikipedia.org/wiki/ISO_3166-1_alpha-2) Alpha-2 code. e.g. \"US\" United States of America."
            },
            {
              "schema": {
                "type": "string",
                "minLength": 3,
                "maxLength": 3
              },
              "in": "query",
              "name": "cityCode",
              "description": "[IATA code](http://www.iata.org/publications/Pages/code-search.aspx) for city metropolitan area."
            }
          ],
          "responses": {
            "200": {
              "description": "Successful Reply",
              "content": {
                "application/json": {
                  "schema": {
                    "type": "object",
                    "properties": {
                      "warnings": {
                        "type": "array",
                        "items": {
                          "$ref": "#/components/schemas/warnings"
                        }
                      },
                      "meta": {
                        "$ref": "#/components/schemas/meta"
                      },
                      "data": {
                        "type": "array",
                        "items": {
                          "$ref": "#/components/schemas/DiseaseAreaReport"
                        }
                      }
                    }
                  },
                  "examples": {
                    "example": {
                      "value": {
                        "data": [
                          {
                            "type": "covid19-area-report",
                            "area": {
                              "name": "United States of America",
                              "areaType": "Country",
                              "iataCode": "US",
                              "geoCode": {
                                "latitude": "37.0902",
                                "longitude": "95.7129"
                              }
                            },
                            "summary": "<p>Authorities imposed international travel restrictions from late February 2020</p>",
                            "diseaseRiskLevel": "High",
                            "diseaseInfection": {
                              "rate": "176.02",
                              "level": "Extreme",
                              "date": "2021-04-25",
                              "infectionMapLink": "https://www.nytimes.com/interactive/2020/us/coronavirus-us-cases.html"
                            },
                            "diseaseCases": {
                              "confirmed": 32940846,
                              "deaths": 585970,
                              "recovered": 0,
                              "active": 32354876,
                              "date": "2021-05-06"
                            },
                            "hotspots": "<p>Minnesota, Michigan, Pennsylvania, New Jersey, Delaware, New York, Connecticut, Rhode Island, New Hampshire, Vermont, Massachusetts</p>",
                            "dataSources": {
                              "governmentSiteLink": "https://www.cdc.gov/coronavirus/2019-ncov/index.html",
                              "healthDepartmentSiteLink": "https://www.helsenorge.no/en/coronavirus/"
                            },
                            "areaRestrictions": [
                              {
                                "text": "<p>A nightly nationwide curfew is in effect from 21:00-06:00 local time until further notice.</p>",
                                "date": "2021-05-02",
                                "restrictionType": "Lockdown"
                              }
                            ],
                            "areaAccessRestrictions": {
                              "transportation": {
                                "text": "<p>American Airlines, United Airlines, Delta Air Lines and other major carriers have suspended most flights to China, Hong Kong, Macau, the UK, continental Europe and Latin America. Officials partially lifted a ban on flights by Air China, China Eastern Airlines, China Southern Airlines, and Xiamen Airlines after Chinese authorities allowed United and Delta airlines to operate one round-trip between the two countries.</p>",
                                "isBanned": "Partial",
                                "throughDate": "indef",
                                "date": "2021-04-25",
                                "transportationType": "Air"
                              },
                              "declarartionDocumentations": {
                                "text": "<p>Arriving travellers must complete a passenger locator form within 24 hours of arrival.</p>",
                                "documentRequired": "Yes",
                                "travelDocumentLink": "https://travel.state.gov/content/travel/en/traveladvisories/ea/covid-19-information.html",
                                "healthDocumentLink": "https://travel.state.gov/content/travel/en/traveladvisories/ea/covid-19-information.html",
                                "date": "2021-05-03"
                              },
                              "entry": {
                                "text": "<p>Travellers arriving from or transiting through India, South Africa, China, Iran, Brazil, Ireland, the United Kingdom and the European Schengen Area countries of Austria, Belgium, Czech Republic, Denmark, Estonia, Finland, France, Germany, Greece, Hungary, Iceland, Italy, Latvia, Liechtenstein, Lithuania, Luxembourg, Malta, Netherlands, Norway, Poland, Portugal, Slovakia, Slovenia, Spain, Sweden, Switzerland, Monaco, San Marin and Vatican City within 14 days of their planned arrival in the US are barred from entry.</p>",
                                "exemptions": "https://travel.state.gov/content/travel/en/News/visas-news/presidential-proclamation-coronavirus.html",
                                "ban": "Partial",
                                "throughDate": "indef",
                                "rules": "https://www.cdc.gov/coronavirus/2019-ncov/travelers/from-other-countries.html",
                                "date": "2021-04-26"
                              },
                              "diseaseTesting": {
                                "when": "Before travel",
                                "requirement": "Mandatory",
                                "text": "<p>All passengers on inbound international flights - including US citizens and fully vaccinated people - must present a negative COVID-19 test result </p>",
                                "rules": "https://www.cdc.gov/coronavirus/2019-ncov/travelers/testing-international-air-travelers.html#:~:text=On%20January%2012%2C%202021%2C%20CDC,airline%20before%20boarding%20the%20flight.",
                                "isRequired": "Yes",
                                "date": "2021-05-05"
                              },
                              "tracingApplication": {
                                "text": "<p>There is no national contact tracing app. Local authorities have developed or are developing optional platforms through Apple's Exposure Notification API in multiple states, including Alabama, Arizona, California, Colorado, Delaware, Maryland, Michigan, Nevada, New York, North Carolina, North Dakota, Oregon, Pennsylvania, South Carolina, Virginia Washington and Wyoming, the District of Columbia, and territories, including Guam and Puerto Rico.</p>",
                                "isRequired": "No",
                                "date": "2021-05-05"
                              },
                              "quarantineModality": {
                                "text": "<p>International travellers are not required to quarantine upon arrival. However, the CDC recommends that travellers stay home and self-quarantine for 7 days after arrival. Travellers should take a test again 3-5 days after arrival; if a test is not available or results are delayed, travellers are recommended to stay home and self-quarantine for a total of 10 days after travelling.</p>",
                                "rules": "https://travel.state.gov/content/travel/en/traveladvisories/ea/covid-19-information.html",
                                "eligiblePerson": "None",
                                "date": "2021-05-05"
                              },
                              "masks": {
                                "isRequired": "Yes, conditional",
                                "text": "<p>Some states and territories require residents to wear masks while out in public. Violators may be subject to fines and be denied entry to businesses and use of public transport. Most states and territories have voluntary measures or measures restricted to specific venues and municipalities.</p> <p>Enforcement varies widely and is not consistently enforced below the federal level. Masks must be worn in federal government buildings as well as on inter-state transport, including at airports and onboard flights, trains, planes, intercity buses and ferries until at least 13 September.</p>",
                                "date": "2021-05-05"
                              },
                              "exit": {
                                "isAllowed": "No",
                                "text": "<p>There are no exit restrictions though international travel is strongly discouraged.</p>",
                                "rules": "https://travel.state.gov/content/travel/en/traveladvisories/traveladvisories.html/",
                                "date": "2021-05-05"
                              },
                              "otherRestrictions": {
                                "text": "<p>Effective 23 April-20 May, a state of exception will be imposed in NYC</p>",
                                "date": "2021-05-05"
                              }
                            },
                            "areaPolicy": {
                              "text": "<p>Limited federal policies for quarantine, testing and masking are in effect nationwide. Most policy decisions are implemented at the state, county and municipal level.</p>",
                              "status": "Distancing",
                              "startDate": "2021-04-23",
                              "endDate": "indef",
                              "date": "2021-05-03"
                            },
                            "subArea": [
                              {
                                "area": {
                                  "name": "Houston",
                                  "areaType": "City",
                                  "iataCode": "HOU",
                                  "geoCode": {
                                    "latitude": "29.75894",
                                    "longitude": "-95.3676"
                                  }
                                },
                                "summary": "<p>Authorities were quick to impose curfew measures from mid-March 2020 .</p>",
                                "diseaseRiskLevel": "High"
                              }
                            ]
                          }
                        ],
                        "meta": {
                          "language": "EN",
                          "links": {
                            "self": "https://api.amadeus.net/v1/duty-of-care/disease/covid19-area-report?countryCode=US&cityCode=HOU&language=EN"
                          }
                        }
                      }
                    }
                  }
                }
              }
            },
            "400": {
              "description": "Bad Request\n\ncode    | title                                 \n------- | ------------------------------------- \n4926    | INVALID DATA RECEIVED                          \n32171   | MANDATORY DATA MISSING \n\n",
              "content": {
                "application/json": {
                  "schema": {
                    "type": "object",
                    "properties": {
                      "errors": {
                        "type": "array",
                        "items": {
                          "type": "object",
                          "description": "The Error Definition",
                          "title": "Error",
                          "properties": {
                            "status": {
                              "type": "integer",
                              "description": "The [HTTP status code](https://www.iana.org/assignments/http-status-codes/http-status-codes.xhtml) of this response. This is present only in terminal errors which cause an unsuccessful response. In the case of multiple errors, they must all have the same status."
                            },
                            "code": {
                              "type": "integer",
                              "description": "A machine-readable error code from the Amadeus Canned Messages table, that will enable the API Consumers code to handle this type of error"
                            },
                            "title": {
                              "type": "string",
                              "description": "An error title from the Canned Messages table with a 1:1 correspondence to the error code. This may be localized"
                            },
                            "detail": {
                              "type": "string",
                              "description": "An easy-to-read explanation specific to this occurrence of the problem. It should give the API consumer an idea of what went wrong and how to recover from it. Like the title, this field’s value can be localized."
                            },
                            "source": {
                              "type": "object",
                              "title": "Error_Source",
                              "properties": {
                                "parameter": {
                                  "type": "string",
                                  "description": "The key of the URI path or query parameter that caused the error"
                                },
                                "pointer": {
                                  "type": "string",
                                  "description": "A JSON Pointer [RFC6901] to the associated entity in the request body that caused this error"
                                },
                                "example": {
                                  "type": "string",
                                  "description": "A sample input to guide the user when resolving this issue"
                                }
                              }
                            }
                          }
                        }
                      }
                    }
                  },
                  "examples": {
                    "example-400": {
                      "value": {
                        "errors": [
                          {
                            "status": 400,
                            "code": 32171,
                            "title": "MANDATORY DATA MISSING",
                            "detail": "CONTENT IS MISSING IN THE GIVEN REQUEST, PLEASE CHECK AND RESEND YOUR REQUEST"
                          }
                        ]
                      }
                    }
                  }
                }
              }
            },
            "500": {
              "description": "Internal Server Error\n\ncode    | title                                 \n------- | ------------------------------------- \n141     | SYSTEM ERROR HAS OCCURRED",
              "content": {
                "application/json": {
                  "schema": {
                    "type": "object",
                    "properties": {
                      "errors": {
                        "type": "array",
                        "items": {
                          "$ref": "#/paths/~1duty-of-care~1diseases~1covid19-area-report/get/responses/400/content/application~1json/schema/properties/errors/items"
                        }
                      }
                    }
                  },
                  "examples": {
                    "example-500": {
                      "value": {
                        "errors": [
                          {
                            "status": 500,
                            "code": 141,
                            "title": "SYSTEM ERROR HAS OCCURRED"
                          }
                        ]
                      }
                    }
                  }
                }
              }
            },
            "501": {
              "description": "Not Implemented\ncode    | title                                 \n------- | ------------------------------------- \n141     | UNABLE TO SUPPORT",
              "content": {
                "application/json": {
                  "schema": {
                    "type": "object",
                    "properties": {
                      "errors": {
                        "type": "array",
                        "items": {
                          "$ref": "#/paths/~1duty-of-care~1diseases~1covid19-area-report/get/responses/400/content/application~1json/schema/properties/errors/items"
                        }
                      }
                    }
                  },
                  "examples": {
                    "example-501": {
                      "value": {
                        "errors": [
                          {
                            "status": 501,
                            "code": 141,
                            "title": "UNABLE TO SUPPORT"
                          }
                        ]
                      }
                    }
                  }
                }
              }
            }
          },
          "description": " ",
          "tags": [
            "Covid-19 Area Report"
          ]
        }
      }
    },
    "components": {
      "schemas": {
        "Exit": {
          "allOf": [
            {
              "$ref": "#/components/schemas/DatedInformation"
            },
