{
  "openapi": "3.0.0",
  "info": {
    "title": "Simple API Overview",
    "version": "1.0.0"
  },
  "paths": {
    "/": {
      "get": {
        "summary": "List API versions",
        "responses": {
          "201": {
            "description": "201 response"
          },
          "200": {
            "$ref": "#/components/schemas/MyObject"
          }
        },
        "operationId": "listVersionsv2"
      },
      "parameters": [
        {
          "name": "id",
          "in": "path",
          "description": "ID of pet to use",
          "required": true,
          "schema": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "myObject": {
                  "$ref": "#/components/schemas/MyObject"
                },
                "length": {
                  "type": "integer",
                  "format": "int32"
                }
              }
            }
          }
        }
      ]
    }
  },
  "components": {
    "schemas": {
      "MyObject": {
        "type": "object",
        "properties": {
          "id": {
            "type": "integer",
            "format": "int64"
          },
          "quantity": {
            "type": "integer",
            "format": "int32"
          },
          "percentage": {
            "type": "number",
            "format": "float"
          },
          "result": {
            "type": "number",
            "format": "double"
          }
        }
      }
    }
  }
}
