{
  "openapi": "3.0.0",
  "info": {
    "description": "bandcamp",
    "version": "1.0.0",
    "title": "Bandcamp"
  },
  "servers": [
    {
      "url": "https://bandcamp.com",
      "description": "API endpoint. Does NOT serve any artwork."
    },
    {
      "url": "https://f0.bcbits.com",
      "description": "Artwork serving & resizing. Does NOT serve the API. f0 can vary to f1, f4 etc."
    }
  ],
  "tags": [
    {
      "name": "To be figured out",
      "description": "end-points I have no clue about. Have fun!"
    }
  ],
  "paths": {
    "/api/band/3/search": {
      "get": {
        "summary": "Search for bands.",
        "tags": [
          "Bands"
        ],
        "parameters": [
          {
            "$ref": "#/components/parameters/DevKey"
          },
          {
            "in": "query",
            "name": "name",
            "schema": {
              "type": "string"
            },
            "required": true,
            "example": "hermann"
          }
        ],
        "responses": {
          "200": {
            "description": "the bands",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "results": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "description": "a search result",
                        "properties": {
                          "name": {
                            "$ref": "#/components/schemas/ArtistName"
                          },
                          "band_id": {
                            "$ref": "#/components/schemas/ArtistID"
                          },
                          "subdomain": {
                            "$ref": "#/components/schemas/SubDomain"
                          },
                          "url": {
                            "$ref": "#/components/schemas/ArtistURL"
                          },
                          "offsite_url": {
                            "type": "string",
                            "format": "URI",
                            "description": "the artist's online presence outside bandcamp.com",
                            "example": "http://facebook.com/hermannmusik"
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/api/band/3/discography": {
      "get": {
        "summary": "Get a band's discography.",
        "tags": [
          "Bands"
        ],
        "parameters": [
          {
            "$ref": "#/components/parameters/DevKey"
          },
          {
            "in": "query",
            "name": "band_id",
            "schema": {
              "type": "integer"
            },
            "required": true,
            "example": 2240734681
          }
        ],
        "responses": {
          "200": {
            "description": "the band's discography",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "results": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "description": "a discography item",
                        "properties": {
                          "title": {
                            "$ref": "#/components/schemas/AlbumTitle"
                          },
                          "artist": {
                            "$ref": "#/components/schemas/ArtistName"
                          },
                          "band_id": {
                            "$ref": "#/components/schemas/ArtistID"
                          },
                          "subdomain": {
                            "$ref": "#/components/schemas/SubDomain"
                          },
                          "url": {
                            "$ref": "#/components/schemas/AlbumURL"
                          },
                          "art_id": {
                            "$ref": "#/components/schemas/ArtworkID"
                          },
                          "large_art_url": {
                            "$ref": "#/components/schemas/ArtworkURL"
                          },
                          "small_art_url": {
                            "$ref": "#/components/schemas/ArtworkURL"
                          },
                          "downloadable": {
                            "$ref": "#/components/schemas/DownloadableFlag"
                          },
                          "release_date": {
                            "$ref": "#/components/schemas/Timestamp"
                          },
                          "album_id": {
                            "$ref": "#/components/schemas/AlbumID"
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/api/album/2/info": {
      "get": {
        "summary": "Get album information.",
        "description": "unfortunately this call would not return all playable items, even if an identity cookie or the like was used.",
        "tags": [
          "Albums"
        ],
        "parameters": [
          {
            "$ref": "#/components/parameters/DevKey"
          },
          {
            "in": "query",
            "name": "album_id",
            "schema": {
              "type": "string"
            },
            "required": true,
            "example": 793714377
          }
        ],
        "responses": {
          "200": {
            "description": "the album",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "results": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "description": "a discography item",
                        "properties": {
                          "title": {
                            "$ref": "#/components/schemas/AlbumTitle"
                          },
                          "about": {
                            "type": "string",
                            "description": "a description of the given album",
                            "example": "„Es gseht us, als wär d’Zyt still gstande, was an sich reizvoll wär, aber s’esch die falschi Zyt gsi...“ (Stillstand) \r\n\r\nHerzstück von Hermanns minimaler Mundart-Musik ist der Rhythm Ace FR-1. Mit seinen einfachen Rhythmen und dem warmen, analogen Klang liefert der 50-jährige Drumcomputer das ideale klangliche Gerüst für die drei Musiker, die auf der Suche nach einem neuen Sound waren. Das Gerät zwang ihnen die einfache Frage auf: wie füllt man die stoische Motorik mit Leben? Hermanns Antwort: Mit einfachen erzählenden Texten, die Ausflüge ins Politische, Alltagsphilosophische und Absurde unternehmen. Mit Gitarren- und Synthie-Sounds, die gleichermassen für stimmige Flächen wie für melodiöse Einfälle zuständig sind. Und mit einem Bass, der das stete Treiben des Rhythm Ace elegant mit dem musikalischen Überbau verbindet. All dies ergibt eine stimmungsvolle, punktgenaue Musik, die den Kopf gen oben öffnet, ohne dass die Füsse den Boden verlassen möchten."
                          },
                          "album_id": {
                            "$ref": "#/components/schemas/AlbumID"
                          },
                          "band_id": {
                            "$ref": "#/components/schemas/ArtistID"
                          },
                          "large_art_url": {
                            "$ref": "#/components/schemas/ArtworkURL"
                          },
                          "small_art_url": {
                            "$ref": "#/components/schemas/ArtworkURL"
                          },
                          "downloadable": {
                            "$ref": "#/components/schemas/DownloadableFlag"
                          },
                          "release_date": {
                            "$ref": "#/components/schemas/Timestamp"
                          },
                          "url": {
                            "$ref": "#/components/schemas/AlbumURL"
                          },
                          "credits": {
                            "type": "string",
                            "description": "free text credits of the album",
                            "example": "Hermann: Hannes Herger (Bass), Dani Hug (Synths), Jonathan Winkler (Gesang, Gitarre, Rhythm Ace FR-1)\r\nAufnahme & Mix: Timo Keller, StudioVomDach\r\nMastering: Dave Hofmann, Somastudios.ch\r\nDesign: hugototal.ch"
                          },
                          "tracks": {
                            "type": "array",
                            "items": {
                              "$ref": "#/components/schemas/Track"
                            }
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/api/track/3/info": {
      "get": {
        "summary": "Get track information.",
        "tags": [
          "Tracks"
        ],
        "parameters": [
          {
            "$ref": "#/components/parameters/DevKey"
          },
          {
            "in": "query",
            "name": "track_id",
            "schema": {
              "type": "string"
            },
            "required": true,
            "example": 5020296
          }
        ],
        "responses": {
          "200": {
            "description": "the track",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Track"
                }
              }
            }
          }
        }
      }
    },
    "/api/fancollection/1/followers": {
      "post": {
        "summary": "Get a user's followers",
        "tags": [
          "Fan Collection"
        ],
        "requestBody": {
          "$ref": "#/components/requestBodies/FanCollectionBody"
        },
        "responses": {
          "200": {
            "$ref": "#/components/responses/FanCollection"
          }
        }
      }
    },
    "/api/fancollection/1/following_bands": {
      "post": {
        "summary": "Get the bands a user is following",
        "tags": [
          "Fan Collection"
        ],
        "requestBody": {
          "$ref": "#/components/requestBodies/FanCollectionBody"
        },
        "responses": {
          "200": {
            "$ref": "#/components/responses/FanCollection"
          }
        }
      }
    },
    "/api/fancollection/1/following_fans": {
      "post": {
        "summary": "Get the fans a user is following",
        "tags": [
          "Fan Collection"
        ],
        "requestBody": {
          "$ref": "#/components/requestBodies/FanCollectionBody"
        },
        "responses": {
          "200": {
            "$ref": "#/components/responses/FanCollection"
          }
        }
      }
    },
    "/api/fancollection/1/collection_items": {
      "post": {
        "summary": "Get a user's collection",
        "tags": [
          "Fan Collection"
        ],
        "parameters": [
          {
            "$ref": "#/components/parameters/IdentityCookie"
          }
        ],
        "requestBody": {
          "$ref": "#/components/requestBodies/FanCollectionBody"
        },
        "responses": {
          "200": {
            "$ref": "#/components/responses/ItemCollection"
          }
        }
      }
    },
    "/api/fancollection/1/wishlist_items": {
      "post": {
        "summary": "Get a user's wishlist",
        "tags": [
          "Fan Collection"
        ],
        "requestBody": {
          "$ref": "#/components/requestBodies/FanCollectionBody"
        },
        "responses": {
          "200": {
            "$ref": "#/components/responses/ItemCollection"
          }
        }
      }
    },
    "/api/fancollection/1/fan_suggestions": {
      "post": {
        "summary": "Get a user's suggestions? Don't know, really.",
        "tags": [
          "Fan Collection",
          "To be figured out"
        ],
        "requestBody": {
          "$ref": "#/components/requestBodies/FanCollectionBody"
        },
        "responses": {
          "200": {
            "description": "The suggestions"
          }
        }
      }
    },
    "/api/fancollection/1/gifts_given": {
      "post": {
        "summary": "Get the gifts a user has given?? Don't know, really.",
        "tags": [
          "Fan Collection",
          "To be figured out"
        ],
        "requestBody": {
          "$ref": "#/components/requestBodies/FanCollectionBody"
        },
        "responses": {
          "200": {
            "description": "The gifts"
          }
        }
      }
    },
    "/api/fan/2/collection_summary": {
      "get": {
        "summary": "get a rough summary of the user's collection (items bought etc.)",
        "tags": [
          "Fan Collection"
        ],
        "parameters": [
          {
            "in": "header",
            "schema": {
              "type": "string"
            },
            "name": "Cookie",
            "example": "identity=6%0909ddd7b4f6eca8250cedc5f42546bd39%09%7B%22id%22%3A1712700664%2C%22h1%22%3A%220a36b7aa8ce0b80c36b86d5bc4d0e735%22%2C%22ex%22%3A0%7D;"
          }
        ],
        "responses": {
          "200": {
            "description": "a summary of the user's collection",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "fan_id": {
                      "$ref": "#/components/schemas/FanID"
                    },
                    "collection_summary": {
                      "type": "object",
                      "properties": {
                        "fan_id": {
                          "$ref": "#/components/schemas/FanID"
                        },
                        "tralbum_lookup": {
                          "type": "object",
                          "properties": {
                            "<item id>": {
                              "type": "object",
                              "description": "the key is something like 'a1234', where 'a' is for albums - see ItemType?",
                              "properties": {
                                "item_id": {
                                  "anyOf": [
                                    {
                                      "$ref": "#/components/schemas/AlbumID"
                                    },
                                    {
                                      "$ref": "#/components/schemas/TrackID"
                                    }
                                  ]
                                },
                                "band_id": {
                                  "$ref": "#/components/schemas/ArtistID"
                                },
                                "purchased": {
                                  "type": "string",
                                  "description": "formatted timestamp of purchase, or null if not purchased (wishlist?)"
                                },
                                "item_type": {
                                  "$ref": "#/components/schemas/ItemType"
                                }
                              }
                            }
                          }
                        },
                        "follows": {
                          "type": "object",
                          "properties": {
                            "following": {
                              "type": "object",
                              "properties": {
                                "<fan id>": {
                                  "description": "keys are the followed fans/bands' IDs",
                                  "type": "boolean"
                                }
                              }
                            }
                          }
                        },
                        "url": {
                          "type": "string",
                          "format": "URL",
                          "description": "the fan's home on bandcamp.com",
                          "example": "https://bandcamp.com/michaelherger"
                        },
                        "username": {
                          "type": "string",
                          "example": "michaelherger"
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/fan_feed_poll": {
      "post": {
        "summary": "get the user's \"Music Feed\"",
        "tags": [
          "To be figured out"
        ],
        "parameters": [
          {
            "in": "header",
            "schema": {
              "type": "string"
            },
            "name": "Cookie",
            "example": "__utma=241114640.1402729518.1499081724.1519889216.1520672370.21; __utmb=241114640.2.10.1520672370; __utmc=241114640; __utmz=241114640.1519881502.19.8.utmcsr=google|utmccn=(organic)|utmcmd=organic|utmctr=(not%20provided); __utmt=1; session=1%09t%3A1520672367%09bp%3A1%09r%3A%5B%22nilZ0f0x1520672367%22%2C%2239731231s0a793714377x1519890154%22%2C%2239731231s0i2240734681x1519890153%22%5D; BACKENDID=bender01-3; fan_visits=10236z4679z188671z482972; identity=6%0909ddd7b4f6eca8250cedc5f42546bd39%09%7B%22id%22%3A1712700664%2C%22h1%22%3A%220a36b7aa8ce0b80c36b86d5bc4d0e735%22%2C%22ex%22%3A0%7D; logout=%7B%22username%22%3A%22paypal%40herger.net%22%7D; want_https=1; __qca=P0-412173266-1499081724027; client_id=D38AB1D81E70CC68504277E9FE450FABC8D939D402E73A4D4A8212974965AAF4"
          }
        ],
        "requestBody": {
          "description": "JSON data of yet to understand data",
          "required": true,
          "content": {
            "application/x-www-form-urlencoded": {
              "schema": {
                "type": "object",
                "properties": {
                  "since": {
                    "$ref": "#/components/schemas/Timestamp"
                  },
                  "crumb": {
                    "type": "string",
                    "description": "XXX",
                    "example": "|fan_feed_poll|1520674192|JQgsXLOGnRKBBslOCPQXqpTCKJ0="
                  }
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "the feed"
          }
        }
      }
    },
    "/fan_dash_feed_updates": {
      "post": {
        "summary": "The user's \"Music Feed\" of music suggestions",
        "tags": [
          "Music Feed"
        ],
        "parameters": [
          {
            "$ref": "#/components/parameters/IdentityCookie"
          }
        ],
        "requestBody": {
          "description": "JSON data of yet to understand data",
          "required": true,
          "content": {
            "application/x-www-form-urlencoded": {
              "schema": {
                "type": "object",
                "properties": {
                  "older_than": {
                    "allOf": [
                      {
                        "$ref": "#/components/schemas/Timestamp"
                      },
                      {
                        "description": "the unix timestamp from when the stream should start"
                      }
                    ]
                  },
                  "fan_id": {
                    "$ref": "#/components/schemas/FanID"
                  }
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "the Music Feed",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "stories": {
                      "type": "object",
                      "properties": {
                        "entries": {
                          "description": "the stream's items",
                          "type": "array",
                          "items": {
                            "description": "a Music Stream item",
                            "type": "object",
                            "properties": {
                              "item_type": {
                                "$ref": "#/components/schemas/ItemType"
                              }
                            }
                          }
                        },
                        "track_list": {
                          "description": "the list of playable items from the Music Stream?",
                          "type": "object",
                          "properties": {
                            "entries": {
                              "description": "the stream's items",
                              "type": "array",
                              "items": {
                                "anyOf": [
                                  {
                                    "$ref": "#/components/schemas/Track"
                                  }
                                ]
                              }
                            }
                          }
                        },
                        "query_times": {
                          "description": "the times spent on the various queries required to compile the Music Stream. \"Query\":\"Time\" pairs",
                          "type": "object"
                        },
                        "oldest_story_date": {
                          "$ref": "#/components/schemas/Timestamp"
                        },
                        "feed_timestamp": {
                          "$ref": "#/components/schemas/Timestamp"
                        },
                        "newest_story_date": {
                          "allOf": [
                            {
                              "$ref": "#/components/schemas/Timestamp"
                            },
                            {
                              "description": "the timestamp of the most recent story in the result list"
                            }
                          ]
                        }
                      }
                    },
                    "fan_info": {
                      "description": "information about the fans featured in the stream",
                      "type": "object",
                      "properties": {
                        "<FanID>": {
                          "$ref": "#/components/schemas/Fan"
                        }
                      }
                    },
                    "band_info": {
                      "description": "information about the bands featured in the stream",
                      "type": "object",
                      "properties": {
                        "<ArtistID>": {
                          "type": "object",
                          "properties": {
                            "name": {
                              "$ref": "#/components/schemas/ArtistName"
                            },
                            "band_id": {
                              "$ref": "#/components/schemas/ArtistID"
                            },
                            "image_id": {
                              "$ref": "#/components/schemas/ArtworkID"
                            },
                            "genre_id": {
                              "type": "integer"
                            },
                            "followed": {
                              "type": "integer"
                            },
                            "no_index": {
                              "$ref": "#/components/schemas/UnknownProperty"
                            },
                            "latest_art_id": {
                              "$ref": "#/components/schemas/UnknownProperty"
                            }
                          }
                        }
                      }
                    },
                    "story_collectors": {
                      "description": "fans of one of the stream items?",
                      "type": "array",
                      "items": {
                        "$ref": "#/components/schemas/UnknownProperty"
                      }
                    },
                    "item_lookup": {
                      "description": "unknown, just empty?",
                      "type": "object"
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/api/bcweekly/2/list": {
      "get": {
        "summary": "Get a list of the Weekly shows.",
        "tags": [
          "Weekly"
        ],
        "responses": {
          "200": {
            "description": "the list of available Weekly shows",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "results": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "published_date": {
                            "type": "string",
                            "description": "a formatted timestamp",
                            "example": "27 Sep 2016 00:00:00 GMT"
                          },
                          "date": {
                            "type": "string",
                            "description": "a formatted timestamp",
                            "example": "27 Sep 2016 00:00:00 GMT"
                          },
                          "image_caption": {
                            "$ref": "#/components/schemas/ImageCaption"
                          },
                          "subtitle": {
                            "type": "string",
                            "example": "All in the Family"
                          },
                          "desc": {
                            "$ref": "#/components/schemas/ShowDescription"
                          },
                          "image_id": {
                            "type": "integer",
                            "description": "smallest?"
                          },
                          "v2_image_id": {
                            "type": "integer",
                            "description": "largest?"
                          },
                          "screen_image_id": {
                            "type": "integer",
                            "description": "middle size, still 1000px?"
                          },
                          "id": {
                            "type": "integer",
                            "example": 198
                          },
                          "button_color": {
                            "type": "string",
                            "description": "a HTML/CSS color definition, likely matching the artwork",
                            "example": "f03b2b"
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    },
    "/api/bcweekly/2/get": {
      "get": {
        "summary": "Get information about a Weekly show.",
        "tags": [
          "Weekly"
        ],
        "parameters": [
          {
            "in": "query",
            "name": "id",
            "schema": {
              "type": "integer"
            },
            "example": 270,
            "required": true
          }
        ],
        "responses": {
          "200": {
            "description": "the list of available Weekly shows",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "show_id": {
                      "type": "integer",
                      "example": 198
                    },
                    "title": {
                      "type": "string",
                      "description": "always the same?",
                      "example": "Bandcamp Weekly"
                    },
                    "subtitle": {
                      "type": "string",
                      "example": "All in the Family"
                    },
                    "desc": {
                      "$ref": "#/components/schemas/ShowDescription"
                    },
                    "short_desc": {
                      "type": "string",
                      "example": "Goat & Reginald Omas Mamode IV"
                    },
                    "audio_title": {
                      "type": "string",
                      "example": "BC Weekly 183 September 27th 2016"
                    },
                    "show_screen_image_id": {
                      "type": "integer",
                      "example": 8380257
                    },
                    "show_v2_image_id": {
                      "type": "integer",
                      "example": 8387090
                    },
                    "show_image_id": {
                      "type": "integer",
                      "example": 8387090
                    },
                    "audio_track_id": {
                      "type": "integer",
                      "description": "pointing to single file for whole podcast"
                    },
                    "audio_duration": {
                      "type": "number",
                      "example": 5388.41
                    },
                    "audio_stream": {
                      "type": "object",
                      "description": "pointing to single file for whole podcast",
                      "properties": {
                        "mp3-128": {
                          "type": "string",
                          "format": "url",
