  "paths": {
    "/Traces": {
      "post": {
        "operationId": "BatchGetTraces",
        "description": "Retrieves a list of traces specified by ID. Each trace is a collection of segment documents that originates from a single request. Use <code>GetTraceSummaries</code> to get a list of trace IDs.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/BatchGetTracesResult"
                }
              }
            }
          },
          "480": {
            "description": "InvalidRequestException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidRequestException"
                }
              }
            }
          },
          "481": {
            "description": "ThrottledException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ThrottledException" }
              }
            }
          }
        },
        "parameters": [
          {
            "name": "NextToken",
            "in": "query",
            "schema": { "type": "string" },
            "description": "Pagination token",
            "required": false
          }
        ],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["TraceIds"],
                "properties": {
                  "TraceIds": {
                    "description": "Specify the trace IDs of requests for which to retrieve segments.",
                    "type": "array",
                    "items": { "$ref": "#/components/schemas/TraceId" }
                  },
                  "NextToken": {
                    "description": "Pagination token.",
                    "type": "string"
                  }
                }
              }
            }
          }
        },
        "summary": "Batch Get Traces"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ]
    },
    "/CreateGroup": {
      "post": {
        "operationId": "CreateGroup",
        "description": "Creates a group resource with a name and a filter expression. ",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/CreateGroupResult" }
              }
            }
          },
          "480": {
            "description": "InvalidRequestException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidRequestException"
                }
              }
            }
          },
          "481": {
            "description": "ThrottledException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ThrottledException" }
              }
            }
          }
        },
        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["GroupName"],
                "properties": {
                  "GroupName": {
                    "description": "The case-sensitive name of the new group. Default is a reserved name and names must be unique.",
                    "type": "string",
                    "minLength": 1,
                    "maxLength": 32
                  },
                  "FilterExpression": {
                    "description": "The filter expression defining criteria by which to group traces.",
                    "type": "string"
                  },
                  "InsightsConfiguration": {
                    "description": "The structure containing configurations related to insights.",
                    "type": "object",
                    "properties": {
                      "InsightsEnabled": {
                        "allOf": [
                          { "$ref": "#/components/schemas/NullableBoolean" },
                          {
                            "description": "Set the InsightsEnabled value to true to enable insights or false to disable insights."
                          }
                        ]
                      },
                      "NotificationsEnabled": {
                        "allOf": [
                          { "$ref": "#/components/schemas/NullableBoolean" },
                          {
                            "description": "Set the NotificationsEnabled value to true to enable insights notifications. Notifications can only be enabled on a group with InsightsEnabled set to true."
                          }
                        ]
                      }
                    }
                  },
                  "Tags": {
                    "description": "<p>A map that contains one or more tag keys and tag values to attach to an X-Ray group. For more information about ways to use tags, see <a href=\"https://docs.aws.amazon.com/general/latest/gr/aws_tagging.html\">Tagging AWS resources</a> in the <i>AWS General Reference</i>.</p> <p>The following restrictions apply to tags:</p> <ul> <li> <p>Maximum number of user-applied tags per resource: 50</p> </li> <li> <p>Maximum tag key length: 128 Unicode characters</p> </li> <li> <p>Maximum tag value length: 256 Unicode characters</p> </li> <li> <p>Valid values for key and value: a-z, A-Z, 0-9, space, and the following characters: _ . : / = + - and @</p> </li> <li> <p>Tag keys and values are case sensitive.</p> </li> <li> <p>Don't use <code>aws:</code> as a prefix for keys; it's reserved for AWS use.</p> </li> </ul>",
                    "type": "array",
                    "items": { "$ref": "#/components/schemas/Tag" },
                    "minItems": 0,
                    "maxItems": 200
                  }
                }
              }
            }
          }
        },
        "summary": "Create Group"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ]
    },
    "/CreateSamplingRule": {
      "post": {
        "operationId": "CreateSamplingRule",
        "description": "Creates a rule to control sampling behavior for instrumented applications. Services retrieve rules with <a>GetSamplingRules</a>, and evaluate each rule in ascending order of <i>priority</i> for each request. If a rule matches, the service records a trace, borrowing it from the reservoir size. After 10 seconds, the service reports back to X-Ray with <a>GetSamplingTargets</a> to get updated versions of each in-use rule. The updated rule contains a trace quota that the service can use instead of borrowing from the reservoir.",
        "responses": {
          "200": {
            "description": "Success",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/CreateSamplingRuleResult"
                }
              }
            }
          },
          "480": {
            "description": "InvalidRequestException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InvalidRequestException"
                }
              }
            }
          },
          "481": {
            "description": "ThrottledException",
            "content": {
              "application/json": {
                "schema": { "$ref": "#/components/schemas/ThrottledException" }
              }
            }
          },
          "482": {
            "description": "RuleLimitExceededException",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/RuleLimitExceededException"
                }
              }
            }
          }
        },
        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["SamplingRule"],
                "properties": {
                  "SamplingRule": {
                    "description": "A sampling rule that services use to decide whether to instrument a request. Rule fields can match properties of the service, or properties of a request. The service can ignore rules that don't match its properties.",
                    "type": "object",
                    "properties": {
                      "RuleName": {
                        "allOf": [
                          { "$ref": "#/components/schemas/RuleName" },
                          {
                            "description": "The name of the sampling rule. Specify a rule by either name or ARN, but not both."
                          }
                        ]
                      },
                      "RuleARN": {
                        "allOf": [
                          { "$ref": "#/components/schemas/String" },
                          {
                            "description": "The ARN of the sampling rule. Specify a rule by either name or ARN, but not both."
                          }
                        ]
                      },
                      "ResourceARN": {
                        "allOf": [
                          { "$ref": "#/components/schemas/ResourceARN" },
                          {
                            "description": "Matches the ARN of the AWS resource on which the service runs."
                          }
                        ]
                      },
                      "Priority": {
                        "allOf": [
                          { "$ref": "#/components/schemas/Priority" },
                          {
                            "description": "The priority of the sampling rule."
                          }
                        ]
                      },
                      "FixedRate": {
                        "allOf": [
                          { "$ref": "#/components/schemas/FixedRate" },
                          {
                            "description": "The percentage of matching requests to instrument, after the reservoir is exhausted."
                          }
                        ]
                      },
                      "ReservoirSize": {
                        "allOf": [
                          { "$ref": "#/components/schemas/ReservoirSize" },
                          {
                            "description": "A fixed number of matching requests to instrument per second, prior to applying the fixed rate. The reservoir is not used directly by services, but applies to all services using the rule collectively."
                          }
                        ]
                      },
                      "ServiceName": {
                        "allOf": [
                          { "$ref": "#/components/schemas/ServiceName" },
                          {
                            "description": "Matches the <code>name</code> that the service uses to identify itself in segments."
                          }
                        ]
                      },
                      "ServiceType": {
                        "allOf": [
                          { "$ref": "#/components/schemas/ServiceType" },
                          {
                            "description": "Matches the <code>origin</code> that the service uses to identify its type in segments."
                          }
                        ]
                      },
                      "Host": {
                        "allOf": [
                          { "$ref": "#/components/schemas/Host" },
                          {
                            "description": "Matches the hostname from a request URL."
                          }
                        ]
                      },
                      "HTTPMethod": {
                        "allOf": [
                          { "$ref": "#/components/schemas/HTTPMethod" },
                          {
                            "description": "Matches the HTTP method of a request."
                          }
                        ]
                      },
                      "URLPath": {
                        "allOf": [
                          { "$ref": "#/components/schemas/URLPath" },
                          {
                            "description": "Matches the path from a request URL."
                          }
                        ]
                      },
                      "Version": {
                        "allOf": [
                          { "$ref": "#/components/schemas/Version" },
                          {
                            "description": "The version of the sampling rule format (<code>1</code>)."
                          }
                        ]
                      },
                      "Attributes": {
                        "allOf": [
                          { "$ref": "#/components/schemas/AttributeMap" },
                          {
                            "description": "Matches attributes derived from the request."
                          }
                        ]
                      }
                    }
                  },
                  "Tags": {
                    "description": "<p>A map that contains one or more tag keys and tag values to attach to an X-Ray sampling rule. For more information about ways to use tags, see <a href=\"https://docs.aws.amazon.com/general/latest/gr/aws_tagging.html\">Tagging AWS resources</a> in the <i>AWS General Reference</i>.</p> <p>The following restrictions apply to tags:</p> <ul> <li> <p>Maximum number of user-applied tags per resource: 50</p> </li> <li> <p>Maximum tag key length: 128 Unicode characters</p> </li> <li> <p>Maximum tag value length: 256 Unicode characters</p> </li> <li> <p>Valid values for key and value: a-z, A-Z, 0-9, space, and the following characters: _ . : / = + - and @</p> </li> <li> <p>Tag keys and values are case sensitive.</p> </li> <li> <p>Don't use <code>aws:</code> as a prefix for keys; it's reserved for AWS use.</p> </li> </ul>",
                    "type": "array",
                    "items": { "$ref": "#/components/schemas/Tag" },
                    "minItems": 0,
                    "maxItems": 200
                  }
                }
              }
            }
          }
        },
        "summary": "Create Sampling Rule"
      },
      "parameters": [
        { "$ref": "#/components/parameters/X-Amz-Content-Sha256" },
        { "$ref": "#/components/parameters/X-Amz-Date" },
        { "$ref": "#/components/parameters/X-Amz-Algorithm" },
        { "$ref": "#/components/parameters/X-Amz-Credential" },
        { "$ref": "#/components/parameters/X-Amz-Security-Token" },
        { "$ref": "#/components/parameters/X-Amz-Signature" },
        { "$ref": "#/components/parameters/X-Amz-SignedHeaders" }
      ]
    },
