              "application/json": {
                "schema": { "$ref": "#/components/schemas/ThrottledException" }
              }
            }
          }
        },
        "parameters": [],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["TraceSegmentDocuments"],
                "properties": {
                  "TraceSegmentDocuments": {
                    "description": "A string containing a JSON document defining one or more segments or subsegments.",
