            "schema": {
              "type": "string",
              "pattern": "^[a-zA-Z0-9_-]+$",
              "minLength": 1,
              "maxLength": 128
            }
          },
          {
            "name": "stateName",
            "in": "query",
            "required": false,
            "description": "A filter that limits results to those detectors (instances) in the given state.",
