{
  "openapi": "3.0.3",
  "info": {
    "title": "Polygon Websocket Spec",
    "description": "The future of fintech.",
    "version": "2.0.0"
  },
  "servers": [
    {
      "url": "wss://socket.polygon.io",
      "description": "Real-time"
    },
    {
      "url": "wss://delayed.polygon.io",
      "description": "Delayed"
    }
  ],
