      "hlrCreate": {
        "type": "object",
        "properties": {
          "data": {
            "type": "object",
            "properties": {
              "type": {
                "type": "string",
                "enum": [
                  "higherLevelReview"
                ]
              },
              "attributes": {
                "description": "If informal conference requested (`informalConference: true`), contact (`informalConferenceContact`) and time (`informalConferenceTime`) must be specified.",
                "type": "object",
                "additionalProperties": false,
                "properties": {
                  "informalConference": {
                    "type": "boolean"
                  },
                  "benefitType": {
                    "type": "string",
                    "enum": [
                      "compensation",
                      "pensionSurvivorsBenefits",
                      "fiduciary",
                      "lifeInsurance",
                      "veteransHealthAdministration",
                      "veteranReadinessAndEmployment",
                      "loanGuaranty",
                      "education",
                      "nationalCemeteryAdministration"
                    ]
                  },
                  "veteran": {
                    "type": "object",
                    "properties": {
                      "homeless": {
                        "type": "boolean"
                      },
                      "address": {
                        "$ref": "#/components/schemas/address"
                      },
                      "phone": {
                        "$ref": "#/components/schemas/phone"
                      },
                      "email": {
                        "type": "string",
                        "format": "email",
                        "minLength": 6,
                        "maxLength": 255
                      },
                      "timezone": {
                        "$ref": "#/components/schemas/timezone"
                      }
                    },
                    "additionalProperties": false,
                    "required": [
                      "homeless"
                    ],
                    "if": {
                      "properties": {
                        "homeless": {
                          "const": false
                        }
                      }
                    },
                    "then": {
                      "required": [
                        "address"
                      ]
                    }
                  },
                  "claimant": {
                    "type": "object",
                    "description": "If the claimant is a non-Veteran, claimant information is required. Without this information, we will be unable to identify the claimant. If you're a healthcare provider or an employee of a healthcare provider requesting review of a VA payment decision, you must identify the healthcare provider as the claimant and complete all relevant information in the claimant object.",
                    "properties": {
                      "address": {
                        "$ref": "#/components/schemas/address"
                      },
                      "phone": {
                        "$ref": "#/components/schemas/phone"
                      },
                      "email": {
                        "type": "string",
                        "format": "email",
                        "minLength": 6,
                        "maxLength": 255
                      },
                      "timezone": {
                        "$ref": "#/components/schemas/timezone"
                      }
                    },
                    "required": [
                      "address",
                      "phone"
                    ]
                  },
                  "informalConferenceContact": {
                    "type": "string",
                    "enum": [
                      "veteran",
                      "representative"
                    ]
                  },
                  "informalConferenceTime": {
                    "type": "string",
                    "enum": [
                      "800-1200 ET",
                      "1200-1630 ET"
                    ]
                  },
                  "informalConferenceRep": {
                    "type": "object",
                    "description": "The Representative information listed MUST match the current Power of Attorney for the Veteran.  Any changes to the Power of Attorney must be submitted via a VA 21-22 form separately.",
                    "properties": {
                      "firstName": {
                        "type": "string",
                        "maxLength": 30
                      },
                      "lastName": {
                        "type": "string",
                        "maxLength": 40
                      },
                      "phone": {
                        "$ref": "#/components/schemas/phone"
                      },
                      "email": {
                        "type": "string",
                        "format": "email",
                        "minLength": 6,
                        "maxLength": 255
                      }
                    },
                    "additionalProperties": false,
                    "required": [
                      "firstName",
                      "lastName",
                      "phone"
                    ]
                  },
                  "socOptIn": {
                    "type": "boolean"
                  }
                },
                "required": [
                  "informalConference",
                  "benefitType",
                  "veteran",
                  "socOptIn"
                ],
                "if": {
                  "properties": {
                    "informalConference": {
                      "const": true
                    }
                  }
                },
                "then": {
                  "required": [
                    "informalConferenceContact",
                    "informalConferenceTime"
                  ]
                }
              }
            },
            "additionalProperties": false,
            "required": [
              "type",
              "attributes"
            ]
          },
          "included": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "type": {
                  "type": "string",
                  "enum": [
                    "contestableIssue"
                  ]
                },
                "attributes": {
                  "type": "object",
                  "properties": {
                    "issue": {
                      "allOf": [
                        {
                          "$ref": "#/components/schemas/non_blank_string"
                        },
                        {
                          "minLength": 1,
                          "maxLength": 140
                        }
                      ]
                    },
                    "decisionDate": {
                      "type": "string",
                      "format": "date"
                    },
                    "decisionIssueId": {
                      "type": "integer"
                    },
                    "ratingIssueReferenceId": {
                      "type": "string"
                    },
                    "ratingDecisionReferenceId": {
                      "type": "string"
                    },
                    "socDate": {
                      "type": "string",
                      "format": "date"
                    },
                    "disagreementArea": {
                      "type": "string",
                      "maxLength": 90
                    }
                  },
                  "additionalProperties": false,
                  "required": [
                    "issue",
                    "decisionDate"
                  ]
                }
              },
              "additionalProperties": false,
              "required": [
                "type",
                "attributes"
              ]
            },
            "minItems": 1,
            "uniqueItems": true
          }
        },
        "additionalProperties": false,
        "required": [
          "data",
          "included"
        ]
      },
      "hlrShow": {
