      "uuid": {
        "type": "string",
        "pattern": "^[0-9a-fA-F]{8}(-[0-9a-fA-F]{4}){3}-[0-9a-fA-F]{12}$"
      },
      "X-Consumer-ID": {
        "allOf": [
          {
            "description": "Consumer GUID"
          },
          {
            "$ref": "#/components/schemas/non_blank_string"
          }
        ]
      },
      "X-Consumer-Username": {
        "allOf": [
          {
            "description": "Consumer Username (passed from Kong)"
          },
          {
            "$ref": "#/components/schemas/non_blank_string"
          }
        ]
      },
      "X-VA-Birth-Date": {
        "description": "Veteran's birth date",
        "type": "string",
        "format": "date"
      },
      "X-VA-NonVeteranClaimant-Birth-Date": {
        "description": "Claimant's birth date",
        "type": "string",
        "format": "date"
      },
      "X-VA-NonVeteranClaimant-First-Name": {
        "allOf": [
          {
            "description": "first name"
          },
          {
            "$ref": "#/components/schemas/non_blank_string"
          }
        ]
      },
      "X-VA-NonVeteranClaimant-Last-Name": {
        "allOf": [
          {
            "description": "last name"
          },
          {
            "$ref": "#/components/schemas/non_blank_string"
          }
        ]
      },
      "X-VA-NonVeteranClaimant-Middle-Initial": {
        "allOf": [
          {
            "description": "middle initial"
          },
          {
            "$ref": "#/components/schemas/non_blank_string"
          }
        ]
      },
      "X-VA-NonVeteranClaimant-SSN": {
        "description": "social security number",
        "type": "string",
        "minLength": 9,
        "maxLength": 9,
        "pattern": "^[0-9]{9}$"
      },
      "X-VA-File-Number": {
        "allOf": [
          {
            "description": "VA file number (c-file / css)"
          },
          {
            "maxLength": 9
          },
          {
            "$ref": "#/components/schemas/non_blank_string"
          }
        ]
      },
      "X-VA-First-Name": {
        "allOf": [
          {
            "description": "first name"
          },
          {
            "type": "string"
          }
        ]
      },
      "X-VA-Last-Name": {
        "allOf": [
          {
            "description": "last name"
          },
          {
            "$ref": "#/components/schemas/non_blank_string"
          }
        ]
      },
      "X-VA-Middle-Initial": {
        "allOf": [
          {
            "description": "middle initial"
          },
          {
            "$ref": "#/components/schemas/non_blank_string"
          }
        ]
      },
      "X-VA-SSN": {
        "description": "social security number",
        "type": "string",
        "minLength": 9,
        "maxLength": 9,
        "pattern": "^[0-9]{9}$"
      }
    }
