  "components": {
    "securitySchemes": {
      "BearerAuth": {
        "type": "http",
        "scheme": "bearer",
        "bearerFormat": "JWT",
        "description": "Some few endpoints do not require authentication, but pretty much anything else will require you to be authenticated.\nTo become authenticated you will need to request an API access token through the `/users/login` endpoint.\n\n## Using an access token\nOnce you have an access token for a given user you can pass it along in the HTTP request header.\nAlternatively, if consuming the API through a web application, the token can be sent in the `AuthToken` HTTPOnly cookie as part of the HTTP request.\n"
      }
    },
    "parameters": {
      "acceptHeader": {
        "name": "Accept",
        "in": "header",
        "required": true,
        "schema": {
          "type": "string"
        },
        "description": "application/json"
      },
      "authHeader": {
        "name": "Authorization",
        "in": "header",
        "required": true,
        "schema": {
          "type": "string"
        },
        "description": "Bearer {{access_token}}"
      },
      "contentTypeHeader": {
        "name": "Content-Type",
        "in": "header",
        "required": true,
        "schema": {
          "type": "string"
        },
        "description": "application/json"
      },
      "pageNumber": {
        "in": "query",
        "name": "page",
        "schema": {
          "type": "integer",
          "minimum": 1
        },
        "required": false,
        "example": 1,
        "description": "Number of the page to be retrieved"
      },
      "pageSize": {
        "in": "query",
        "name": "per_page",
        "schema": {
          "type": "integer",
          "minimum": 1,
          "maximum": 100
        },
        "required": false,
        "example": 25,
        "description": "Number of items returned per page"
      }
    },
    "schemas": {
      "AcceptedResponseMessage": {
        "type": "object",
        "properties": {
          "message": {
            "type": "string",
            "description": "Message with information about the response",
            "example": "Successful"
          }
        },
        "required": [
          "message"
        ]
      },
      "Action": {
        "type": "object",
        "nullable": true,
        "properties": {
          "id": {
            "type": "integer",
            "description": "The Id of the given action",
            "example": 567819876
          },
          "status": {
            "$ref": "#/components/schemas/ActionStatus"
          },
          "type": {
            "$ref": "#/components/schemas/ActionType",
            "description": "The action type"
          },
          "type_human_readable": {
            "type": "string",
            "description": "Human readable version of the action type",
            "example": "Deploy"
          },
          "summary": {
            "$ref": "#/components/schemas/ActionSummary"
          },
          "servers": {
            "$ref": "#/components/schemas/ServerActions"
          },
          "resource": {
            "$ref": "#/components/schemas/Resource"
          },
          "environment": {
            "$ref": "#/components/schemas/EnvironmentRelation"
          },
          "triggered_by_user": {
            "$ref": "#/components/schemas/UserRelation"
          },
          "project": {
            "$ref": "#/components/schemas/ProjectRelation"
          },
          "hook_requests": {
            "$ref": "#/components/schemas/ActionHookRequest"
          },
          "started_at": {
            "type": "string",
            "format": "date-time",
            "description": "The date and time when the action started execution (i.e., left the `pending/queued` status)",
            "example": "2019-09-25T15:50:48.000000Z",
            "nullable": true
          },
          "completed_at": {
            "type": "string",
            "format": "date-time",
            "description": "The date and time when the action has finished execution",
            "example": "2019-09-25T13:52:04.000000Z",
            "nullable": true
          },
          "created_at": {
            "type": "string",
            "format": "date-time",
            "description": "The date and time when the record was created",
            "example": "2019-09-25T15:50:48.000000Z"
          },
          "updated_at": {
            "type": "string",
            "format": "date-time",
            "description": "The date and time when the record was last updated",
            "example": "2019-09-25T13:52:04.000000Z"
          }
        },
        "required": [
          "id",
          "status",
          "type",
          "type_human_readable",
          "summary",
          "resource",
          "triggered_by_user"
        ]
      },
      "ActionHookRequest": {
        "type": "object",
        "description": "The incoming and outgoing hook request linked to this action",
        "properties": {
          "incoming": {
            "$ref": "#/components/schemas/HookRequestRelation"
          },
          "outgoing": {
            "$ref": "#/components/schemas/HookRequestRelation"
          }
        }
      },
      "Actions": {
        "description": "The list of related actions",
        "type": "array",
        "items": {
          "$ref": "#/components/schemas/ActionListItem"
        }
      },
      "ActionListItem": {
        "allOf": [
          {
            "$ref": "#/components/schemas/ActionRelation"
          },
          {
            "$ref": "#/components/schemas/ActionListItemParams"
          }
        ]
      },
      "ActionListItemParams": {
        "type": "object",
        "properties": {
          "servers": {
            "$ref": "#/components/schemas/ServerActions"
          }
        }
      },
      "ActionStatus": {
        "description": "Current item status",
        "type": "string",
        "enum": [
          "pending",
          "in-progress",
          "completed",
          "failed"
        ],
        "example": "in-progress"
      },
      "ActionStep": {
        "type": "object",
        "nullable": true,
        "description": "Action step executed on server",
        "properties": {
          "id": {
            "type": "integer",
            "description": "The unique id of the action step",
            "example": 18931
          },
          "action_id": {
            "type": "integer",
            "description": "The unique id of the action linked to this step",
            "example": 98412
          },
          "action_server_id": {
            "type": "integer",
            "description": "The unique id of the action server linked to this step",
            "example": 65712
          },
          "server_id": {
            "type": "integer",
            "description": "The Id of the server on which the action must be performed",
            "example": 4567890
          },
          "name": {
            "type": "string",
            "description": "Name of the action describing your purpose",
            "example": "Setting up cron job"
          },
          "order": {
            "type": "integer",
            "description": "The execution order of the given step",
            "example": 3
          },
          "status": {
            "$ref": "#/components/schemas/ActionStatus"
          },
          "started_at": {
            "type": "string",
            "format": "date-time",
            "description": "The date and time when the action started execution (i.e., left the `pending/queued` status)",
            "nullable": true,
            "example": "2019-09-25T15:50:48.000000Z"
          },
          "completed_at": {
            "type": "string",
            "format": "date-time",
            "description": "The date and time when the action has finished execution",
            "nullable": true,
            "example": "2019-09-25T13:52:04.000000Z"
          },
          "created_at": {
            "type": "string",
            "format": "date-time",
            "description": "The date and time when the record was created",
            "example": "2019-09-25T15:50:48.000000Z"
          },
          "updated_at": {
            "type": "string",
            "format": "date-time",
            "description": "The date and time when the record was last updated",
            "example": "2019-09-25T13:52:04.000000Z"
          }
        },
        "required": [
          "id",
          "action_id",
          "action_server_id",
          "server_id",
          "name",
          "order",
          "status"
        ]
      },
      "ActionSteps": {
        "description": "The list of action steps",
        "type": "array",
        "items": {
          "$ref": "#/components/schemas/ActionStep"
        }
      },
      "ActionStepLog": {
        "type": "object",
        "properties": {
          "updated_at": {
            "type": "string",
