            "example": true
          },
          "requires_daemon": {
            "description": "Indicates if the application requires a daemon to keep it alive",
            "type": "boolean",
            "example": false
          },
          "project_id": {
            "type": "integer",
            "description": "Numeric ID of the project to which the application belongs to"
