  "paths": {
    "/timetable/personal": {
      "get": {
        "summary": "Returns the personal timetable of the user.",
        "tags": [
          "Timetable"
        ],
        "security": [
          {
            "OAuthSecurity": [
              "personal_timetable"
            ],
            "OAuthToken": []
          }
        ],
        "parameters": [
          {
            "$ref": "#/components/parameters/client_secret"
          },
          {
            "name": "date",
            "in": "query",
            "description": "A date to filter entries by",
            "required": false,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "A timetable.",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "timetable": {
                      "$ref": "#/components/schemas/timetable"
                    },
                    "ok": {
                      "type": "boolean"
                    }
                  }
                }
              }
            }
          },
          "400": {
            "description": "Request error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/error"
