{
  "openapi": "3.0.0",
  "info": {
    "title": "Tezos RPC",
    "description": "Tezos client RPC API.",
    "version": "11.0"
  },
  "paths": {
    "/chains/{chain_id}": {
      "patch": {
        "description": "Forcefully set the bootstrapped flag of the node",
        "parameters": [
          {
            "name": "chain_id",
            "in": "path",
            "description": "A chain identifier. This is either a chain hash in Base58Check notation or a one the predefined aliases: 'main', 'test'.",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "requestBody": {
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "bootstrapped": {
                    "type": "boolean"
                  }
                },
                "required": [
                  "bootstrapped"
                ]
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {}
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/error"
                }
              }
            }
          }
        }
      }
    },
    "/chains/{chain_id}/blocks": {
      "get": {
        "description": "Lists block hashes from '<chain>', up to the last checkpoint, sorted with decreasing fitness. Without arguments it returns the head of the chain. Optional arguments allow to return the list of predecessors of a given block or of a set of blocks.",
        "parameters": [
          {
            "name": "chain_id",
            "in": "path",
            "description": "A chain identifier. This is either a chain hash in Base58Check notation or a one the predefined aliases: 'main', 'test'.",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "length",
            "in": "query",
            "description": "The requested number of predecessors to return (per request; see next argument).",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "head",
            "in": "query",
            "description": "block_hash (Base58Check-encoded) An empty argument requests blocks starting with the current head. A non empty list allows to request one or more specific fragments of the chain.",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "min_date",
            "in": "query",
            "description": "A date in seconds from epoch When `min_date` is provided, blocks with a timestamp before `min_date` are filtered out",
            "required": false,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "type": "array",
                    "items": {
                      "$ref": "#/components/schemas/block_hash"
                    }
                  }
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/error"
                }
              }
            }
          }
        }
      }
    },
    "/chains/{chain_id}/chain_id": {
      "get": {
        "description": "The chain unique identifier.",
        "parameters": [
          {
            "name": "chain_id",
            "in": "path",
            "description": "A chain identifier. This is either a chain hash in Base58Check notation or a one the predefined aliases: 'main', 'test'.",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Chain_id"
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/error"
                }
              }
            }
          }
        }
      }
    },
    "/chains/{chain_id}/checkpoint": {
      "get": {
        "description": "DEPRECATED: use `../levels/{checkpoint, savepoint, caboose, history_mode}` instead. The current checkpoint for this chain.",
        "parameters": [
          {
            "name": "chain_id",
            "in": "path",
            "description": "A chain identifier. This is either a chain hash in Base58Check notation or a one the predefined aliases: 'main', 'test'.",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "block": {
                      "$ref": "#/components/schemas/block_header"
                    },
                    "savepoint": {
                      "type": "integer",
                      "minimum": -2147483648,
                      "maximum": 2147483647
                    },
                    "caboose": {
                      "type": "integer",
                      "minimum": -2147483648,
                      "maximum": 2147483647
                    },
                    "history_mode": {
                      "$ref": "#/components/schemas/history_mode"
                    }
                  },
                  "required": [
                    "block",
                    "savepoint",
                    "caboose",
                    "history_mode"
                  ]
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/error"
                }
              }
            }
          }
        }
      }
    },
    "/chains/{chain_id}/invalid_blocks": {
      "get": {
        "description": "Lists blocks that have been declared invalid along with the errors that led to them being declared invalid.",
        "parameters": [
          {
            "name": "chain_id",
            "in": "path",
            "description": "A chain identifier. This is either a chain hash in Base58Check notation or a one the predefined aliases: 'main', 'test'.",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "array",
                  "items": {
                    "type": "object",
                    "properties": {
                      "block": {
                        "$ref": "#/components/schemas/block_hash"
                      },
                      "level": {
                        "type": "integer",
                        "minimum": -2147483648,
                        "maximum": 2147483647
                      },
                      "errors": {
                        "$ref": "#/components/schemas/error"
                      }
                    },
                    "required": [
                      "block",
                      "level",
                      "errors"
                    ]
                  }
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/error"
                }
              }
            }
          }
        }
      }
    },
    "/chains/{chain_id}/invalid_blocks/{block_hash}": {
      "get": {
        "description": "The errors that appears during the block (in)validation.",
        "parameters": [
          {
            "name": "chain_id",
            "in": "path",
            "description": "A chain identifier. This is either a chain hash in Base58Check notation or a one the predefined aliases: 'main', 'test'.",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "block_hash",
            "in": "path",
            "description": "block_hash (Base58Check-encoded)",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "block": {
                      "$ref": "#/components/schemas/block_hash"
                    },
                    "level": {
                      "type": "integer",
                      "minimum": -2147483648,
                      "maximum": 2147483647
                    },
                    "errors": {
                      "$ref": "#/components/schemas/error"
                    }
                  },
                  "required": [
                    "block",
                    "level",
                    "errors"
                  ]
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/error"
                }
              }
            }
          }
        }
      },
      "delete": {
        "description": "Remove an invalid block for the tezos storage",
        "parameters": [
          {
            "name": "chain_id",
            "in": "path",
            "description": "A chain identifier. This is either a chain hash in Base58Check notation or a one the predefined aliases: 'main', 'test'.",
            "required": true,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "block_hash",
            "in": "path",
            "description": "block_hash (Base58Check-encoded)",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {}
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/error"
                }
              }
            }
          }
        }
      }
    },
    "/chains/{chain_id}/is_bootstrapped": {
      "get": {
        "description": "The bootstrap status of a chain",
        "parameters": [
          {
            "name": "chain_id",
            "in": "path",
            "description": "A chain identifier. This is either a chain hash in Base58Check notation or a one the predefined aliases: 'main', 'test'.",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "bootstrapped": {
                      "type": "boolean"
                    },
                    "sync_state": {
                      "$ref": "#/components/schemas/chain_status"
                    }
                  },
                  "required": [
                    "bootstrapped",
                    "sync_state"
                  ]
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/error"
                }
              }
            }
          }
        }
      }
    },
    "/chains/{chain_id}/levels/caboose": {
      "get": {
        "description": "The current caboose for this chain.",
        "parameters": [
          {
            "name": "chain_id",
            "in": "path",
            "description": "A chain identifier. This is either a chain hash in Base58Check notation or a one the predefined aliases: 'main', 'test'.",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "block_hash": {
                      "$ref": "#/components/schemas/block_hash"
                    },
                    "level": {
                      "type": "integer",
                      "minimum": -2147483648,
                      "maximum": 2147483647
                    }
                  },
                  "required": [
                    "block_hash",
                    "level"
                  ]
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/error"
                }
              }
            }
          }
        }
      }
    },
    "/chains/{chain_id}/levels/checkpoint": {
      "get": {
        "description": "The current checkpoint for this chain.",
        "parameters": [
          {
            "name": "chain_id",
            "in": "path",
            "description": "A chain identifier. This is either a chain hash in Base58Check notation or a one the predefined aliases: 'main', 'test'.",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "block_hash": {
                      "$ref": "#/components/schemas/block_hash"
                    },
                    "level": {
                      "type": "integer",
                      "minimum": -2147483648,
                      "maximum": 2147483647
                    }
                  },
                  "required": [
                    "block_hash",
                    "level"
                  ]
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/error"
                }
              }
            }
          }
        }
      }
    },
    "/chains/{chain_id}/levels/savepoint": {
      "get": {
        "description": "The current savepoint for this chain.",
        "parameters": [
          {
            "name": "chain_id",
            "in": "path",
            "description": "A chain identifier. This is either a chain hash in Base58Check notation or a one the predefined aliases: 'main', 'test'.",
            "required": true,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "block_hash": {
                      "$ref": "#/components/schemas/block_hash"
                    },
                    "level": {
                      "type": "integer",
                      "minimum": -2147483648,
                      "maximum": 2147483647
                    }
                  },
                  "required": [
                    "block_hash",
                    "level"
                  ]
                }
              }
            }
          },
          "default": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/error"
                }
              }
            }
          }
        }
      }
    },
    "/config": {
      "get": {
        "description": "Return the runtime node configuration (this takes into account the command-line arguments and the on-disk configuration file)",
        "responses": {
          "200": {
            "description": "",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "data-dir": {
                      "description": "Location of the data dir on disk.",
                      "oneOf": [
                        {
                          "$ref": "#/components/schemas/unistring"
                        }
                      ]
                    },
                    "disable-config-validation": {
                      "description": "Disable the node configuration validation.",
                      "type": "boolean"
                    },
                    "rpc": {
                      "description": "Configuration of rpc parameters",
                      "type": "object",
                      "properties": {
                        "listen-addrs": {
                          "description": "Hosts to listen to. If the port is not specified, the default port 8732 will be assumed.",
                          "type": "array",
                          "items": {
                            "$ref": "#/components/schemas/unistring"
                          }
                        },
                        "listen-addr": {
                          "description": "Legacy value: Host to listen to",
                          "oneOf": [
                            {
                              "$ref": "#/components/schemas/unistring"
                            }
                          ]
                        },
                        "cors-origin": {
                          "description": "Cross Origin Resource Sharing parameters, see https://en.wikipedia.org/wiki/Cross-origin_resource_sharing.",
                          "type": "array",
                          "items": {
                            "$ref": "#/components/schemas/unistring"
                          }
                        },
                        "cors-headers": {
                          "description": "Cross Origin Resource Sharing parameters, see https://en.wikipedia.org/wiki/Cross-origin_resource_sharing.",
                          "type": "array",
                          "items": {
                            "$ref": "#/components/schemas/unistring"
                          }
                        },
                        "crt": {
                          "description": "Certificate file (necessary when TLS is used).",
                          "oneOf": [
                            {
                              "$ref": "#/components/schemas/unistring"
                            }
                          ]
                        },
                        "key": {
                          "description": "Key file (necessary when TLS is used).",
                          "oneOf": [
                            {
                              "$ref": "#/components/schemas/unistring"
                            }
                          ]
                        },
                        "acl": {
                          "description": "A list of RPC ACLs for specific listening addresses.",
                          "type": "array",
                          "items": {
                            "oneOf": [
                              {
                                "title": "Whitelist",
                                "type": "object",
                                "properties": {
                                  "address": {
                                    "$ref": "#/components/schemas/unistring"
                                  },
                                  "whitelist": {
                                    "type": "array",
                                    "items": {
                                      "$ref": "#/components/schemas/unistring"
                                    }
                                  }
                                },
                                "required": [
                                  "address",
                                  "whitelist"
                                ]
                              },
                              {
                                "title": "Blacklist",
                                "type": "object",
                                "properties": {
                                  "address": {
                                    "$ref": "#/components/schemas/unistring"
                                  },
                                  "blacklist": {
                                    "type": "array",
                                    "items": {
                                      "$ref": "#/components/schemas/unistring"
                                    }
                                  }
                                },
                                "required": [
                                  "address",
                                  "blacklist"
                                ]
                              }
                            ]
                          }
                        }
                      }
                    },
                    "p2p": {
                      "description": "Configuration of network parameters",
                      "type": "object",
                      "properties": {
                        "expected-proof-of-work": {
                          "description": "Floating point number between 0 and 256 that represents a difficulty, 24 signifies for example that at least 24 leading zeroes are expected in the hash.",
                          "type": "integer"
                        },
                        "bootstrap-peers": {
                          "description": "List of hosts. Tezos can connect to both IPv6 and IPv4 hosts. If the port is not specified, default port 9732 will be assumed.",
                          "type": "array",
                          "items": {
                            "$ref": "#/components/schemas/unistring"
                          }
                        },
                        "listen-addr": {
                          "description": "Host to listen to. If the port is not specified, the default port 9732 will be assumed.",
                          "oneOf": [
                            {
                              "$ref": "#/components/schemas/unistring"
                            }
                          ]
                        },
                        "discovery-addr": {
                          "description": "Host for local peer discovery. If the port is not specified, the default port 10732 will be assumed.",
                          "nullable": true,
                          "oneOf": [
                            {
                              "title": "Some",
                              "oneOf": [
                                {
                                  "$ref": "#/components/schemas/unistring"
                                }
                              ]
                            }
                          ]
                        },
                        "private-mode": {
                          "description": "Specify if the node is in private mode or not. A node in private mode rejects incoming connections from untrusted peers and only opens outgoing connections to peers listed in 'bootstrap-peers' or provided with '--peer' option. Moreover, these peers will keep the identity and the address of the private node secret.",
                          "type": "boolean"
                        },
                        "limits": {
                          "description": "Network limits",
                          "type": "object",
                          "properties": {
                            "connection-timeout": {
                              "description": "Delay acceptable when initiating a connection to a new peer, in seconds.",
                              "oneOf": [
                                {
                                  "$ref": "#/components/schemas/timespan.system"
                                }
                              ]
                            },
                            "authentication-timeout": {
                              "description": "Delay granted to a peer to perform authentication, in seconds.",
                              "oneOf": [
                                {
                                  "$ref": "#/components/schemas/timespan.system"
                                }
                              ]
                            },
                            "min-connections": {
                              "description": "Strict minimum number of connections (triggers an urgent maintenance).",
                              "type": "integer",
                              "minimum": 0,
                              "maximum": 65535
                            },
                            "expected-connections": {
                              "description": "Targeted number of connections to reach when bootstrapping / maintaining.",
                              "type": "integer",
                              "minimum": 0,
                              "maximum": 65535
                            },
                            "max-connections": {
                              "description": "Maximum number of connections (exceeding peers are disconnected).",
                              "type": "integer",
                              "minimum": 0,
                              "maximum": 65535
                            },
                            "backlog": {
                              "description": "Number above which pending incoming connections are immediately rejected.",
                              "type": "integer",
                              "minimum": 0,
                              "maximum": 255
                            },
                            "max-incoming-connections": {
                              "description": "Number above which pending incoming connections are immediately rejected.",
                              "type": "integer",
                              "minimum": 0,
                              "maximum": 255
                            },
                            "max-download-speed": {
                              "description": "Max download speeds in KiB/s.",
                              "type": "integer",
                              "minimum": -1073741824,
                              "maximum": 1073741823
                            },
                            "max-upload-speed": {
                              "description": "Max upload speeds in KiB/s.",
                              "type": "integer",
                              "minimum": -1073741824,
                              "maximum": 1073741823
                            },
                            "swap-linger": {
                              "$ref": "#/components/schemas/timespan.system"
                            },
                            "binary-chunks-size": {
                              "type": "integer",
                              "minimum": 0,
                              "maximum": 255
                            },
                            "read-buffer-size": {
                              "description": "Size of the buffer passed to read(2).",
                              "type": "integer",
                              "minimum": -1073741824,
                              "maximum": 1073741823
                            },
                            "read-queue-size": {
                              "type": "integer",
                              "minimum": -1073741824,
                              "maximum": 1073741823
                            },
                            "write-queue-size": {
                              "type": "integer",
                              "minimum": -1073741824,
                              "maximum": 1073741823
                            },
                            "incoming-app-message-queue-size": {
                              "type": "integer",
                              "minimum": -1073741824,
                              "maximum": 1073741823
                            },
                            "incoming-message-queue-size": {
                              "type": "integer",
                              "minimum": -1073741824,
                              "maximum": 1073741823
                            },
                            "outgoing-message-queue-size": {
                              "type": "integer",
                              "minimum": -1073741824,
                              "maximum": 1073741823
                            },
                            "max_known_points": {
                              "type": "array",
                              "items": {
                                "oneOf": [
                                  {
                                    "type": "integer",
                                    "minimum": 0,
                                    "maximum": 65535
                                  },
                                  {
                                    "type": "integer",
                                    "minimum": 0,
                                    "maximum": 65535
                                  }
                                ]
                              }
                            },
                            "max_known_peer_ids": {
                              "description": "The max and target size for the known address table.",
                              "type": "array",
                              "items": {
                                "oneOf": [
                                  {
                                    "type": "integer",
                                    "minimum": 0,
                                    "maximum": 65535
                                  },
                                  {
                                    "type": "integer",
                                    "minimum": 0,
                                    "maximum": 65535
                                  }
                                ]
                              }
                            },
                            "peer_greylist_size": {
                              "description": "The number of peer_ids kept in the peer_id greylist.",
                              "type": "integer",
                              "minimum": 0,
                              "maximum": 65535
                            },
                            "ip_greylist_size_in_kilobytes": {
                              "description": "The size of the IP address greylist (in kilobytes).",
                              "type": "integer",
                              "minimum": 0,
                              "maximum": 65535
                            },
                            "ip_greylist_cleanup_delay": {
                              "description": "The time an IP address is kept in the greylist.",
                              "oneOf": [
                                {
                                  "$ref": "#/components/schemas/timespan.system"
                                }
                              ]
                            },
                            "greylist-timeout": {
                              "description": "GC delay for the greylists tables, in seconds.",
                              "oneOf": [
                                {
                                  "$ref": "#/components/schemas/timespan.system"
                                }
                              ]
                            },
                            "maintenance-idle-time": {
                              "description": "How long to wait at most, in seconds, before running a maintenance loop.",
                              "oneOf": [
                                {
                                  "$ref": "#/components/schemas/timespan.system"
