  "paths": {
    "/distance.{outputFormat}": {
      "get": {
        "summary": "Get distance and travel time between two geographic points",
        "description": "Represents the distance and time of the shortest or fastest path between given start and end points.",
        "tags": [
          "distance"
        ],
        "parameters": [
          {
            "name": "outputFormat",
            "in": "path",
            "description": "Format of representation",
            "required": true,
            "schema": {
              "type": "string",
              "enum": [
                "json",
                "kml",
                "html"
              ],
              "default": "json"
            },
            "example": "json"
          },
          {
            "name": "points",
            "in": "query",
            "description": "A list of any number of route points in start to end order. See <a href=https://github.com/bcgov/api-specs/blob/master/router/glossary.md#points target='_blank'>points</a>",
            "required": true,
            "schema": {
              "type": "string"
            },
            "example": "-123.70794,48.77869,-123.53785,48.38200"
          },
          {
            "name": "outputSRS",
            "in": "query",
            "description": "The EPSG code of the spatial reference system (SRS) to use for output geometries. See <a href=https://github.com/bcgov/api-specs/blob/master/router/glossary.md#outputSRS target=\"_blank\">outputSRS</a>",
            "required": false,
            "schema": {
              "type": "integer",
              "enum": [
                4326,
                4269,
                3005,
                26907,
                26908,
                26909,
                26910,
                26911
              ],
              "default": 4326
            }
          },
          {
            "name": "criteria",
            "in": "query",
            "description": "Routing criteria to optimize (e.g., shortest, fastest). Default is shortest.",
            "required": false,
            "schema": {
              "type": "string",
              "enum": [
                "shortest",
                "fastest"
              ],
              "default": "shortest"
            }
          },
          {
            "name": "distanceUnit",
            "in": "query",
            "description": "distance unit of measure (e.g., km, mi). Default is km.",
            "required": false,
            "schema": {
              "type": "string",
              "enum": [
                "km",
                "mi"
              ],
              "default": "km"
            }
          },
          {
            "name": "roundTrip",
            "in": "query",
            "description": "If true, route ends at start point. Default is false.",
            "required": false,
            "schema": {
              "type": "boolean",
              "default": false
            }
          },
          {
            "name": "departure",
            "in": "query",
            "description": "departure date and time in internet timestamp notation as defined in RFC 3339, section 5.6 (e.g., 2019-02-28T11:36:00-08:00);<br> Ignored if time-dependency modules are disabled",
            "required": false,
            "schema": {
              "type": "string",
              "format": "date-time"
            }
          },
          {
            "name": "correctSide",
            "in": "query",
            "description": "If true, route starts and ends on same side of road as start and end points.Default is false.",
            "required": false,
            "schema": {
              "type": "boolean",
              "default": false
            }
          },
          {
            "name": "disable",
            "in": "query",
            "description": "A comma-separated list of time-related modules to disable (e.g., sc,tf,ev,td).<br>Module names include:<br> sc – ferry schedules; disabled by default; disabled by default and only suitable for demos<br>tf – historic traffic congestion; disabled by default and only suitable for demos<br>ev – road events; disabled by default and only suitable for demos<br>td – time-dependency; disabling this disables sc, tf, and ev modules<br>tr – turn restrictions; if td is disabled, time-dependent turn restrictions are ignored<br>tc - turn costs (e.g., left turns take longer than right turns)",
            "required": false,
            "schema": {
              "type": "string",
              "default": "sc,tf,ev,td"
            }
          },
          {
            "name": "routeDescription",
            "in": "query",
            "description": "Route description (e.g., Shortest route from 1002 Johnson St, Victoria to 1105 Royal Ave,New Westminster)",
            "required": false,
            "schema": {
              "type": "string",
              "default": "Routing results"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "Distance and time of the shortest or fastest route"
          }
        }
      },
