{
  "components": {
    "schemas": {
      "jsonResponse": {
        "properties": {
          "type": {
            "type": "string"
          },
          "queryAddress": {
            "type": "string"
          },
          "searchTimestamp": {
            "type": "string"
          },
          "executionTime": {
            "type": "number"
          },
          "version": {
            "type": "string"
          },
          "baseDataDate": {
            "type": "string",
            "format": "date"
          },
          "crs": {
            "type": "object",
            "properties": {
              "type": {
                "type": "string"
              },
              "properties": {
                "type": "object",
                "properties": {
                  "code": {
                    "type": "integer",
                    "format": "int32"
                  }
                }
              }
            }
          },
          "interpolation": {
            "type": "string"
          },
          "echo": {
            "type": "string"
          },
          "locationDescriptor": {
            "type": "string"
          },
          "setBack": {
            "type": "integer",
            "format": "int32"
          },
          "minScore": {
            "type": "integer",
            "format": "int32"
          },
          "maxResults": {
            "type": "integer",
            "format": "int32"
          },
          "disclaimer": {
            "type": "string"
          },
          "privacyStatement": {
            "type": "string"
          },
          "copyrightNotice": {
            "type": "string"
          },
          "copyrightLicense": {
            "type": "string"
          },
          "features": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "type": {
                  "type": "string"
                },
                "geometry": {
                  "type": "object",
                  "properties": {
                    "type": {
                      "type": "string"
                    },
                    "crs": {
                      "type": "object",
                      "properties": {
                        "type": {
                          "type": "string"
                        },
                        "properties": {
                          "type": "object",
                          "properties": {
                            "code": {
                              "type": "integer",
                              "format": "int32"
                            }
                          }
                        }
                      }
                    },
                    "coordinates": {
                      "type": "array",
                      "items": {
                        "type": "number"
                      }
                    }
                  }
                },
                "properties": {
                  "type": "object",
                  "properties": {
                    "fullAddress": {
                      "type": "string"
                    },
                    "score": {
                      "type": "integer",
                      "format": "int32"
                    },
                    "matchPrecision": {
                      "type": "string"
                    },
                    "precisionPoints": {
                      "type": "integer",
                      "format": "int32"
                    },
                    "faults": {
                      "type": "array",
                      "items": {
                        "type": "object",
                        "properties": {
                          "element": {
                            "type": "string"
                          },
                          "fault": {
                            "type": "string"
                          },
                          "penalty": {
                            "type": "integer",
                            "format": "int32"
                          }
                        }
                      }
                    },
                    "siteName": {
                      "type": "string"
                    },
                    "unitDesignator": {
                      "type": "string"
                    },
                    "unitNumber": {
                      "type": "string"
                    },
                    "unitNumberSuffix": {
                      "type": "string"
                    },
                    "civicNumber": {
                      "type": "integer",
                      "format": "int32"
                    },
                    "civicNumberSuffix": {
                      "type": "string"
                    },
                    "streetName": {
                      "type": "string"
                    },
                    "streetType": {
                      "type": "string"
                    },
                    "isStreetTypePrefix": {
                      "type": "string"
                    },
                    "streetDirection": {
                      "type": "string"
                    },
                    "isStreetDirectionPrefix": {
                      "type": "string"
                    },
                    "streetQualifier": {
                      "type": "string"
                    },
                    "localityName": {
                      "type": "string"
                    },
                    "localityType": {
                      "type": "string"
                    },
                    "electoralArea": {
                      "type": "string"
                    },
                    "provinceCode": {
                      "type": "string"
                    },
                    "locationPositionalAccuracy": {
                      "type": "string"
                    },
                    "locationDescriptor": {
                      "type": "string"
                    },
                    "siteID": {
                      "type": "string"
                    },
                    "blockID": {
                      "type": "integer",
                      "format": "int32"
                    },
                    "fullSiteDescriptor": {
                      "type": "string"
                    },
                    "accessNotes": {
                      "type": "string"
                    },
                    "siteStatus": {
                      "type": "string"
                    },
                    "siteRetireDate": {
                      "type": "string"
                    },
                    "changeDate": {
                      "type": "string"
                    },
                    "isOfficial": {
                      "type": "string"
                    }
                  }
                }
              }
            }
          }
        }
      }
    },

     "securitySchemes": {
        "apikey": {
          "type": "apiKey",
          "in": "header",
          "name": "apikey"
        }
      }

  },
  "externalDocs": {
    "description": "BC Address Geocoder",
    "url": "https://www2.gov.bc.ca/gov/content?id=118DD57CD9674D57BDBD511C2E78DC0D"
  },
  "info": {
    "title": "Geocoder REST API",
    "description": "This API represents address cleaning, correction, completion, geocoding, reverse geocoding, and proximity resources for intersection addresses, physical addresses and their occupants in British Columbia. Please read our [data collection notice](https://github.com/bcgov/api-specs/blob/master/COLLECTION_NOTICE.md#collection-notice).  \n\nPlease note that you may experience issues when submitting requests to the delivery or test environment if using this [OpenAPI specification](https://github.com/bcgov/api-specs) in other API console viewers. \n\n[Developer API keys](https://github.com/bcgov/gwa/wiki/Developer-Guide#developer-api-keys) are unique and provide the ability to make up to 2 requests per second. Production government applications may use organization API keys which are further described in [the Developer guide](https://github.com/bcgov/gwa/wiki/Developer-Guide#developer-api-keys).\n\n **Notification: If you have applications or web pages that link to the public geocoder at one of the following URLs, please note that they are now deprecated [More Details](https://www2.gov.bc.ca/gov/content?id=103ADC5A956842828554238DEF28D6E5).  \n - http://apps.gov.bc.ca/pub/geocoder \n - https://apps.gov.bc.ca/pub/geocoder\n \n \n **To acquire an API key please visit the [API Key Request](https://kq.apps.gov.bc.ca/) application.**\n ",
    "version": "2.0.0",
    "license": {
      "name": "Open Government License - British Columbia",
      "url": "https://www2.gov.bc.ca/gov/content?id=A519A56BC2BF44E4A008B33FCF527F61"
    },
    "termsOfService": "https://www2.gov.bc.ca/gov/content?id=D1EE0A405E584363B205CD4353E02C88",
    "contact": {
      "name": "DataBC",
      "url": "https://data.gov.bc.ca/",
      "email": "IDDDBCLS@Victoria1.gov.bc.ca"
    }
  },
  "openapi": "3.0.0",
  "paths": {
    "/addresses.{outputFormat}": {
      "get": {
        "summary": "Geocode an address",
        "description": "Represents the set of geocoded and standardized sites and intersections whose address best matches a given query address.",
        "tags": [
          "sites",
          "intersections"
        ],
        "parameters": [
          {
            "name": "outputFormat",
            "in": "path",
            "description": "Results format. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#outputFormat target=\"_blank\">outputFormat</a>",
            "required": true,
            "schema": {
              "type": "string",
              "enum": [
                "json",
                "geojson",
                "xhtml",
                "kml",
                "gml",
                "csv",
                "shpz"
              ],
              "default": "json"
            },
            "example": "json"
          },
          {
            "name": "addressString",
            "in": "query",
            "description": "Civic or intersection address as a single string. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#addressString target=\"_blank\">addressString</a>",
            "required": false,
            "schema": {
              "type": "string"
            },
            "example": "525 Superior Street, Victoria, BC"
          },
          {
            "name": "locationDescriptor",
            "in": "query",
            "description": "Describes the nature of the address location. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#locationDescriptor target=\"_blank\">locationDescriptor</a>",
            "required": false,
            "schema": {
              "type": "string",
              "enum": [
                "any",
                "accessPoint",
                "frontDoorPoint",
                "parcelPoint",
                "rooftopPoint",
                "routingPoint"
              ],
              "default": "any"
            }
          },
          {
            "name": "maxResults",
            "in": "query",
            "description": "The maximum number of search results to return.",
            "required": false,
            "schema": {
              "type": "integer",
              "default": 1
            }
          },
          {
            "name": "interpolation",
            "in": "query",
            "description": "accessPoint interpolation method. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#interpolation target=\"_blank\">interpolation</a>",
            "required": false,
            "schema": {
              "type": "string",
              "enum": [
                "adaptive",
                "linear",
                "none"
              ],
              "default": "adaptive"
            }
          },
          {
            "name": "echo",
            "in": "query",
            "description": "If true, include unmatched address details such as site name in results.",
            "required": false,
            "schema": {
              "type": "boolean",
              "default": true
            }
          },
          {
            "name": "brief",
            "in": "query",
            "description": "If true, include only basic match and address details in results. Not supported for shp, csv, and gml formats.",
            "required": false,
            "schema": {
              "type": "boolean",
              "default": false
            }
          },
          {
            "name": "autoComplete",
            "in": "query",
            "description": "If true, addressString is expected to contain a partial address that requires completion. Not supported for shp, csv, gml formats.",
            "required": false,
            "schema": {
              "type": "boolean",
              "default": false
            }
          },
          {
            "name": "setBack",
            "in": "query",
            "description": "The distance to move the accessPoint away from the curb and towards the inside of the parcel (in metres). Ignored if locationDescriptor not set to accessPoint.",
            "required": false,
            "schema": {
              "type": "integer",
              "default": 0
            }
          },
          {
            "name": "outputSRS",
            "in": "query",
            "description": "The EPSG code of the spatial reference system (SRS) to use for output geometries. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#outputSRS target=\"_blank\">outputSRS</a>",
            "required": false,
            "schema": {
              "type": "integer",
              "enum": [
                4326,
                4269,
                3005,
                26907,
                26908,
                26909,
                26910,
                26911
              ],
              "default": 4326
            }
          },
          {
            "name": "minScore",
            "in": "query",
            "description": "The minimum score required for a match to be returned. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#minScore target=\"_blank\">minScore</a>",
            "required": false,
            "schema": {
              "type": "integer",
              "default": 1
            }
          },
          {
            "name": "matchPrecision",
            "in": "query",
            "description": "Example: street,locality.  A comma separated list of individual match precision levels to include in results. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#matchPrecision target=\"_blank\">matchPrecision</a>",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "matchPrecisionNot",
            "in": "query",
            "description": "Example: street,locality.  A comma separated list of individual match precision levels to exclude from results. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#matchPrecisionNot target=\"_blank\">matchPrecisionNot</a>",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "siteName",
            "in": "query",
            "description": "A string containing the name of the building, facility, or institution (e.g., Duck Building, Casa Del Mar, Crystal Garden, Bluebird House).See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#siteName target=\"_blank\">siteName</a>",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "unitDesignator",
            "in": "query",
            "description": "The type of unit within a house or building. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#unitDesignator target=\"_blank\">unitDesignator</a>",
            "required": false,
            "schema": {
              "type": "string",
              "enum": [
                "APT",
                "BLDG",
                "BSMT",
                "FLR",
                "LOBBY",
                "LWR",
                "PAD",
                "PH",
                "REAR",
                "RM",
                "SIDE",
                "SITE",
                "SUITE",
                "TH",
                "UNIT",
                "UPPR"
              ]
            }
          },
          {
            "name": "unitNumber",
            "in": "query",
            "description": "The number of the unit, suite, or apartment within a house or building.",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "unitNumberSuffix",
            "in": "query",
            "description": "A letter that follows the unit number as in Unit 1A or Suite 302B.",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "civicNumber",
            "in": "query",
            "description": "The official number assigned to a site by an address authority. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#civicNumber target=\"_blank\">civicNumber</a>",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "civicNumberSuffix",
            "in": "query",
            "description": "A letter or fraction that follows the civic number (e.g., the A in 1039A Bledsoe St). See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#civicNumberSuffix target=\"_blank\">civicNumberSuffix</a>",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "streetName",
            "in": "query",
            "description": "The official name of the street as assigned by an address authority (e.g., the Douglas in 1175 Douglas Street). See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#streetName target=\"_blank\">streetName</a>",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "streetType",
            "in": "query",
            "description": "The type of street as assigned by a municipality (e.g., the ST in 1175 DOUGLAS St). See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#streetType target=\"_blank\">streetType</a>",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "streetDirection",
            "in": "query",
            "description": "The abbreviated compass direction as defined by Canada Post and B.C. civic addressing authorities. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#streetDirection target=\"_blank\">streetDirection</a>",
            "required": false,
            "schema": {
              "type": "string",
              "enum": [
                "N",
                "S",
                "E",
                "W",
                "O",
                "NE",
                "NO",
                "NW",
                "SE",
                "SO",
                "SW"
              ]
            }
          },
          {
            "name": "streetQualifier",
            "in": "query",
            "description": "Example: the Bridge in Johnson St Bridge. The qualifier of a street name. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#streetQualifier target=\"_blank\">streetQualifier</a>",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "localityName",
            "in": "query",
            "description": "The name of the locality assigned to a given site by an address authority. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#localityName target=\"_blank\">localityName</a>",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "provinceCode",
            "in": "query",
            "description": "The ISO 3166-2 Sub-Country Code. The code for British Columbia is BC.",
            "required": false,
            "schema": {
              "type": "string",
              "default": "BC"
            }
          },
          {
            "name": "localities",
            "in": "query",
            "description": "A comma separated list of locality names that matched addresses must belong to. For example, setting localities to Nanaimo only returns addresses in Nanaimo",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "notLocalities",
            "in": "query",
            "description": "A comma-separated list of localities to exclude from the search.",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "bbox",
            "in": "query",
            "description": "Example: -126.07929,49.7628,-126.0163,49.7907.  A bounding box (xmin,ymin,xmax,ymax) that limits the search area. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#bbox target=\"_blank\">bbox</a>",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "centre",
            "in": "query",
            "description": "Example: -124.0165926,49.2296251 .  The coordinates of a centre point (x,y) used to define a bounding circle that will limit the search area. This parameter must be specified together with 'maxDistance'. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#centre target='_blank'>centre</a>",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "maxDistance",
            "in": "query",
            "description": "The maximum distance (in metres) to search from the given point.  If not specified, the search distance is unlimited.",
            "required": false,
            "schema": {
              "type": "number"
            }
          },
          {
            "name": "extrapolate",
            "in": "query",
            "description": "If true, uses supplied parcelPoint to derive an appropriate accessPoint.           See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#extrapolate target=\"_blank\">extrapolate</a>",
            "required": false,
            "schema": {
              "type": "boolean"
            }
          },
          {
            "name": "parcelPoint",
            "in": "query",
            "description": "The coordinates of a point (x,y) known to be inside the parcel containing a given address. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#parcelPoint target=\"_blank\">parcelPoint</a>",
            "required": false,
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "A list of matching sites or intersections and their physical locations. See <a href='https://github.com/bcgov/api-specs/blob/master/geocoder/geocoder-developer-guide.md#resource-representations-in-http-responses' target=\"_blank\">addresses response</a>",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/jsonResponse"
                }
              }
            }
          }
        }
      }
    },
    "/occupants/addresses.{outputFormat}": {
      "get": {
        "summary": "Geocode an address and identify site occupants",
        "description": "Represents the set of occupants whose addresses best match a given query address. Every occupant has an associated site which has a standardized address and a coordinate location on the Earth.",
        "tags": [
          "occupants"
        ],
        "parameters": [
          {
            "name": "outputFormat",
            "in": "path",
            "description": "Results format. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#outputFormat target=\"_blank\">outputFormat</a>",
            "required": true,
            "schema": {
              "type": "string",
              "enum": [
                "json",
                "geojson",
                "xhtml",
                "kml",
                "gml",
                "csv",
                "shpz"
              ],
              "default": "json"
            },
            "example": "json"
          },
          {
            "name": "addressString",
            "in": "query",
            "description": "Occupant name OR Occupant name ** address",
            "required": false,
            "schema": {
              "type": "string"
            },
            "example": "Sir James Douglas Elementary"
          },
          {
            "name": "tags",
            "in": "query",
            "description": "Example: schools;courts;employment<br>A list of tags separated by semicolons.",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "name": "locationDescriptor",
            "in": "query",
            "description": "Describes the nature of the address location. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#locationDescriptor target=\"_blank\">locationDescriptor</a>",
            "required": false,
            "schema": {
              "type": "string",
              "enum": [
                "any",
                "accessPoint",
                "frontDoorPoint",
                "parcelPoint",
                "rooftopPoint",
                "routingPoint"
              ],
              "default": "any"
            }
          },
          {
            "name": "maxResults",
            "in": "query",
            "description": "The maximum number of search results to return.",
            "required": false,
            "schema": {
              "type": "integer",
              "default": 1
            }
          },
          {
            "name": "interpolation",
            "in": "query",
            "description": "accessPoint interpolation method. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#interpolation target=\"_blank\">interpolation</a>",
            "required": false,
            "schema": {
              "type": "string",
              "enum": [
                "adaptive",
                "linear",
                "none"
              ],
              "default": "adaptive"
            }
          },
          {
            "name": "echo",
            "in": "query",
            "description": "If true, include unmatched address details such as site name in results.",
            "schema": {
              "type": "boolean",
              "default": false
            }
          },
          {
            "name": "brief",
            "in": "query",
            "description": "If true, include only basic match and address details in results. Not supported for shp, csv, and gml formats.",
            "required": false,
            "schema": {
              "type": "boolean",
              "default": false
            }
          },
          {
            "name": "autoComplete",
            "in": "query",
            "description": "If true, addressString is expected to contain a partial address that requires completion. Not supported for shp, csv, gml formats.",
            "required": false,
            "schema": {
              "type": "boolean",
              "default": false
            }
          },
          {
            "name": "setBack",
            "in": "query",
            "description": "The distance to move the accessPoint away from the curb and towards the inside of the parcel (in metres). Ignored if locationDescriptor not set to accessPoint.",
            "required": false,
            "schema": {
              "type": "integer",
              "default": 0
            }
          },
          {
            "name": "outputSRS",
            "in": "query",
            "description": "The EPSG code of the spatial reference system (SRS) to use for output geometries. See <a href=https://github.com/bcgov/api-specs/blob/master/geocoder/glossary.md#outputSRS target=\"_blank\">outputSRS</a>",
            "required": false,
            "schema": {
