    "title": "Request Portal API",
    "description": "Request Portal API Endpoints",
    "contact": {
      "name": "LEAF Support",
      "email": "leafsupport@ablevets.com"
    },
    "license": {
      "name": "Apache 2.0",
      "url": "https://www.apache.org/licenses/LICENSE-2.0.html"
    },
    "version": "2.0.0"
  },
  "servers": [
    {
      "url": "https://localhost/LEAF_Request_Portal/api/",
      "description": "Local DEV server (uses test data)"
    }
  ],
  "components": {
    "responses": {
      "404NotFound": {
        "description": "The specified resource was not found."
      },
      "400InvalidID": {
        "description": "The specified resource was invalid."
      },
      "default": {
        "description": "Unexpected error"
      }
    },
    "parameters": {
      "cookieParam": {
        "name": "cookie",
        "in": "header",
        "description": "PHPSESSID to be passed as a header",
        "required": true,
        "schema": {
          "type": "string"
        }
      },
      "tokenParam": {
        "name": "CSRFToken",
        "in": "header",
        "description": "CSRFToken to be passed as a header",
        "required": true,
        "schema": {
          "type": "string"
        }
      }
    }
  },
  "paths": {
