      "get": {
        "summary": "Give an API endpoint as query parameter for GET operation",
        "parameters": [
          {
            "in": "query",
            "name": "a",
            "required": true,
            "schema": {
              "type": "string",
              "enum": [
                "form/1",
                "group",
                "indicator",
                "service",
                "workflow",
                "system/dbversion"
              ]
            },
            "allowReserved": true
          }
        ],
        "responses": {
          "200": {
            "description": "Successful operation"
          }
        }
      },
      "post": {
        "summary": "Give an API endpoint as query parameter for POST operation",
        "parameters": [
          {
            "$ref": "#/components/parameters/cookieParam"
          },
          {
            "$ref": "#/components/parameters/tokenParam"
          },
          {
            "in": "query",
            "name": "a",
            "required": true,
            "schema": {
              "type": "string"
            },
            "allowReserved": true
          }
        ],
        "responses": {
          "200": {
            "description": "Successful operation"
          }
        }
      },
      "delete": {
