{
    "openapi": "3.0.1",
    "info": {
        "title": "Lightdash API",
        "description": "API spec for Lightdash server",
        "contact": {
            "email": "support@lightdash.com"
        },
        "license": {
            "name": "MIT",
            "url": "https://github.com/lightdash/lightdash/blob/main/LICENSE"
        },
        "version": "1.0.0"
    },
    "externalDocs": {
        "description": "Lightdash documentation",
        "url": "https://docs.lightdash.com"
    },
    "servers": [
        {
            "url": "http://localhost:8080/api/v1"
        }
    ],
    "tags": [
        {
            "name": "user",
            "description": "User operation"
        }
    ],
    "paths": {
        "/login": {
            "post": {
                "summary": "Login with email and password",
                "tags": ["user"],
                "operationId": "loginWithPassword",
                "security": [],
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/Login"
                            }
                        }
                    },
                    "required": true
                },
                "responses": {
                    "200": {
                        "description": "Successfully logged in",
                        "headers": {
                            "Set-Cookie": {
                                "schema": {
                                    "type": "string"
                                }
                            }
                        },
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            }
        },
        "/user": {
            "get": {
                "summary": "Get user profile",
                "tags": ["user"],
                "operationId": "getUser",
                "responses": {
                    "200": {
                        "description": "Successfully found user",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "properties": {
                                                "results": {
                                                    "$ref": "#/components/schemas/LightdashUser"
                                                }
                                            },
                                            "required": ["results"]
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            },
            "post": {
                "summary": "Create user",
                "tags": ["user"],
                "operationId": "createUser",
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/CreateUser"
                            }
                        }
                    },
                    "required": true
                },
                "responses": {
                    "201": {
                        "description": "Successfully created user",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "properties": {
                                                "results": {
                                                    "$ref": "#/components/schemas/LightdashUser"
                                                }
                                            },
                                            "required": ["results"]
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            }
        },
        "/org": {
            "patch": {
                "summary": "Update organization",
                "tags": ["organization"],
                "operationId": "updateOrganization",
                "responses": {
                    "200": {
                        "$ref": "#/components/responses/SuccessResponse"
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            }
        },
        "/org/users": {
            "get": {
                "summary": "Get organization users",
                "tags": ["organization"],
                "operationId": "getOrganizationUsers",
                "responses": {
                    "200": {
                        "description": "Successfully get organization users",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "properties": {
                                                "results": {
                                                    "type": "array",
                                                    "items": {
                                                        "$ref": "#/components/schemas/OrganizationUser"
                                                    }
                                                }
                                            },
                                            "required": ["results"]
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            }
        },
        "/org/users/{userUuid}": {
            "patch": {
                "summary": "Update organization member",
                "tags": ["organization"],
                "operationId": "updateOrganizationMember",
                "parameters": [
                    {
                        "in": "path",
                        "name": "userUuid",
                        "schema": {
                            "type": "string",
                            "format": "uuid"
                        },
                        "required": true
                    }
                ],
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "type": "object",
                                "properties": {
                                    "role": {
                                        "type": "string"
                                    }
                                },
                                "additionalProperties": false
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "Successfully updated user",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "$ref": "#/components/schemas/OrganizationUser"
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            },
            "delete": {
                "summary": "Delete organization user",
                "tags": ["organization"],
                "operationId": "deleteOrganizationUser",
                "parameters": [
                    {
                        "in": "path",
                        "name": "userUuid",
                        "schema": {
                            "type": "string",
                            "format": "uuid"
                        },
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "$ref": "#/components/responses/SuccessResponse"
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            }
        },
        "/invite-links": {
            "delete": {
                "summary": "Delete all invite links",
                "tags": ["user"],
                "operationId": "deleteInviteLinks",
                "responses": {
                    "200": {
                        "$ref": "#/components/responses/SuccessResponse"
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            },
            "post": {
                "summary": "Create a new invite link",
                "tags": ["user"],
                "operationId": "createInviteLink",
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/CreateInviteLink"
                            }
                        }
                    },
                    "required": true
                },
                "responses": {
                    "201": {
                        "description": "Successfully created invite link",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "properties": {
                                                "results": {
                                                    "$ref": "#/components/schemas/InviteLink"
                                                }
                                            },
                                            "required": ["results"]
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            }
        },
        "/invite-links/{inviteLinkId}": {
            "get": {
                "summary": "Get an invite link",
                "tags": ["user"],
                "operationId": "getInviteLink",
                "parameters": [
                    {
                        "in": "path",
                        "name": "inviteLinkId",
                        "schema": {
                            "type": "string"
                        },
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Successfully got invite link",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "properties": {
                                                "results": {
                                                    "$ref": "#/components/schemas/InviteLink"
                                                }
                                            },
                                            "required": ["results"]
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            }
        },
        "/projects/{projectUuid}/sqlQuery": {
            "post": {
                "summary": "Run sql query",
                "description": "Run a sql query on project",
                "tags": ["project"],
                "operationId": "runSqlQuery",
                "parameters": [
                    {
                        "in": "path",
                        "name": "projectUuid",
                        "schema": {
                            "type": "string",
                            "format": "uuid"
                        },
                        "required": true
                    }
                ],
                "requestBody": {
                    "description": "Sql",
                    "required": true,
                    "content": {
                        "application/json": {
                            "schema": {
                                "type": "object",
                                "properties": {
                                    "sql": {
                                        "type": "string"
                                    }
                                },
                                "required": ["sql"]
                            }
                        }
                    }
                },
                "responses": {
                    "201": {
                        "description": "Successfully ran sql query",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "type": "object",
                                            "properties": {
                                                "rows": {
                                                    "type": "array",
                                                    "items": {
                                                        "additionalProperties": true
                                                    }
                                                }
                                            },
                                            "required": ["rows"]
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            }
        },
        "/projects/{projectUuid}/catalog": {
            "get": {
                "summary": "Get project catalog",
                "description": "Get project catalog with all valid tables",
                "tags": ["project"],
                "operationId": "getProjectCatalog",
                "parameters": [
                    {
                        "in": "path",
                        "name": "projectUuid",
                        "schema": {
                            "type": "string",
                            "format": "uuid"
                        },
                        "required": true
                    }
                ],
                "responses": {
                    "201": {
                        "description": "Successfully get project catalog",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "type": "object",
                                            "properties": {
                                                "results": {
                                                    "$ref": "#/components/schemas/ProjectCatalog"
                                                }
                                            },
                                            "required": ["results"]
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            }
        },
        "/projects/{projectUuid}/dashboards": {
            "post": {
                "summary": "Create dashboard",
                "description": "Create new dashboard on project",
                "tags": ["dashboard"],
                "operationId": "createDashboard",
                "parameters": [
                    {
                        "in": "path",
                        "name": "projectUuid",
                        "schema": {
                            "type": "string",
                            "format": "uuid"
                        },
                        "required": true
                    }
                ],
                "requestBody": {
                    "description": "New dashboard specification",
                    "required": true,
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/CreateDashboard"
                            }
                        }
                    }
                },
                "responses": {
                    "201": {
                        "description": "Successfully created dashboard",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "properties": {
                                                "results": {
                                                    "$ref": "#/components/schemas/Dashboard"
                                                }
                                            },
                                            "required": ["results"]
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            },
            "get": {
                "summary": "List dashboards",
                "description": "List all dashboards in a project",
                "tags": ["dashboard"],
                "operationId": "getDashboards",
                "parameters": [
                    {
                        "in": "path",
                        "name": "projectUuid",
                        "schema": {
                            "type": "string",
                            "format": "uuid"
                        },
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "description": "List of all dashboards in project",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "properties": {
                                                "results": {
                                                    "type": "array",
                                                    "items": {
                                                        "$ref": "#/components/schemas/DashboardListItem"
                                                    }
                                                }
                                            },
                                            "required": ["results"]
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            }
        },
        "/projects/{projectUuid}/tablesConfiguration": {
            "get": {
                "summary": "Get project tables configuration",
                "description": "Get project tables configuration",
                "tags": ["project"],
                "operationId": "getProjectTablesConfiguration",
                "parameters": [
                    {
                        "in": "path",
                        "name": "projectUuid",
                        "schema": {
                            "type": "string",
                            "format": "uuid"
                        },
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Project tables configuration",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "properties": {
                                                "results": {
                                                    "$ref": "#/components/schemas/ProjectTablesConfiguration"
                                                }
                                            },
                                            "required": ["results"]
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            },
            "patch": {
                "summary": "Update project tables configuration",
                "description": "Update project tables configuration",
                "tags": ["project"],
                "operationId": "updateProjectTablesConfiguration",
                "parameters": [
                    {
                        "in": "path",
                        "name": "projectUuid",
                        "schema": {
                            "type": "string",
                            "format": "uuid"
                        },
                        "required": true
                    }
                ],
                "requestBody": {
                    "description": "New tables configuration",
                    "required": true,
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/ProjectTablesConfiguration"
                            }
                        }
                    }
                },
                "responses": {
                    "201": {
                        "description": "Successfully created dashboard",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "properties": {
                                                "results": {
                                                    "$ref": "#/components/schemas/ProjectTablesConfiguration"
                                                }
                                            },
                                            "required": ["results"]
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            }
        },
        "/dashboards/{dashboardUuid}": {
            "get": {
                "summary": "Get dashboard",
                "description": "Get details for a single dashboard by dashboard_id",
                "tags": ["dashboard"],
                "operationId": "getDashboard",
                "parameters": [
                    {
                        "in": "path",
                        "name": "dashboardUuid",
                        "schema": {
                            "type": "string",
                            "format": "uuid"
                        },
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Details for a single dashboard",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "properties": {
                                                "results": {
                                                    "$ref": "#/components/schemas/Dashboard"
                                                }
                                            },
                                            "required": ["results"]
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            },
            "patch": {
                "summary": "Update dashboard",
                "description": "Update details for a single dashboard by dashboard_id",
                "tags": ["dashboard"],
                "operationId": "patchDashboard",
                "parameters": [
                    {
                        "in": "path",
                        "name": "dashboardUuid",
                        "schema": {
                            "type": "string",
                            "format": "uuid"
                        },
                        "required": true
                    }
                ],
                "requestBody": {
                    "description": "Dashboard fields",
                    "required": true,
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/UpdateDashboard"
                            }
                        }
                    }
                },
                "responses": {
                    "200": {
                        "description": "Details for a single dashboard",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "properties": {
                                                "results": {
                                                    "$ref": "#/components/schemas/Dashboard"
                                                }
                                            },
                                            "required": ["results"]
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            },
            "delete": {
                "summary": "Delete dashboard",
                "description": "Delete a single dashboard by dashboard_id",
                "tags": ["dashboard"],
                "operationId": "deleteDashboard",
                "parameters": [
                    {
                        "in": "path",
                        "name": "dashboardUuid",
                        "schema": {
                            "type": "string",
                            "format": "uuid"
                        },
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "$ref": "#/components/responses/SuccessResponse"
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            }
        },
        "/saved/{savedChartUuid}/availableFilters": {
            "parameters": [
                {
                    "schema": {
                        "type": "string"
                    },
                    "name": "savedChartUuid",
                    "in": "path",
                    "required": true
                }
            ],
            "get": {
                "summary": "Get available filters for a saved chart",
                "tags": ["saved-chart"],
                "operationId": "getAvailableChartFilters",
                "responses": {
                    "200": {
                        "description": "Available filters for a saved chart",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "properties": {
                                                "results": {
                                                    "$ref": "#/components/schemas/Fields"
                                                }
                                            }
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            }
        },
        "/saved/{savedChartUuid}": {
            "parameters": [
                {
                    "schema": {
                        "type": "string"
                    },
                    "name": "savedChartUuid",
                    "in": "path",
                    "required": true
                }
            ],
            "get": {
                "summary": "Get a saved chart",
                "tags": ["saved"],
                "operationId": "getSavedChart",
                "parameters": [
                    {
                        "in": "path",
                        "name": "savedChartUuid",
                        "schema": {
                            "type": "string",
                            "format": "uuid"
                        },
                        "required": true
                    }
                ],
                "responses": {
                    "200": {
                        "description": "Details for a saved chart",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "allOf": [
                                        {
                                            "$ref": "#/components/schemas/Success"
                                        },
                                        {
                                            "properties": {
                                                "results": {
                                                    "$ref": "#/components/schemas/SavedChart"
                                                }
                                            },
                                            "required": ["results"]
                                        }
                                    ]
                                }
                            }
                        }
                    },
                    "default": {
                        "$ref": "#/components/responses/ErrorResponse"
                    }
                }
            }
        }
    },
    "security": [
        {
            "cookieAuth": []
        }
    ],
    "components": {
        "securitySchemes": {
            "cookieAuth": {
                "type": "apiKey",
                "in": "cookie",
                "name": "connect.sid"
            }
        },
        "schemas": {
            "Login": {
                "type": "object",
                "properties": {
                    "email": {
                        "type": "string"
                    },
                    "password": {
                        "type": "string"
                    }
                },
                "required": ["email", "password"]
            },
            "Error": {
                "type": "object",
                "required": ["status", "error"],
                "properties": {
                    "status": {
                        "type": "string",
                        "enum": ["error"]
                    },
                    "error": {
                        "type": "object",
                        "required": ["name", "statusCode", "message"],
                        "properties": {
                            "name": {
                                "type": "string"
                            },
                            "statusCode": {
                                "type": "number"
                            },
                            "message": {
                                "type": "string"
                            },
                            "data": {
                                "type": "object"
                            }
                        }
                    }
                }
            },
            "Success": {
                "type": "object",
                "properties": {
                    "status": {
                        "type": "string",
                        "enum": ["ok"]
                    }
                }
            },
            "LightdashUser": {
                "type": "object",
                "additionalProperties": false,
                "properties": {
                    "userUuid": {
                        "type": "string"
                    },
                    "firstName": {
                        "type": "string"
                    },
                    "lastName": {
                        "type": "string"
                    },
                    "email": {
                        "type": "string"
                    },
                    "organizationUuid": {
                        "type": "string"
                    },
                    "organizationName": {
                        "type": "string"
                    },
                    "isTrackingAnonymized": {
                        "type": "boolean"
                    },
                    "role": {
                        "type": "string"
                    }
                },
                "required": [
                    "userUuid",
                    "firstName",
                    "lastName",
                    "isTrackingAnonymized"
                ]
            },
            "CreateUser": {
                "type": "object",
                "properties": {
                    "inviteCode": {
                        "type": "string"
                    },
                    "firstName": {
                        "type": "string"
                    },
                    "lastName": {
                        "type": "string"
                    },
                    "email": {
                        "type": "string"
                    },
                    "password": {
                        "type": "string"
                    },
                    "isMarketingOptedIn": {
                        "type": "boolean"
                    },
                    "isTrackingAnonymized": {
                        "type": "boolean"
                    }
                },
                "required": [
                    "inviteCode",
                    "firstName",
                    "lastName",
                    "email",
                    "password",
                    "isMarketingOptedIn",
