                      }
                    },
                    "statusCode": {
                      "type": "integer",
                      "format": "int32"
                    },
                    "mart": {
                      "type": "string",
                      "description": "Marketplace name. Example: Walmart-US",
                      "xml": {
                        "namespace": "http://walmart.com/"
