{
  "openapi": "3.0.1",
  "info": {
    "title": "Price Management",
    "description": "The price is a fundamental building block for your listing on Walmart.com. You can use the price management APIs to set up and manage the price for a given item"
  },
  "servers": [
    {
      "url": "https://marketplace.walmartapis.com",
      "description": "Production URL"
    },
    {
      "url": "https://sandbox.walmartapis.com",
      "description": "Sandbox URL"
    }
  ],
  "security": [
    {
      "basicScheme": []
    }
  ],
  "paths": {
    "/v3/price": {
      "put": {
        "tags": [
          "Prices"
        ],
        "summary": "Update a price",
        "description": "Updates the regular price for a given item.",
        "operationId": "updatePrice",
        "parameters": [
          {
            "in": "header",
            "name": "Authorization",
            "description": "Basic authorization header. Base 64 encodes the Client ID and Client Secret retrieved in step two of the integration steps.",
            "required": true,
            "schema": {
              "type": "string"
            },
            "example": "Basic YzcyOTFjNmItNzI5MC00...."
          },
          {
            "in": "header",
            "name": "WM_SEC.ACCESS_TOKEN",
            "description": "The access token retrieved in the Token API call",
            "required": true,
            "schema": {
              "type": "string"
            },
            "example": "eyJraWQiOiIzZjVhYTFmNS1hYWE5LTQzM....."
          },
          {
            "in": "header",
            "name": "WM_CONSUMER.CHANNEL.TYPE",
            "description": "A unique ID to track the consumer request by channel. Use the Consumer Channel Type received during onboarding",
            "required": false,
            "schema": {
              "type": "string"
            }
          },
          {
            "in": "header",
            "name": "WM_QOS.CORRELATION_ID",
            "description": "A unique ID which identifies each API call and used to track and debug issues; use a random generated GUID for this ID",
            "required": true,
            "schema": {
              "type": "string"
            },
            "example": "b3261d2d-028a-4ef7-8602-633c23200af6"
          },
          {
            "in": "header",
            "name": "WM_SVC.NAME",
            "description": "Walmart Service Name",
            "required": true,
            "schema": {
              "type": "string"
            },
            "example": "Walmart Service Name"
          }
        ],
        "requestBody": {
          "description": "The request body consists of a Feed file attached to the request.",
          "content": {
            "application/json": {
              "schema": {
                "required": [
                  "pricing",
                  "sku"
                ],
                "type": "object",
                "properties": {
                  "offerId": {
                    "type": "string",
                    "description": "This is applicable only for promotions"
                  },
                  "sku": {
                    "type": "string"
                  },
                  "replaceAll": {
                    "type": "string",
                    "description": "This is applicable only for promotions",
                    "enum": [
                      "true",
                      "false"
                    ]
                  },
                  "pricing": {
                    "type": "array",
                    "items": {
                      "required": [
                        "currentPrice",
                        "currentPriceType"
                      ],
                      "type": "object",
                      "properties": {
                        "effectiveDate": {
                          "type": "string",
                          "description": "This is applicable only for promotions",
                          "format": "date-time"
                        },
                        "expirationDate": {
                          "type": "string",
                          "description": "This is applicable only for promotions",
                          "format": "date-time"
                        },
                        "promoId": {
                          "type": "string",
                          "description": "This is applicable only for promotions"
                        },
                        "processMode": {
                          "type": "string",
                          "description": "This is applicable only for promotions",
                          "enum": [
                            "UPSERT",
                            "DELETE"
                          ]
                        },
                        "currentPriceType": {
                          "type": "string",
                          "description": "This is applicable only for both promotions and price",
                          "enum": [
                            "BASE",
                            "REDUCED",
                            "CLEARANCE"
                          ]
                        },
                        "currentPrice": {
                          "type": "object",
                          "properties": {
                            "currency": {
                              "type": "string",
                              "enum": [
                                "USD",
                                "CAD"
                              ]
                            },
                            "amount": {
                              "type": "number",
                              "format": "double"
                            }
                          }
                        },
                        "comparisonPriceType": {
                          "type": "string",
                          "description": "This is applicable only for promotions",
                          "enum": [
                            "BASE"
                          ]
                        },
                        "comparisonPrice": {
                          "type": "object",
                          "properties": {
                            "currency": {
                              "type": "string",
                              "enum": [
                                "USD",
                                "CAD"
                              ]
                            },
                            "amount": {
                              "type": "number",
                              "format": "double"
                            }
                          },
                          "description": "This is applicable only for promotions"
                        },
