    },
    "contact": {
      "name": "Krishna",
      "email": "kvasudevan@infoblox.com"
    }
  },
  "paths": {
    "/wapi/v2.11/discovery": {},
    "/wapi/v2.11/discovery/{discovery_reference}": {
      "post": {
        "description": "Function calls",
        "tags": [
          "discovery"
        ],
        "parameters": [
          {
            "name": "discovery_reference",
            "in": "path",
            "required": true,
            "description": "Enter the reference for discovery",
            "schema": {
              "type": "string",
              "example": "resourceID:resourceName"
            }
          },
          {
            "name": "_function",
            "in": "query",
            "required": true,
            "description": "Select the function",
            "schema": {
              "type": "string",
              "enum": [
                "clear_network_port_assignment",
                "control_switch_port",
                "discovery_data_conversion",
                "get_device_support_info",
                "get_job_devices",
                "get_job_process_details",
                "import_device_support_bundle",
                "modify_sdn_assignment",
                "modify_vrf_assignment",
                "provision_network_dhcp_relay",
                "provision_network_port"
              ]
            }
          }
        ],
        "requestBody": {
          "description": "Enter the request body here",
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {}
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "OK",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "properties": {
                    "clear_network_port_assignment": {
                      "type": "string",
                      "example": {
                        "network_deprovision_info": "discovery:networkdeprovisioninfo"
                      }
                    },
                    "control_switch_port": {
                      "type": "string",
                      "example": {
                        "port_configs": "discovery:port:control:info"
                      }
                    },
                    "discovery_data_conversion": {
                      "type": "string",
                      "example": {
                        "type": [
                          "HOST_RECORD",
                          "FIXED_ADDRESS",
                          "IPV6_FIXED_ADDRESS",
                          "A_AND_PTR_RECORD",
                          "AAAA_AND_PTR_RECORD"
                        ],
                        "addresses": "ipv4address",
                        "attributes": "discovery:conversion_attributes",
                        "extensible_attributes": "string"
                      }
                    },
                    "get_device_support_info": {
                      "type": "string",
                      "example": {
                        "device": "string"
                      }
                    },
                    "get_job_devices": {
                      "type": "string",
                      "example": {
                        "task": "string"
                      }
                    },
                    "get_job_process_details": {
                      "type": "string",
                      "example": {
                        "device": "string",
                        "task": "string",
                        "start_line": "uint"
                      }
                    },
                    "import_device_support_bundle": {
                      "type": "string",
                      "example": {
                        "token": "string",
                        "filename": "string"
                      }
                    },
                    "modify_sdn_assignment": {
                      "type": "string",
                      "example": {
                        "mode": [
                          "ASSIGN",
                          "UNASSIGN"
                        ],
                        "network_view": "string",
                        "sdn_networks": "discovery:sdnnetwork"
                      }
                    },
                    "modify_vrf_assignment": {
                      "type": "string",
                      "example": {
                        "mode": [
                          "ASSIGN",
                          "UNASSIGN"
                        ],
                        "network_view": "string",
                        "vrfs": "discovery:vrf"
                      }
                    },
                    "provision_network_dhcp_relay": {
                      "type": "string",
                      "example": {
                        "enable_dhcp_relay": "bool",
                        "interfaces": "discovery:deviceinterface",
                        "parent": "string"
                      }
                    },
                    "provision_network_port": {
                      "type": "string",
                      "example": {
                        "network": "string",
                        "network_view": "string",
                        "router_ip": "string",
                        "device": "string",
                        "enable_dhcp_relay": "bool",
                        "interface": "string",
                        "vlan_info": "discovery:vlaninfo"
                      }
                    }
                  }
                }
              }
            }
          }
        },
        "security": [
          {
            "basicAuth": []
          }
        ]
      }
    },
