{
  "openapi":"3.0.0",
  "info":{
    "description":"",
    "version":"1.0",
    "title":"Engage Digital API",
    "termsOfService":"https://developer.ringcentral.com"
  },
  "tags":[
    {
      "name":"Sources"
    }
  ],
  "paths":{
    "/content_sources":{
      "get":{
        "tags":[
          "Sources"
        ],
        "summary":"Getting all sources",
        "operationId":"getAllSources",
        "description":"This method renders sources ordered by creation date (ascending).",
        "parameters":[
          {
            "name":"offset",
            "in":"query",
            "description":"The record index to start. Default value is 0.",
            "required":false,
            "schema":{
              "type":"integer",
              "format":"int32"
            }
          },
          {
            "name":"limit",
            "in":"query",
            "description":"The max number of records to return. Default value is 30, max value is 150.",
            "required":false,
            "schema":{
              "type":"integer",
              "format":"int32"
            }
          }
        ],
        "responses":{
          "200":{
            "description":"Success",
            "content":{
              "application/json":{
                "schema":{
                  "$ref":"#/components/schemas/GetAllSourcesResponse"
                }
              }
            }
          }
        }
      }
    },
    "/content_sources/{sourceId}":{
      "get":{
        "tags":[
          "Sources"
        ],
        "summary":"Getting a source from its id",
        "operationId":"getSource",
        "description":"This method renders a source from given id.",
        "parameters":[
          {
            "name":"sourceId",
            "in":"path",
            "required":true,
            "schema":{
              "type":"string"
            }
          }
        ],
        "responses":{
          "200":{
            "description":"Success",
            "content":{
              "application/json":{
                "schema":{
                  "$ref":"#/components/schemas/Source"
                }
              }
            }
          }
        }
      },
      "put":{
        "tags":[
          "Sources"
        ],
        "summary":"Updating a source",
        "operationId":"updateSource",
        "description":"This method updates an existing source from given attributes and renders it in case of success.",
        "parameters":[
          {
            "name":"sourceId",
            "in":"path",
            "required":true,
            "schema":{
              "type":"string"
            }
          },
          {
            "name":"name",
            "description":"Source name",
            "in":"query",
            "required":false,
            "schema":{
              "type":"string"
            }
          },
          {
            "name":"active",
            "description":"Activate/deactivate the source (Boolean)",
            "in":"query",
            "required":false,
            "schema":{
              "type":"boolean"
            }
          },
          {
            "name":"channel_id",
            "description":"Channel",
            "in":"query",
            "required":false,
            "schema":{
              "type":"string"
            }
          },
          {
            "name":"color",
            "description":"Color of the icon (see S​ ource colors​) (Integer)",
            "in":"query",
            "required":false,
            "schema":{
              "type":"integer"
            }
          },
          {
            "name":"sla_response",
            "description":"Response time (seconds)",
            "in":"query",
            "required":false,
            "schema":{
              "type":"integer"
            }
          },
          {
            "name":"sla_expired_strategy",
            "description":"SLA expired strategy (\"max\", \"half\" or \"base\")",
            "in":"query",
            "required":false,
            "schema":{
              "type":"string",
              "enum":[
                "max",
                "half",
                "base"
              ]
            }
          },
          {
            "name":"intervention_messages_boost",
            "description":"Priority boost of messages with intervention (Integer)",
            "in":"query",
            "required":false,
            "schema":{
              "type":"integer"
            }
          },
          {
            "name":"transferred_tasks_boost",
            "description":"Priority boost of transferred tasks (Integer)",
            "in":"query",
            "required":false,
            "schema":{
              "type":"integer"
            }
          },
          {
            "name":"hidden_from_stats",
            "description":"Hide from statistics (Boolean)",
            "in":"query",
            "required":false,
            "schema":{
              "type":"boolean"
            }
          },
          {
            "name":"default_category_ids[]",
            "description":"Default categories",
            "in":"query",
            "required":false,
            "schema":{
              "type":"array",
              "items":{
                "type":"string"
              }
            }
          },
          {
            "name":"user_thread_default_category_ids[]",
            "description":"Default categories (agent messages)",
            "in":"query",
            "required":false,
            "schema":{
              "type":"array",
              "items":{
                "type":"string"
              }
            }
          },
          {
            "name":"default_content_language",
            "description":"Default content language",
            "in":"query",
            "required":false,
            "schema":{
              "type":"string"
            }
          },
          {
            "name":"auto_detect_content_language",
            "description":"Auto-detect content language (Boolean)",
            "in":"query",
            "required":false,
            "schema":{
              "type":"boolean"
            }
          },
          {
            "name":"content_archiving",
            "description":"Automatic archiving of old contents (Boolean)",
            "in":"query",
            "required":false,
            "schema":{
              "type":"boolean"
            }
          },
          {
            "name":"content_archiving_period",
            "description":"Archive contents older than (seconds)",
            "in":"query",
            "required":false,
            "schema":{
              "type":"integer"
            }
          }
        ],
        "responses":{
          "200":{
            "description":"Success",
            "content":{
              "application/json":{
                "schema":{
                  "$ref":"#/components/schemas/Source"
                }
              }
            }
          }
        }
      }
    }
  },
  "servers":[
    {
      "url":"https://DOMAIN.api.engagement.dimelo.com/1.0"
    }
  ],
  "components":{
    "schemas":{
      "GetAllSourcesResponse":{
        "properties":{
          "count":{
            "type":"integer",
            "format":"int32"
          },
          "limit":{
            "type":"integer",
            "format":"int32"
          },
          "offset":{
            "type":"integer",
            "format":"int32"
          },
          "records":{
            "type":"array",
            "items":{
              "$ref":"#/components/schemas/Source"
            }
          }
        }
      },
      "Source":{
        "properties":{
          "id":{
            "type":"string"
          },
          "created_at":{
            "type":"string",
            "format":"date-time"
          },
          "updated_at":{
            "type":"string",
            "format":"date-time"
          },
          "active":{
            "type":"boolean",
            "description":"Activate/deactivate the source"
          },
          "community_id":{
            "type":"string"
          },
          "channel_id":{
            "type":"string"
          },
          "name":{
            "type":"string",
            "description":"Source name"
          },
          "status":{
            "type":"string"
          },
          "color":{
            "type":"integer",
            "format":"int32",
            "description":"Color of the icon: Default: 0 Blue: 1 Green: 2 Turquoise: 3 Purple: 4 Yellow: 5 Orange: 6 Red: 7 Asphalt: 8 Grey: 9"
          },
          "sla_response":{
            "type":"integer",
            "format":"int32",
            "description":"Response time (seconds)"
          },
          "sla_expired_strategy":{
            "type":"string",
            "enum":[
              "max",
              "half",
              "base"
            ],
            "description":"SLA expired strategy (\"max\", \"half\" or \"base\")"
          },
          "intervention_messages_boost":{
            "type":"integer",
            "format":"int32",
            "description":"Priority boost of messages with intervention"
          },
          "transferred_tasks_boost":{
            "type":"integer",
            "format":"int32",
            "description":"Priority boost of transferred tasks"
          },
          "hidden_from_stats":{
            "type":"boolean",
            "description":"Hide from statistics"
          },
          "default_category_ids":{
            "type":"array",
            "items":{
              "type":"string"
            },
            "description":"Default categories"
          },
          "user_thread_default_category_ids":{
            "type":"array",
            "items":{
              "type":"string"
            },
            "description":"Default categories (agent messages)"
          },
          "default_content_language":{
            "type":"string",
            "description":"Default content language"
          },
          "auto_detect_content_language":{
            "type":"boolean",
            "description":"Auto-detect content language (Boolean)"
          },
          "time_sheet_ids":{
            "type":"array",
            "items":{
              "type":"string"
            }
          },
          "content_archiving":{
            "type":"boolean",
            "description":"Automatic archiving of old contents (Boolean)"
          },
          "content_archiving_period":{
            "type":"integer",
            "format":"int64",
            "description":"Archive contents older than (seconds)"
          },
          "content_languages":{
            "type":"array",
            "items":{
              "type":"string"
            }
          },
          "type":{
            "type":"string"
          },
          "error_message":{
            "type":"string"
          }
        }
      }
    }
  }
}
