{
	"openapi": "3.0.1",
	"servers": [
		{
			"description": "API para a entidade Curso, perfil acadêmico (Course) para produtos TOTVS",
			"url": "{{host}}/api/educational/v1",
			"variables": {
				"serverUrl": {
					"default": "localhost"
				},
				"serverHttpPort": {
					"default": "8051"
				}
			}
		}
	],
	"info": {
		"description": "API para a entidade curso",
		"version": "1.000",
		"title": "Cursos educacionais",
		"contact": {
			"name": "T-Talk",
			"url": "API.Totvs.com.br",
			"email": "comiteintegracao@totvs.com.br"
		},
		"x-totvs": {
			"messageDocumentation": {
				"name": "Course",
				"description": "Curso",
				"segment": "Educacional"
			},
			"productInformation": [
				{
					"product": "RM",
					"contact": "TribeEdu.Squad.EduPortalMobile@totvs.com.br",
					"description": "Entidade de curso, perfil acadêmico"
				}
			]
		}
	},
	"paths": {
		"/Academics/{AcademicInternalID}/Courses ": {
			"get": {
				"tags": [
					"Course"
				],
				"summary": "Retorna todos os cursos cadastrados no sistema",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo está disponível com todos os parâmetros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"description": "Retorna todos os cursos cadastrados no sistema",
				"operationId": "getAll",
				"parameters": [
					{
						"$ref": "#/components/parameters/AcademicInternalID"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Order"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Page"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/PageSize"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Fields"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Course_1_000.json#/definitions/PagedCourse"
								}
							}
						}
					},
					"400": {
						"description": "Erro ao processar solicitação",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"post": {
				"tags": [
					"Course"
				],
				"summary": "Inclui o curso passado na requisição",
				"description": "Inclui o Course passado na requisição",
				"operationId": "postCourse",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponível com todos os parâmetros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"parameters": [
					{
						"$ref": "#/components/parameters/AcademicInternalID"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"requestBody": {
					"content": {
						"application/json": {
							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Course_1_000.json#/definitions/Course"
							}
						}
					},
					"description": "Curso para ser incluído"
				},
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Course_1_000.json#/definitions/Course"
								}
							}
						}
					},
					"400": {
						"description": "erro no momento da inclusão",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		},
		"/Academics/{AcademicInternalID}/Courses/{InternalId}": {
			"get": {
				"tags": [
					"Course"
				],
				"summary": "Retorna detalhes do curso",
				"description": "Retorna o curso",
				"operationId": "getId",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponível com todos os parâmetros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"parameters": [
					{
						"$ref": "#/components/parameters/AcademicInternalID"
					},
					{
						"$ref": "#/components/parameters/InternalId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Course_1_000.json#/definitions/Course"
								}
							}
						}
					},
					"404": {
						"description": "Usuário não localizado na base",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"put": {
				"tags": [
					"Course"
				],
				"summary": "Altera o curso passado na requisição",
				"description": "Altera o curso passado na requisição",
				"operationId": "putCourseId",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponível com todos os parâmetros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"parameters": [
					{
						"$ref": "#/components/parameters/AcademicInternalID"
					},
					{
						"$ref": "#/components/parameters/InternalId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"requestBody": {
					"content": {
						"application/json": {
							"schema": {
								"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Course_1_000.json#/definitions/Course"
							}
						}
					},
					"description": "Curso para ser editado"
				},
				"responses": {
					"200": {
						"description": "Operação realizada com sucesso",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/schemas/Course_1_000.json#/definitions/Course"
								}
							}
						}
					},
					"400": {
						"description": "Erro no momento da alteração",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					},
					"404": {
						"description": "Usuário não localizado na base",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			},
			"delete": {
				"tags": [
					"Course"
				],
				"summary": "Exclusão de curso da base",
				"description": "Exclusão de curso da base",
				"operationId": "DeleteCourse",
				"x-totvs": {
					"productInformation": [
						{
							"product": "RM",
							"available": true,
							"note": "Este verbo esta disponível com todos os parâmetros",
							"minimalVersion": "12.1.23"
						}
					]
				},
				"parameters": [
					{
						"$ref": "#/components/parameters/AcademicInternalID"
					},
					{
						"$ref": "#/components/parameters/InternalId"
					},
					{
						"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/parameters/Authorization"
					}
				],
				"responses": {
					"204": {
						"description": "Curso removido com sucesso"
					},
					"404": {
						"description": "Curso não encontrado",
						"content": {
							"application/json": {
								"schema": {
									"$ref": "https://raw.githubusercontent.com/totvs/ttalk-standard-message/master/jsonschema/apis/types/totvsApiTypesBase.json#/definitions/ErrorModel"
								}
							}
						}
					}
				}
			}
		}
	},
	"components": {
		"parameters": {
			"AcademicInternalID": {
				"name": "AcademicInternalID",
				"in": "path",
				"required": true,
				"description": "InternalId da matriz curricular",
				"schema": {
					"type": "string"
				},
				"x-totvs": [
					{
						"product": "RM",
						"note": "CODCOLIGADA|CODFILIAL|CODTIPOCURSO",
						"required": true,
						"available": true
					}
				]
			},
			"InternalId": {
				"name": "InternalId",
				"in": "path",
				"required": true,
				"description": "InternalId do curso",
				"schema": {
					"type": "string"
				},
				"x-totvs": [
					{
						"product": "RM",
						"note": "SCURSO.CODCOLIGADA|SCURSO.CODCURSO",
						"required": false,
						"available": true
					}
				]
			}
		},
		"schemas": {}
	}
}
