{
  "openapi": "3.0.3",
  "info": {
    "title": "PagerDuty Jira Server Integration API",
    "description": "This is API documentation for PagerDuty's integration with a Jira Server and is compatible with the Atlassian marketplace add-on verison 4.0.0+. Additional integration documentation can be found here: https://support.pagerduty.com/docs/jira-server",
    "contact": {
      "name": "PagerDuty Support",
      "url": "http://www.pagerduty.com/support",
      "email": "support@pagerduty.com"
    },
    "version": "1.0.0"
  },
  "servers": [
    {
      "url": "https://app.pagerduty.com/integration-jira-service"
    }
  ],
  "security": [
    {
      "BearerToken": []
    }
  ],
  "tags": [
    {
      "name": "Rules",
      "description": "Rules are used to configure bi-directional incident/issue synchronization."
    }
  ],
  "components": {
    "parameters": {
      "total": {
        "name": "total",
        "in": "query",
        "description": "Returns the total number of records matched by the query if enabled.",
        "schema": {
          "type": "boolean",
          "default": false
        }
      },
      "limit": {
        "name": "limit",
        "in": "query",
        "description": "Set the maximum number of results matched by the query.",
        "schema": {
          "type": "integer",
          "minimum": 1,
          "maximum": 100,
          "default": 25
        }
      },
      "offset": {
        "name": "offset",
        "in": "query",
        "description": "The number of items to skip before starting to collect the result set.",
        "schema": {
          "type": "integer",
          "minimum": 0,
          "default": 0
        }
      },
      "id": {
        "name": "id",
        "description": "The ID of the resource.",
        "in": "path",
        "required": true,
        "schema": {
          "type": "string"
        }
      },
      "x-pagerduty-jira-signature": {
        "name": "x-pagerduty-jira-signature",
        "in": "header",
        "description": "Jira Authorization Signature\n\nInspect network requests that are done by the PagerDuty Addon settings page\nand look for a request, like `GET /accounts_mapping`.\n\nIn the request headers you will find `x-pagerduty-jira-signature`.\nUse its value as it is to make your requests.\n",
        "required": true,
        "schema": {
          "type": "string"
        }
      }
    },
    "responses": {
      "ArgumentError": {
        "description": "Caller provided invalid arguments. Please review the response for error details. Retrying with the same arguments will *not* work.",
        "content": {
          "application/json": {
            "schema": {
              "$ref": "#/components/schemas/Error"
            },
            "examples": {
              "InvalidInputProvided": {
                "summary": "Invalid input provided",
                "value": {
                  "error": {
                    "message": "Invalid Input Provided"
                  }
                }
              },
              "ValidationError": {
                "summary": "Validation error",
                "value": {
                  "error": {
                    "errors": [
                      "Name can't be blank"
                    ],
                    "message": "Invalid Input Provided"
                  }
                }
              },
              "MalformedJson": {
                "summary": "Malformed json",
                "value": {
                  "error": {
                    "message": "Malformed json"
                  }
                }
              }
            }
          }
        }
      },
      "Forbidden": {
        "description": "Caller is not authorized to view the requested resource.\nWhile your authentication is valid, the authenticated user or token does not have permission to perform this action.\n",
        "content": {
          "application/json": {
            "schema": {
              "$ref": "#/components/schemas/Error"
            },
            "examples": {
              "AuthorizationError": {
                "summary": "Authorization Error",
                "value": {
                  "error": {
                    "message": "Authorization error"
                  }
                }
              }
            }
          }
        }
      },
      "NotFound": {
        "description": "The requested resource was not found.",
        "content": {
          "application/json": {
            "schema": {
              "$ref": "#/components/schemas/Error"
            },
            "examples": {
              "NotFound": {
                "summary": "Not Found",
                "value": {
                  "error": {
                    "message": "Record not found"
                  }
                }
              }
            }
          }
        }
      },
      "UnprocessableEntity": {
        "description": "Unprocessable Entity. Some arguments failed validation checks.",
        "content": {
          "application/json": {
            "schema": {
              "$ref": "#/components/schemas/Error"
            },
            "examples": {
              "UnprocessableEntity": {
                "summary": "Unprocessable Entity",
                "value": {
                  "error": {
                    "message": "Unprocessable Entity"
                  }
                }
              }
            }
          }
        }
      }
    },
    "schemas": {
      "Rule": {
        "type": "object",
        "properties": {
          "name": {
            "description": "A name of the rule",
            "type": "string",
            "maxLength": 128,
            "example": "Sample Rule"
          },
          "enabled": {
            "description": "Indicates if the rule is enabled",
            "type": "boolean",
            "default": false
          },
          "config": {
            "description": "Contatins an object which describes a rule",
            "type": "object",
            "properties": {
              "sync_comments": {
                "type": "boolean",
                "description": "Indicates if comments should be synchronized",
                "default": false
              },
              "services": {
                "description": "An array of the PagerDuty services",
                "type": "array",
                "minItems": 1,
                "items": {
                  "$ref": "#/components/schemas/ServiceReference"
                }
              },
              "projects": {
                "description": "An array of Jira projects",
                "type": "array",
                "minItems": 1,
                "items": {
                  "$ref": "#/components/schemas/Project"
                }
              },
              "issue_types": {
                "description": "An array of Jira issue types",
                "type": "array",
                "minItems": 1,
                "items": {
                  "$ref": "#/components/schemas/IssueType"
                }
              },
              "autocreate_issues": {
                "description": "Indicates if automatic Jira issue creation is enabled",
                "type": "boolean",
                "default": false
              },
              "autocreate_issues_config": {
                "description": "An array of rules that are applied when automatic Jira issue creation is enabled",
                "type": "array",
                "items": {
                  "$ref": "#/components/schemas/RuleAutocreateIssueConfigurationItem"
                }
              },
              "autocreate_incidents": {
                "description": "Indicates if automatic PagerDuty incident creation is enabled",
                "type": "boolean",
                "default": false
              },
              "autocreate_incidents_config": {
                "description": "An array of rules that are applied when automatic PagerDuty incident creation is enabled",
                "type": "array",
                "items": {
                  "$ref": "#/components/schemas/RuleAutocreateIncidentConfigurationItem"
                }
              },
              "react_on_incident_event": {
                "description": "Indicates if reaction on PagerDuty incident updates is enabled",
                "type": "boolean",
                "default": false
              },
              "react_on_incident_event_config": {
                "description": "An array of rules that are applied to the Jira issue when reaction on PagerDuty incident updates is enabled",
                "type": "array",
                "items": {
                  "$ref": "#/components/schemas/RuleReactOnIncidentEventConfigurationItem"
                }
              },
              "react_on_issue_status_change": {
                "description": "Indicates if reaction on Jira issue status change is enabled",
                "type": "boolean",
                "default": false
              },
              "react_on_issue_status_change_config": {
                "description": "An array of rules that are applied to the PagerDuty incident when reaction on Jira issue status change is enabled",
                "type": "array",
                "items": {
                  "$ref": "#/components/schemas/RuleReactOnIssueStatusChangeConfigurationItem"
                }
              }
            }
          },
          "created_at": {
            "description": "The date/time rule was created",
            "type": "string",
            "format": "date-time",
            "readOnly": true,
            "example": "2020-06-02T12:42:37Z"
          }
        },
        "required": [
          "name",
          "enabled",
          "config",
          "created_at"
        ]
      },
      "RuleAutocreateIncidentConfigurationItem": {
        "description": "Configuration item of the rule for automatical PagerDuty incidents creation from the Jira issues",
        "type": "object",
        "required": [
          "jql"
        ],
        "properties": {
          "jql": {
            "description": "Rule will be applied, when issue matches the JQL",
            "type": "string"
          },
          "on_create": {
            "description": "Apply the rule on issue creation",
            "type": "boolean",
            "default": false
          },
          "on_update": {
            "description": "Apply the rule on issue update",
            "type": "boolean",
            "default": false
          },
          "actions": {
            "$ref": "#/components/schemas/RuleActions"
          },
          "actions_params": {
            "$ref": "#/components/schemas/RuleActionsParams"
          },
          "target_service": {
            "$ref": "#/components/schemas/ServiceReference"
          }
        }
      },
      "RuleAutocreateIssueConfigurationItem": {
        "description": "Configuration item of the rule for automatical/manual Jira issues creation from PagerDuty incidents",
        "type": "object",
        "properties": {
          "condition": {
            "type": "object",
            "properties": {
              "pd_events": {
                "$ref": "#/components/schemas/RuleConditionPdCreateIssueEvents"
              },
              "pd_priorities": {
                "$ref": "#/components/schemas/RuleConditionPdPriorities"
              },
              "pd_urgency": {
                "$ref": "#/components/schemas/RuleConditionPdUrgency"
              }
            }
          },
          "actions": {
            "$ref": "#/components/schemas/RuleActions"
          },
          "actions_params": {
            "$ref": "#/components/schemas/RuleActionsParams"
          },
          "target_project": {
            "$ref": "#/components/schemas/Project"
          },
          "target_issue_type": {
            "$ref": "#/components/schemas/IssueType"
          }
        }
      },
      "RuleReactOnIncidentEventConfigurationItem": {
        "description": "Configuration item of the rule for updating an issue that will only be applied when the incident is already linked and conditions are met",
        "type": "object",
        "properties": {
          "condition": {
            "type": "object",
            "properties": {
              "pd_events": {
                "$ref": "#/components/schemas/RuleConditionPdUpdateIssueEvents"
              },
              "pd_priorities": {
                "$ref": "#/components/schemas/RuleConditionPdPriorities"
              },
              "pd_urgency": {
                "$ref": "#/components/schemas/RuleConditionPdUrgency"
              }
            }
          },
          "actions": {
            "$ref": "#/components/schemas/RuleActions"
          },
          "actions_params": {
            "$ref": "#/components/schemas/RuleActionsParams"
          },
          "target_project": {
            "$ref": "#/components/schemas/Project"
          },
          "target_issue_type": {
            "description": "Issue Type, which should set on the issue",
            "allOf": [
              {
                "$ref": "#/components/schemas/IssueType"
              }
            ]
          }
        },
        "title": "",
        "x-examples": {
          "example-1": {}
        }
      },
      "RuleReactOnIssueStatusChangeConfigurationItem": {
        "description": "Configuration item of the rule for updating an incident that will only be applied when there is a change in status",
        "type": "object",
        "properties": {
          "jira_statuses": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/JiraStatus"
            }
          },
          "actions": {
            "$ref": "#/components/schemas/RuleActions"
          },
          "actions_params": {
            "$ref": "#/components/schemas/RuleActionsParams"
          }
        }
      },
      "RuleConditionPdCreateIssueEvents": {
        "description": "A list of PagerDuty incident actions which should be matched to create a Jira issue",
        "type": "array",
        "items": {
          "type": "string",
          "enum": [
            "incident.trigger",
            "incident.acknowledge",
            "incident.unacknowledge",
            "incident.resolve",
            "incident.escalate",
            "incident.assign",
            "incident.delegate",
            "incident.trigger_manually"
          ]
        },
        "example": [
          "incident.trigger",
          "incident.acknowledge",
          "incident.assign"
        ]
      },
      "RuleConditionPdUpdateIssueEvents": {
        "description": "A list of PagerDuty incident actions which should be matched to update a Jira issue",
        "type": "array",
        "items": {
          "type": "string",
          "enum": [
            "incident.trigger",
            "incident.acknowledge",
            "incident.unacknowledge",
            "incident.resolve",
            "incident.escalate",
            "incident.assign",
            "incident.delegate"
          ]
        },
        "example": [
          "incident.acknowledge",
          "incident.assign",
          "incident.escalate"
        ]
      },
      "RuleConditionPdPriorities": {
        "description": "A list of PagerDuty priorities that should be matched to create/update a Jira issue",
        "type": "array",
        "items": {
          "$ref": "#/components/schemas/PriorityReference"
        }
      },
      "RuleConditionPdUrgency": {
        "description": "A PagerDuty urgency that should be matched to create/update a Jira issue",
        "type": "string",
        "nullable": true,
        "enum": [
          "high",
          "low"
        ]
      },
      "RuleActions": {
        "description": "Actions that applied when the rule will be executed",
        "type": "array",
        "items": {
          "type": "object"
        }
      },
      "RuleActionsParams": {
        "description": "Configuration of actions that applied when the rule will be executed",
        "type": "object"
      },
      "Project": {
        "description": "Jira Project",
        "nullable": true,
        "type": "object",
        "properties": {
          "id": {
            "type": "string"
          },
          "key": {
            "type": "string"
          },
          "label": {
            "type": "string"
          },
          "icon": {
            "type": "string",
            "format": "url"
          },
          "html_url": {
            "type": "string",
            "format": "url"
          }
        },
        "required": [
          "id",
          "key",
          "label",
          "icon",
          "html_url"
        ],
        "example": {
          "id": "10000",
          "key": "PD",
          "label": "PagerDuty",
          "icon": "https://example.com/secure/projectavatar?avatarId=10324",
          "html_url": "https://example.com/projects/PD"
        }
      },
      "IssueType": {
        "description": "Jira Issue Type",
        "nullable": true,
        "type": "object",
        "properties": {
          "id": {
            "type": "string"
          },
          "label": {
            "type": "string"
          },
          "icon": {
            "type": "string",
            "format": "url"
          }
        },
        "required": [
          "id",
          "label",
          "icon"
        ],
        "example": {
          "id": "10002",
          "label": "Story",
          "icon": "http://example.com/images/icons/issuetypes/story.svg"
        }
      },
      "JiraStatus": {
        "description": "Jira Status",
        "type": "object",
        "properties": {
          "id": {
            "type": "string"
          },
          "label": {
            "type": "string"
          }
        },
        "required": [
          "id"
        ],
        "example": {
          "id": "10000",
          "label": "To Do"
        }
      },
      "PriorityReference": {
        "description": "The reference to the PagerDuty priority",
        "allOf": [
          {
            "$ref": "#/components/schemas/Reference"
          },
          {
            "type": "object",
            "properties": {
              "type": {
                "type": "string",
                "enum": [
                  "priority_reference"
                ]
              }
            }
          }
        ],
        "example": {
          "id": "PZQ39X0",
          "type": "priority_reference"
        }
      },
      "ServiceReference": {
        "description": "The reference to the service",
        "allOf": [
          {
            "$ref": "#/components/schemas/Reference"
          },
          {
            "type": "object",
            "properties": {
              "type": {
                "type": "string",
                "enum": [
                  "service_reference"
                ]
              }
            }
          }
        ],
        "example": {
          "id": "PIJ90N7",
          "type": "service_reference"
        }
      },
      "Reference": {
        "type": "object",
        "required": [
          "type",
          "id"
        ],
        "properties": {
          "id": {
            "type": "string"
          },
          "summary": {
            "type": "string",
            "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
          },
          "type": {
            "type": "string",
            "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
          },
          "self": {
            "type": "string",
            "format": "url",
            "description": "The API show URL at which the object is accessible"
          },
          "html_url": {
            "type": "string",
            "format": "url",
            "nullable": true,
            "description": "A URL at which the entity is uniquely displayed in the Web app"
          }
        }
      },
      "Pagination": {
        "type": "object",
        "properties": {
          "offset": {
            "type": "integer",
            "description": "Echoes offset pagination property.",
            "readOnly": true,
            "default": 0
          },
          "limit": {
            "type": "integer",
            "description": "Echoes limit pagination property.",
            "readOnly": true,
            "minimum": 0,
            "maximum": 100,
            "default": 25
          },
          "more": {
            "type": "boolean",
            "description": "Indicates if there are additional records to return.",
            "readOnly": true,
            "default": false
          },
          "total": {
            "type": "integer",
            "description": "The total number of records matching the given query.",
            "nullable": true,
            "readOnly": true,
            "default": null
          }
        },
        "required": [
          "offset",
          "limit",
          "more",
          "total"
        ]
      },
      "Error": {
        "type": "object",
        "properties": {
          "error": {
            "type": "object",
            "properties": {
              "message": {
                "type": "string",
                "readOnly": true,
                "description": "Error message string.",
                "example": "Invalid Input Provided"
              },
              "errors": {
                "type": "array",
                "readOnly": true,
                "items": {
                  "type": "string",
                  "readOnly": true,
                  "description": "Human-readable error details."
                },
                "example": [
                  "Name can't be blank"
                ]
              }
            },
            "required": [
              "message"
            ]
          }
        },
        "required": [
          "error"
        ]
      }
    },
    "securitySchemes": {
      "BearerToken": {
        "type": "http",
        "scheme": "bearer",
        "description": "PagerDuty OAuth user Access Token\n\nInspect network requests that are done by the PagerDuty Addon settings page\nand look for a request like `GET /accounts_mapping`.\n\nIn the request headers you will find `Authorization`.\nUse its value as it is to make your requests.\n"
      }
    }
  },
  "paths": {
    "/rules": {
      "get": {
        "tags": [
          "Rules"
        ],
        "summary": "List rules",
        "description": "Returns a list of rules.",
        "operationId": "getRules",
        "parameters": [
          {
            "$ref": "#/components/parameters/total"
          },
          {
            "$ref": "#/components/parameters/limit"
          },
          {
            "$ref": "#/components/parameters/offset"
          },
          {
            "$ref": "#/components/parameters/x-pagerduty-jira-signature"
          }
        ],
        "responses": {
          "200": {
            "description": "A paginated list of rules.",
            "content": {
              "application/json": {
                "schema": {
                  "allOf": [
                    {
                      "$ref": "#/components/schemas/Pagination"
                    },
                    {
                      "type": "object",
                      "properties": {
                        "rules": {
                          "type": "array",
                          "items": {
                            "$ref": "#/components/schemas/Rule"
                          }
                        }
                      },
                      "required": [
                        "rules"
                      ]
                    }
                  ]
                },
                "examples": {
                  "Response": {
                    "summary": "Response Example",
                    "value": {
                      "rules": [
                        {
                          "name": "Sample Rule",
                          "enabled": true,
                          "created_at": "2020-06-02T12:42:37Z",
                          "config": {
                            "sync_comments": true,
                            "services": [
                              {
                                "id": "PLW70S2",
                                "type": "service_reference",
                                "label": "Jira Integration",
                                "html_url": "https://subdomain.pagerduty.com/service-directory/PLW70S2"
                              },
                              {
                                "id": "PR5R1T1",
                                "type": "service_reference",
                                "label": "Jira Performance Tests",
                                "html_url": "https://subdomain.pagerduty.com/service-directory/PR5R1T1"
                              }
                            ],
                            "projects": [
                              {
                                "key": "PD",
                                "id": "10000",
                                "label": "PagerDuty",
                                "html_url": "http://jira.example.com/projects/PD",
                                "icon": "http://jira.example.com/secure/projectavatar?avatarId=10324"
                              },
                              {
                                "key": "HD",
                                "id": "10001",
                                "label": "Helpdesk",
                                "html_url": "http://jira.example.com/projects/HD",
                                "icon": "http://jira.example.com/secure/projectavatar?avatarId=10324"
                              }
                            ],
                            "issue_types": [
                              {
                                "id": "10002",
                                "icon": "http://jira.example.com/images/icons/issuetypes/story.svg",
                                "label": "Story"
                              },
                              {
                                "id": "10003",
                                "icon": "http://jira.example.com/secure/viewavatar?size=xsmall&avatarId=10318&avatarType=issuetype",
                                "label": "Task"
                              },
                              {
                                "id": "10004",
                                "icon": "http://jira.example.com/secure/viewavatar?size=xsmall&avatarId=10303&avatarType=issuetype",
                                "label": "Bug"
                              }
                            ],
                            "autocreate_incidents": true,
                            "autocreate_incidents_config": [
                              {
                                "jql": "issuetype=Bug",
                                "on_create": true,
                                "on_update": true,
                                "target_service": {
                                  "id": "PR5R1T1",
                                  "type": "service_reference"
                                },
                                "actions": [
                                  {
                                    "key": "jira_field_set_priority",
                                    "label": "Set Priority",
                                    "icon": "jira",
                                    "isDisabled": false
                                  },
                                  {
                                    "key": "jira_field_set_labels",
                                    "label": "Set Labels",
                                    "icon": "jira",
                                    "isDisabled": false
                                  },
                                  {
                                    "key": "pd_set_priority",
                                    "label": "Set Incident Priority",
                                    "icon": "pd",
                                    "isDisabled": false
                                  }
                                ],
                                "actions_params": {
                                  "jira_field_set_priority": {
                                    "label": "Highest",
                                    "icon": "http://jira.example.com/images/icons/priorities/highest.svg",
                                    "name": "Highest",
                                    "id": "1"
                                  },
                                  "jira_field_set_labels": [
                                    {
                                      "label": "Service name",
                                      "icon": "pd",
                                      "key": "incident.service.summary"
                                    }
                                  ],
                                  "pd_set_priority": {
                                    "id": "PZQ39X0",
                                    "type": "priority_reference",
                                    "label": "P1",
                                    "html_url": null,
                                    "icon": "rect_color_ff4500"
                                  }
                                }
                              },
                              {
                                "jql": "issuetype=Story",
                                "on_create": true,
