    "/": {
      "get": {
        "summary": "Returns general information about an instance.",
        "operationId": "describeInstance",
        "responses": {
          "200": {
            "description": "The instance information",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/InstanceInfo"
