{
    "openapi":"3.0.0",
    "info":{
      "description":"",
      "version":"1.0",
      "title":"Engage Digital API",
      "termsOfService":"https://developer.ringcentral.com"
    },
    "tags":[
      {
        "name":"Settings"
      }
    ],
    "paths":{
      "/settings":{
        "get":{
          "tags":[
            "Settings"
          ],
          "summary":"Getting all settings",
          "operationId":"getAllSettings",
          "description":"This method renders all settings of your domain.\n\nAuthorization​: only users that can update settings.",
          "responses":{
            "200":{
              "description":"Success",
              "content":{
                "application/json":{
                  "schema":{
                    "$ref":"#/components/schemas/Settings"
                  }
                }
              }
            }
          }
        },
        "put":{
          "tags":[
            "Settings"
          ],
          "summary":"Updating settings",
          "operationId":"updateSettings",
          "description":"This method updates the current domain settings.\n\nAuthorization​: only users that can update settings.",
          "parameters":[
            {
              "name":"activity_presence_threshold",
              "in":"query",
              "required":false,
              "description":"(in hours).",
              "schema":{
                "type":"integer"
              }
            },
            {
              "name":"activity_tracking",
              "in":"query",
              "required":false,
              "description":"​Enable activity tracking (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"beginning_of_week",
              "in":"query",
              "required":false,
              "description":"(Day of week)",
              "schema":{
                "type":"string"
              }
            },
            {
              "name":"category_tagging",
              "in":"query",
              "required":false,
              "description":"A​ ctivate the forced categorization by source.​ (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"content_languages",
              "in":"query",
              "required":false,
              "description":"(See format)",
              "schema":{
                "type":"string"
              }
            },
            {
              "name":"dashboard",
              "in":"query",
              "required":false,
              "description":"Activate the dashboard (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"deny_iframe_integration",
              "in":"query",
              "required":false,
              "description":"Prevent the DD to be embed by other websites (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"disable_password_autocomplete",
              "in":"query",
              "required":false,
              "description":"(Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"expire_password_after",
              "in":"query",
              "required":false,
              "description":"password expiration delay (in seconds)",
              "schema":{
                "type":"integer"
              }
            },
            {
              "name":"expire_password_enabled",
              "in":"query",
              "required":false,
              "description":"enable password expiration (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"export_in_seconds",
              "in":"query",
              "required":false,
              "description":"provide durations in seconds in export (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"fold_useless_contents",
              "in":"query",
              "required":false,
              "description":"fold archived contents (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"fte_duration",
              "in":"query",
              "required":false,
              "description":"FTE data period (in hours)",
              "schema":{
                "type":"integer"
              }
            },
            {
              "name":"identity_merge",
              "in":"query",
              "required":false,
              "description":"enable identity merge (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"intervention_defer_rates[]",
              "in":"query",
              "required":false,
              "description":"(Array of times in seconds)",
              "schema":{
                "type":"array",
                "items":{
                  "type":"integer"
                }
              }
            },
            {
              "name":"intervention_defer_threshold",
              "in":"query",
              "required":false,
              "description":"(in seconds)",
              "schema":{
                "type":"integer"
              }
            },
            {
              "name":"intervention_rates",
              "in":"query",
              "required":false,
              "description":"(Array of times in seconds)",
              "schema":{
                "type":"array",
                "items":{
                  "type":"integer"
                }
              }
            },
            {
              "name":"locale",
              "in":"query",
              "required":false,
              "description":"locale code (String)",
              "schema":{
                "type":"string"
              }
            },
            {
              "name":"multi_lang",
              "in":"query",
              "required":false,
              "description":"activate multi language support for messages (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"name",
              "in":"query",
              "required":false,
              "description":"Name of the Dimelo Digital (String)",
              "schema":{
                "type":"string"
              }
            },
            {
              "name":"password_archivable_enabled",
              "in":"query",
              "required":false,
              "description":"prohibit reusing old passwords (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"password_archivable_size",
              "in":"query",
              "required":false,
              "description":"number of archived passwords",
              "schema":{
                "type":"integer"
              }
            },
            {
              "name":"password_min_length",
              "in":"query",
              "required":false,
              "description":"minimum character length",
              "schema":{
                "type":"integer"
              }
            },
            {
              "name":"password_non_word",
              "in":"query",
              "required":false,
              "description":"should contain at least 1 non alphanumeric char (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"password_numbers",
              "in":"query",
              "required":false,
              "description":"should contain at least 1 number (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"password_recovery_disabled",
              "in":"query",
              "required":false,
              "description":"disable password recovery by email (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"push_enabled",
              "in":"query",
              "required":false,
              "description":"Enable push mode (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"reply_as_any_identity",
              "in":"query",
              "required":false,
              "description":"Enable reply as any identity (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"rtl_support",
              "in":"query",
              "required":false,
              "description":"Enable right to left support (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"self_approval_required",
              "in":"query",
              "required":false,
              "description":"​Allow authors to ask approval of their messages (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"session_timeout",
              "in":"query",
              "required":false,
              "description":"Session timeout (in minutes)",
              "schema":{
                "type":"integer"
              }
            },
            {
              "name":"spellchecking",
              "in":"query",
              "required":false,
              "description":"Enable spellchecking (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"style",
              "in":"query",
              "required":false,
              "description":"Defines the DD’s design (String)",
              "schema":{
                "type":"string"
              }
            },
            {
              "name":"third_party_services_disabled",
              "in":"query",
              "required":false,
              "description":"Disable third-party services (tracking...) (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"timezone",
              "in":"query",
              "required":false,
              "description":"Use the timezone endpoint to get the timezone name (String)",
              "schema":{
                "type":"string"
              }
            },
            {
              "name":"track_js",
              "in":"query",
              "required":false,
              "description":"Track JS errors (Boolean)",
              "schema":{
                "type":"boolean"
              }
            },
            {
              "name":"type",
              "in":"query",
              "required":false,
              "description":"Can be ‘demo’, ‘production’ or ‘archived’",
              "schema":{
                "type":"string",
                "enum":[
                  "demo",
                  "production",
                  "archived"
                ]
              }
            },
            {
              "name":"urgent_task_threshold",
              "in":"query",
              "required":false,
              "description":"Chat max response time (in seconds)",
              "schema":{
                "type":"integer"
              }
            },
            {
              "name":"use_system_font",
              "in":"query",
              "required":false,
              "description":"Experimental (Boolean)",
              "schema":{
                "type":"boolean"
              }
            }
          ],
          "responses":{
            "200":{
              "description":"Success",
              "content":{
                "application/json":{
                  "schema":{
                    "$ref":"#/components/schemas/Settings"
                  }
                }
              }
            }
          }
        }
      }
    },
    "servers":[
      {
        "url":"https://DOMAIN.api.engagement.dimelo.com/1.0"
      }
    ],
    "components":{
      "schemas":{
        "Settings":{
          "properties":{
            "id":{
              "type":"string"
            },
            "created_at":{
              "type":"string",
              "format":"date-time"
            },
            "updated_at":{
              "type":"string",
              "format":"date-time"
            },
            "activity_presence_threshold":{
              "type":"integer"
            },
            "activity_tracking":{
              "type":"boolean"
            },
            "beginning_of_week":{
              "type":"string"
            },
            "content_languages[]":{
              "type":"array",
              "items":{
                "type":"string"
              }
            },
            "deny_iframe_integration":{
              "type":"boolean"
            },
            "disable_password_autocomplete":{
              "type":"boolean"
            },
            "dump_in_preprod":{
              "type":"boolean"
            },
            "expire_password_after":{
              "type":"integer"
            },
            "expire_password_enabled":{
              "type":"boolean"
            },
            "fte_duration":{
              "type":"integer"
            },
            "identity_merge":{
              "type":"boolean"
            },
            "intervention_defer_rates":{
              "type":"array",
              "items":{
                "type":"integer"
              }
            },
            "intervention_defer_threshold":{
              "type":"integer"
            },
            "intervention_rates":{
              "type":"array",
              "items":{
                "type":"integer"
              }
            },
            "locale":{
              "type":"string"
            },
            "multi_lang":{
              "type":"boolean"
            },
            "name":{
              "type":"string"
            },
            "password_archivable_enabled":{
              "type":"boolean"
            },
            "password_archivable_size":{
              "type":"integer"
            },
            "password_min_length":{
              "type":"integer"
            },
            "password_non_word":{
              "type":"boolean"
            },
            "password_numbers":{
              "type":"boolean"
            },
            "password_recovery_disabled":{
              "type":"boolean"
            },
            "push_enabled":{
              "type":"boolean"
            },
            "reply_as_any_identity":{
              "type":"boolean"
            },
            "rtl_support":{
              "type":"boolean"
            },
            "self_approval_required":{
              "type":"boolean"
            },
            "session_timeout":{
              "type":"integer"
            },
            "sharding_key":{
              "type":"string"
            },
            "spellchecking":{
              "type":"boolean"
            },
            "style":{
              "type":"string"
            },
            "third_party_services_disabled":{
              "type":"boolean"
            },
            "timezone":{
              "type":"string"
            },
            "type":{
              "type":"string"
            },
            "urgent_task_threshold":{
              "type":"integer"
            },
            "browser_notifications_disabled":{
              "type":"boolean"
            },
            "display_only_unknown_bbcode":{
              "type":"boolean"
            },
            "intervention_closing_period":{
              "type":"integer"
            },
            "use_two_letters_avatars":{
              "type":"boolean"
            }
          }
        }
      }
    }
  }
