                        "name": "outputSRS",
                        "in": "query",
                        "description": "The EPSG code of the spatial reference system (SRS) to use for output geometries.",
                        "required": false,
                        "schema": {
                            "type": "integer",
                            "enum": [
                                4326,
                                4269,
                                3005,
                                3857,
                                26907,
                                26908,
                                26909,
                                26910,
                                26911
                            ],
                            "default": 4326
