                        "name": "embed",
                        "in": "query",
                        "description": "A flag to indicate whether to embed the corresponding 'feature' into each matching name",
                        "required": false,
                        "schema": {
                            "type": "integer",
                            "enum": [
                                0,
                                1
                            ]
                        }
                    },
                    {
                        "name": "outputStyle",
                        "in": "query",
                        "description": "A flag indicating whether to include with each matching name a succinct list of attributes (summary), or a comprehensive list of attributes (detail)",
                        "required": false,
                        "schema": {
                            "type": "string",
                            "enum": [
                                "summary",
                                "detail"
                            ],
                            "default": "summary"
                        }
                    },
                    {
                        "name": "itemsPerPage",
                        "in": "query",
                        "description": "The number of search results to return (1-200)",
                        "required": false,
                        "schema": {
                            "type": "integer",
                            "default": 20
                        }
                    },
                    {
                        "name": "startIndex",
                        "in": "query",
                        "description": "The index of the first record to be returned (>= 1)",
                        "required": false,
                        "schema": {
                            "type": "integer",
                            "default": 1
                        }
                    }
                ],
                "responses": {
                    "200": {
                        "description": "A list of names matching the search criteria"
                    },
                    "400": {
                        "description": "A required parameter is missing or invalid"
