{
  "openapi":"3.0.0",
  "info":{
    "description":"",
    "version":"1.0",
    "title":"Engage Digital API",
    "termsOfService":"https://developer.ringcentral.com"
  },
  "tags":[
    {
      "name":"Channels"
    }
  ],
  "paths":{
    "/channels":{
      "get":{
        "tags":[
          "Channels"
        ],
        "summary":"Getting all channels",
        "operationId":"getAllChannels",
        "description":"This method renders all channels ordered by date of creation.",
        "parameters":[
          {
            "name":"offset",
            "in":"query",
            "description":"The record index to start. Default value is 0.",
            "required":false,
            "schema":{
              "type":"integer",
              "format":"int32"
            }
          },
          {
            "name":"limit",
            "in":"query",
            "description":"The max number of records to return. Default value is 30, max value is 150.",
            "required":false,
            "schema":{
              "type":"integer",
              "format":"int32"
            }
          }
        ],
        "responses":{
          "200":{
            "description":"Success",
            "content":{
              "application/json":{
                "schema":{
                  "$ref":"#/components/schemas/GetAllChannelsResponse"
                }
              }
            }
          }
        }
      }
    },
    "/channels/{channelId}":{
      "get":{
        "tags":[
          "Channels"
        ],
        "summary":"Getting a channel from its id",
        "operationId":"getChannel",
        "description":"This method renders a channel from given id.",
        "parameters":[
          {
            "name":"channelId",
            "in":"path",
            "required":true,
            "schema":{
              "type":"string"
            }
          }
        ],
        "responses":{
          "200":{
            "description":"Success",
            "content":{
              "application/json":{
                "schema":{
                  "$ref":"#/components/schemas/Channel"
                }
              }
            }
          }
        }
      },
      "put":{
        "tags":[
          "Channels"
        ],
        "summary":"Updating a channel",
        "operationId":"updateChannel",
        "description":"This method updates an existing channel from given attributes and renders it in case of success.\n\nAuthorization​: only users that are able to update channels.",
        "parameters":[
          {
            "name":"channelId",
            "in":"path",
            "required":true,
            "schema":{
              "type":"string"
            }
          },
          {
            "name":"name",
            "in":"query",
            "required":false,
            "description":"The name of the channel.",
            "schema":{
              "type":"string"
            }
          },
          {
            "name":"source_ids",
            "in":"query",
            "required":false,
            "description":"An array containing id of each source assigned to a channel (multiple).",
            "schema":{
              "type":"array",
              "items":{
                "type":"string"
              }
            }
          },
          {
            "name":"soft_capability",
            "in":"query",
            "required":false,
            "description":"Number of tasks that can be assigned to agent by the routing before they are considered \"occupied\".",
            "schema":{
              "type":"integer"
            }
          },
          {
            "name":"hard_capability",
            "in":"query",
            "required":false,
            "description":"M​aximum number of tasks that can be assigned to agents.",
            "schema":{
              "type":"integer"
            }
          },
          {
            "name":"task_timeout_seconds",
            "in":"query",
            "required":false,
            "description":"this field defines the time before a task expires (in seconds).",
            "schema":{
              "type":"integer"
            }
          }
        ],
        "responses":{
          "200":{
            "description":"Success",
            "content":{
              "application/json":{
                "schema":{
                  "$ref":"#/components/schemas/Channel"
                }
              }
            }
          }
        }
      }
    }
  },
  "servers":[
    {
      "url":"https://DOMAIN.api.engagement.dimelo.com/1.0"
    }
  ],
  "components":{
    "securitySchemes":{
      "bearerAuth":{
        "type":"http",
        "scheme":"bearer"
      }
    },
    "schemas":{
      "GetAllChannelsResponse":{
        "properties":{
          "count":{
            "type":"integer",
            "format":"int32"
          },
          "limit":{
            "type":"integer",
            "format":"int32"
          },
          "offset":{
            "type":"integer",
            "format":"int32"
          },
          "records":{
            "type":"array",
            "items":{
              "$ref":"#/components/schemas/Channel"
            }
          }
        }
      },
      "Channel":{
        "properties":{
          "id":{
            "type":"string"
          },
          "created_at":{
            "type":"string",
            "format":"date-time"
          },
          "updated_at":{
            "type":"string",
            "format":"date-time"
          },
          "name":{
            "type":"string"
          },
          "label":{
            "type":"string"
          },
          "source_ids":{
            "type":"array",
            "items":{
              "type":"string"
            }
          },
          "hard_capability":{
            "type":"integer"
          },
          "soft_capability":{
            "type":"integer"
          },
          "task_timeout_seconds":{
            "type":"integer"
          }
        }
      }
    }
  }
}
