        "schema": {
          "type": "string"
        },
        "example": "testing"
      },
      "accessToken": {
        "name": "accessToken",
        "in": "query",
        "description": "The token that grants the access. It has to be valid, not elapsed and it has to belong to the owner of the _APPID_ or to an user with RootAdmin privileges. The use of the accessToken in the parameter is __DEPRECATED__. Please pass the access token in the HTTP Header: \"Authorization: Bearer _accessToken_\".\n",
        "required": true,
        "schema": {
          "type": "string"
        }
      },
      "accessTokenAppidOnly": {
        "name": "accessToken",
        "in": "query",
        "description": "The token that grants the access. It has to be valid, not elapsed and it has to belong to the owner of the _APPID_. The use of the accessToken in the parameter is __DEPRECATED__. Please pass the access token in the HTTP Header: \"Authorization: Bearer _accessToken_\".\n",
        "required": true,
        "schema": {
          "type": "string"
        }
      },
      "accessTokenUsername": {
        "name": "accessToken",
        "in": "query",
        "description": "The token that grants the access. It has to be valid, not elapsed and it has to belong to the _USERNAME_ or to an user with RootAdmin privileges. The use of the accessToken in the parameter is __DEPRECATED__. Please pass the access token in the HTTP Header: \"Authorization: Bearer _accessToken_\".\n",
        "required": true,
        "schema": {
          "type": "string"
        }
      },
      "accessTokenRootadminOnly": {
        "name": "accessToken",
        "in": "query",
        "description": "The token that grants the access. It has to be valid, not elapsed and it has to belong to an user with RootAdmin privilege. The use of the accessToken in the parameter is __DEPRECATED__. Please pass the access token in the HTTP Header: \"Authorization: Bearer _accessToken_\".\n",
        "required": true,
        "schema": {
          "type": "string"
        }
      },
      "accessTokenBasic": {
        "name": "accessTokenBasic",
        "in": "query",
        "description": "The token that grants the access. It has to be valid, and not elapsed. The use of the accessToken in the parameter is __DEPRECATED__. Please pass the access token in the HTTP Header: \"Authorization: Bearer _accessToken_\".\n",
        "required": true,
        "schema": {
          "type": "string"
        }
      },
      "accessTokenElementid": {
        "name": "accessToken",
        "in": "query",
