  "components": {
    "schemas": {
      "Us": {
        "type": "object",
        "properties": {
          "date": {
            "type": "integer",
            "description": "Date on which data was collected by The COVID Tracking Project.",
            "nullable": false,
            "example": 20200501
          },
          "states": {
            "type": "integer",
            "description": "Number of states and territories included in the US dataset for this day.",
            "nullable": false,
            "example": 50
          },
          "positive": {
            "type": "integer",
            "description": "Individuals with confirmed or probable COVID-19 per the expanded [CSTE case definition](https://cdn.ymaws.com/www.cste.org/resource/resmgr/2020ps/Interim-20-ID-01_COVID-19.pdf) of April 5th, 2020 [approved by the CDC](https://wwwn.cdc.gov/nndss/conditions/coronavirus-disease-2019-covid-19/case-definition/2020/).",
            "nullable": true,
            "example": 50
          },
          "negative": {
            "type": "integer",
            "description": "Individuals with a completed viral test that returned a negative result. For states / territories that do not report this number directly, we compute it using one of several methods, depending on which data points the state provides.",
            "nullable": true,
            "example": 50
          },
          "pending": {
            "type": "integer",
            "description": "Tests whose results have not yet been reported.",
            "nullable": true,
            "example": 50
          },
          "hospitalizedCurrently": {
            "type": "integer",
            "description": "Individuals who are currently hospitalized with COVID-19. Definitions vary by state / territory. Where possible, we report hospitalizations with confirmed or probable COVID-19 cases per the expanded [CSTE case definition](https://cdn.ymaws.com/www.cste.org/resource/resmgr/2020ps/Interim-20-ID-01_COVID-19.pdf) of April 5th, 2020 [approved by the CDC](https://wwwn.cdc.gov/nndss/conditions/coronavirus-disease-2019-covid-19/case-definition/2020/).",
            "nullable": true,
            "example": 50
          },
          "hospitalizedCumulative": {
            "type": "integer",
            "description": "Total number of individuals who have ever been hospitalized with COVID-19.  Definitions vary by state / territory. Where possible, we report hospitalizations with confirmed or probable COVID-19 cases per the expanded [CSTE case definition](https://cdn.ymaws.com/www.cste.org/resource/resmgr/2020ps/Interim-20-ID-01_COVID-19.pdf) of April 5th, 2020 [approved by the CDC](https://wwwn.cdc.gov/nndss/conditions/coronavirus-disease-2019-covid-19/case-definition/2020/).",
            "nullable": true,
            "example": 50
          },
          "inIcuCurrently": {
            "type": "integer",
            "description": "Individuals who are currently hospitalized in the Intensive Care Unit with COVID-19. Definitions vary by state / territory. Where possible, we report patients in the ICU with confirmed or probable COVID-19 cases per the expanded [CSTE case definition](https://cdn.ymaws.com/www.cste.org/resource/resmgr/2020ps/Interim-20-ID-01_COVID-19.pdf) of April 5th, 2020 [approved by the CDC](https://wwwn.cdc.gov/nndss/conditions/coronavirus-disease-2019-covid-19/case-definition/2020/).",
            "nullable": true,
            "example": 50
          },
          "inIcuCumulative": {
            "type": "integer",
            "description": "Total number of individuals who have ever been hospitalized in the Intensive Care Unit with COVID-19. Definitions vary by state / territory. Where possible, we report patients in the ICU with confirmed or probable COVID-19 cases per the expanded [CSTE case definition](https://cdn.ymaws.com/www.cste.org/resource/resmgr/2020ps/Interim-20-ID-01_COVID-19.pdf) of April 5th, 2020 [approved by the CDC](https://wwwn.cdc.gov/nndss/conditions/coronavirus-disease-2019-covid-19/case-definition/2020/).",
            "nullable": true,
            "example": 50
          },
          "onVentilatorCurrently": {
            "type": "integer",
            "description": "Individuals who are currently hospitalized under advanced ventilation with COVID-19. Definitions vary by state / territory. Where possible, we report patients on ventilation with confirmed or probable COVID-19 cases per the expanded [CSTE case definition](https://cdn.ymaws.com/www.cste.org/resource/resmgr/2020ps/Interim-20-ID-01_COVID-19.pdf) of April 5th, 2020 [approved by the CDC](https://wwwn.cdc.gov/nndss/conditions/coronavirus-disease-2019-covid-19/case-definition/2020/).",
            "nullable": true,
            "example": 50
          },
          "onVentilatorCumulative": {
            "type": "integer",
            "description": "Total number of individuals who have ever been hospitalized under advanced ventilation with COVID-19. Definitions vary by state / territory. Where possible, we report patients on ventilation with confirmed or probable COVID-19 cases per the expanded [CSTE case definition](https://cdn.ymaws.com/www.cste.org/resource/resmgr/2020ps/Interim-20-ID-01_COVID-19.pdf) of April 5th, 2020 [approved by the CDC](https://wwwn.cdc.gov/nndss/conditions/coronavirus-disease-2019-covid-19/case-definition/2020/).",
            "nullable": true,
            "example": 50
          },
          "recovered": {
            "type": "integer",
            "description": "Deprecated: Individuals who have recovered from COVID-19. Definitions vary by state / territory.",
            "nullable": true,
            "example": 50
          },
          "dateChecked": {
            "type": "string",
            "description": "Deprecated. This is an old label for *lastUpdateEt*.",
            "nullable": true,
            "example": 50
          },
          "death": {
            "type": "integer",
            "description": "Total fatalities with confirmed OR probable COVID-19 case diagnosis (per the expanded [CSTE case definition](https://cdn.ymaws.com/www.cste.org/resource/resmgr/2020ps/Interim-20-ID-01_COVID-19.pdf) of April 5th, 2020 [approved by the CDC](https://wwwn.cdc.gov/nndss/conditions/coronavirus-disease-2019-covid-19/case-definition/2020/)). In states where the information is available, it only tracks fatalities with confirmed OR probable COVID-19 case diagnosis where COVID-19 is an underlying cause of death according to the death certificate based on [WHO guidelines](https://www.who.int/classifications/icd/Guidelines_Cause_of_Death_COVID-19.pdf?ua=1).",
            "nullable": true,
            "example": 50
          },
          "hospitalized": {
            "type": "integer",
            "description": "Deprecated. Old label for hospitalizedCumulative.",
            "nullable": true,
            "example": 50
          },
          "total": {
            "type": "integer",
            "description": "Deprecated. Computed by adding *positive*, *negative*, and *pending* values.",
            "nullable": true,
            "example": 50
          },
          "totalTestResults": {
            "type": "integer",
            "description": "Where possible, we report total tests in units of people tested, rather than units of specimens tested. Currently computed by adding positive and negative values because some states do not report totals and to work around different reporting cadences for cases and tests.",
            "nullable": true,
            "example": 50
          },
          "posNeg": {
            "type": "integer",
            "description": "Deprecated. Computed by adding *positive* and *negative* values.",
            "nullable": true,
            "example": 50
          },
          "deathIncrease": {
            "type": "integer",
            "description": "Increase in *death* computed by subtracting the value of *death* for the previous day from the value of *death* for the current day.",
            "nullable": true,
            "example": 50
          },
          "hospitalizedIncrease": {
            "type": "integer",
            "description": "Increase in *hospitalizedCumulative* computed by subtracting the value of *hospitalizedCumulative* for the previous day from the value of *hospitalizedCumulative* for the current day.",
            "nullable": true,
            "example": 50
          },
          "negativeIncrease": {
            "type": "integer",
            "description": "Increase in *negative* computed by subtracting the value of *negative* for the previous day from the value for *negative* from the current day.",
            "nullable": true,
            "example": 50
          },
