            "post": {
                "tags": [
                    "Account"
                ],
                "summary": "设置杠杆倍数",
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "required": [
                                    "mgnMode",
                                    "lever"
                                ],
                                "type": "object",
                                "properties": {
                                    "lever": {
                                        "type": "string",
                                        "description": "必填<br>杠杆倍数"
                                    },
                                    "mgnMode": {
                                        "type": "string",
                                        "description": "保证金模式<br>`isolated`：逐仓，`cross`：全仓<br>如果`ccy`有效传值，该参数值只能为`cross`。"
                                    },
                                    "instId": {
                                        "type": "string",
                                        "description": "可选<br>产品ID：币对、合约<br>`instId`和`ccy`至少要传一个；如果两个都传，默认使用instId"
                                    },
                                    "ccy": {
                                        "type": "string",
                                        "description": "可选<br>保证金币种，仅适用于跨币种保证金模式的全仓`币币杠杆`。"
                                    },
                                    "posSide": {
                                        "type": "string",
                                        "description": "可选<br>持仓方向<br>`long`：双向持仓多头<br>`short`：双向持仓空头<br>`net`：单向持仓<br>在双向持仓且保证金模式为逐仓条件下必填，且仅可选择`long`或`short`，其他情况下非必填，默认`net`；仅适用于`交割/永续`"
                                    }
                                }
                            },
                            "examples": {
                                "1": {
                                    "summary": "币对层面-设置逐仓杠杆倍数（币币杠杆）",
                                    "value": {
                                        "instId": "BTC-USDT",
                                        "lever": "5",
                                        "mgnMode": "isolated"
                                    }
                                },
                                "2": {
                                    "summary": "币对层面-设置单币种保证金模式下全仓杠杆倍数（币币杠杆）",
                                    "value": {
                                        "instId": "BTC-USDT",
                                        "lever": "5",
                                        "mgnMode": "cross"
                                    }
                                },
                                "3": {
                                    "summary": "币种层面-设置跨币种保证金模式下全仓杠杆倍数（币币杠杆）",
                                    "value": {
                                        "ccy": "BTC",
                                        "lever": "5",
                                        "mgnMode": "cross"
                                    }
                                },
