{
  "openapi": "3.0.3",
  "info": {
    "title": "Application",
    "version": "0.0.1",
    "description": "To use the API you must first sign-in using the `POST /auth/sign-in` endpoint and collect the **Bearer token** from the response.\n"
  },
  "servers": [
    {
      "url": "/api"
    }
  ],
  "paths": {
    "/auth/sign-up": {
      "post": {
        "tags": ["Authentication"],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["email", "password"],
                "properties": {
                  "email": {
                    "type": "string"
                  },
                  "password": {
                    "type": "string"
                  },
                  "invitationToken": {
                    "type": "string",
                    "description": "This invitation token is sent via email to the user. If they access it via the link, the invitation token is used to auto-accept the invitation to the specific tenant."
                  },
                  "tenantId": {
                    "type": "string",
                    "description": "If the user access the application via subdomain, it onboards the user on the tenant passed."
                  }
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Bearer token."
          },
          "400": {
            "description": "Email is already in use."
          },
          "429": {
            "description": "Too Many Requests."
          }
        }
      }
    },
    "/auth/sign-in": {
      "post": {
        "tags": ["Authentication"],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["email", "password"],
                "properties": {
                  "email": {
                    "type": "string"
                  },
                  "password": {
                    "type": "string"
                  },
                  "invitationToken": {
                    "type": "string",
                    "description": "This invitation token is sent via email to the user. If they access it via the link, the invitation token is used to auto-accept the invitation to the specific tenant."
                  },
                  "tenantId": {
                    "type": "string",
                    "description": "If the user access the application via subdomain, it onboards the user on the tenant passed."
                  }
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Bearer token."
          },
          "400": {
            "description": "Sorry, we don't recognize your credentials."
          },
          "429": {
            "description": "Too Many Requests."
          }
        }
      }
    },
    "/auth/me": {
      "get": {
        "security": [
          {
            "bearerAuth": []
          }
        ],
        "tags": ["Authentication"],
        "responses": {
          "200": {
            "description": "Current User.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/UserCurrent"
                }
              }
            }
          },
          "401": {
            "description": "Unauthorized"
          },
"403": {
            "description": "Forbidden"
          },
          "429": {
            "description": "Too Many Requests."
          }
        }
      }
    },
    "/auth/change-password": {
      "put": {
        "security": [
          {
            "bearerAuth": []
          }
        ],
        "tags": ["Authentication"],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "required": ["oldPassword", "newPassword"],
                "properties": {
                  "oldPassword": {
                    "type": "string"
                  },
                  "newPassword": {
                    "type": "string"
                  }
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "Current User.",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/UserCurrent"
                }
              }
            }
          },
          "400": {
            "description": "The old password is invalid."
          },
          "401": {
            "description": "Unauthorized"
          },
"403": {
            "description": "Forbidden"
          },
          "429": {
            "description": "Too Many Requests."
          }
        }
      }
    },
    "/auth/send-email-address-verification-email": {
      "post": {
        "security": [
          {
            "bearerAuth": []
          }
        ],
        "tags": ["Authentication"],
        "requestBody": {
          "required": true,
          "content": {
            "application/json": {
              "schema": {
                "type": "object",
                "properties": {
                  "tenantId": {
                    "type": "string",
                    "description": "If the user access the application via subdomain, the email will also be sent using the subdomain."
                  }
                }
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "OK"
          },
          "401": {
            "description": "Unauthorized"
          },
"403": {
            "description": "Forbidden"
          },
          "429": {
            "description": "Too Many Requests."
          }
        }
      }
    },
    "/auth/send-password-reset-email": {
