{
  "openapi": "3.0.2",
  "info": {
    "title": "Onyx Health Marketplace API",
    "description": "The Onyx Health Marketplace API provides details about approved applications.",
    "license": {
      "name": "Creative Commons Zero v1.0 Universal",
      "url": "http://spdx.org/licenses/CC0-1.0.html"
    },
    "version": "1.0.0",
    "contact": {
      "name": "Onyx Health",
      "email": "info@onyxhealth.io",
      "url": "https://www.onyxhealth.io/contact/"
    }
  },
  "paths": {
    "/portal/software/marketplace/api/": {
      "summary": "Access details about all currently approved software in the marketplacve.",
      "description": "A public GET API for fetching details about approved software applications.",
      "get": {
        "summary": "Return a JSON document containing all approved software in a list named results.",
        "operationId": "marketplace-all",
        "responses": {
          "default": {
            "description": "Unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "200": {
            "description": "get all approved software in JSON format.",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "items": {"$ref": "#/components/schemas/Software"}
                }
              }

            }
          }
        },
        "parameters": [
          {
            "$ref": "#/components/parameters/data-provider"
          },
          {
            "$ref": "#/components/parameters/count"
          }
        ]
      }
    },
    "/portal/software/marketplace/api/{data-provider}": {
      "summary": "Access to approved software for the given data-provider (i.e. payer).",
      "description": "A public GET API for fetching details about approved software applications.",
      "get": {
        "summary": "Return a JSON document containing all approved software in a list named results.",
        "operationId": "marketplace-data-provider",
        "responses": {
          "default": {
            "description": "Unexpected error",
            "content": {
              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          },
          "200": {
            "description": "get all approved software in JSON format.",
            "content": {
              "application/json": {
                "schema": {
                  "type": "object",
                  "items": {"$ref": "#/components/schemas/Software"}
                }
              }

            }
          }
        },
        "parameters": [
          {
            "$ref": "#/components/parameters/data-provider"
          },
          {
            "$ref": "#/components/parameters/count"
          }
        ]
      }
    }

  },
  "components": {
    "schemas": {
      "Software": {
        "allOf": [
          {
            "$ref": "#/components/schemas/NewSoftware"
          },
          {
            "type": "object",
            "required": [
              "software_id",
              "name",
              "redirect_uris"
            ],
            "properties": {
              "id": {
                "type": "integer",
                "format": "int64"
              }
            }
          }
        ]
      },
      "NewSoftware": {
        "type": "object",
        "required": [
          "name",
          "software_id",
          "redirect_uris"
        ],
        "properties": {
          "name": {
            "type": "string"
          },
          "software_id": {
            "type": "string"
          },
          "redirect_uris": {
            "type": "string"
          }
        }
      },
      "Error": {
        "type": "object",
        "required": [
          "code",
          "message"
        ],
        "properties": {
          "code": {
            "type": "integer",
            "format": "int32"
          },
          "message": {
            "type": "string"
          }
        }
      }
    },

    "parameters": {
      "data-provider": {
        "name": "data-provider",
        "in": "path",
        "description": "id of the resource (\u003dResource.id)",
        "required": true,
        "allowEmptyValue": false,
        "style": "simple",
        "schema": {
          "type": "string"
        }
      },
      "count": {
        "name": "_count",
        "in": "query",
        "description": "The maximum number of search results on a page. The server is not bound to return the number requested, but cannot return more",
        "schema": {
          "type": "number"
        }
      }
    }
  }
}


