        "/api/v5/trade/amend-order": {
            "post": {
                "tags": [
                    "Trade"
                ],
                "summary": "改单",
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "type": "object",
                                "required": [
                                    "instId"
                                ],
                                "properties": {
                                    "instId": {
                                        "type": "string",
                                        "description": "必填<br>产品ID，如：`BTC-USDT`"
                                    },
                                    "ordId": {
                                        "type": "string",
                                        "description": "可选<br>订单ID， ordId和clOrdId必须传一个，若传两个，以ordId为主。"
                                    },
                                    "clOrdId": {
                                        "type": "string",
                                        "description": "可选<br>客户自定义订单ID<br>字母（区分大小写）与数字的组合，可以是纯字母、纯数字且长度要在1-32位之间。"
                                    },
                                    "newSz": {
                                        "type": "string",
                                        "description": "可选<br>修改的新数量，newSz和newPx不可同时为空。对于部分成交订单，该数量应包含已成交数量。"
                                    },
                                    "newPx": {
                                        "type": "string",
                                        "description": "可选<br>修改的新价格"
                                    },
                                    "cxlOnFail": {
                                        "type": "boolean",
                                        "description": "非必填<br>`false`：不自动撤单 `true`：自动撤单 当订单修改失败时，该订单是否需要自动撤销。默认为`false`"
                                    },
                                    "reqId": {
                                        "type": "string",
                                        "description": "非必填<br>用户自定义修改事件ID，字母（区分大小写）与数字的组合，可以是纯字母、纯数字且长度要在1-32位之间。"
                                    }
                                }
                            },
                            "example": {
                                "ordId": "string",
                                "newSz": "0.2",
                                "instId": "BTC-USDT"
                            }
                        }
                    },
                    "required": true
                },
                "responses": {
                    "200": {
                        "description": "成功",
                        "content": {
                            "application/json": {
                                "schema": {
                                    "type": "object",
                                    "additionalProperties": {
                                        "type": "object",
                                        "properties": {}
                                    },
                                    "example": {
                                        "code": "0",
                                        "msg": "",
                                        "data": [
                                            {
                                                "clOrdId": "",
                                                "ordId": "string",
                                                "reqId": "string",
                                                "sCode": "0",
                                                "sMsg": ""
                                            }
                                        ]
                                    }
                                }
                            }
                        }
                    }
                }
            }
        },
