        },
        "/api/v5/trade/close-position": {
            "post": {
                "tags": [
                    "Trade"
                ],
                "summary": "仓位市价全平",
                "requestBody": {
                    "content": {
                        "application/json": {
                            "schema": {
                                "type": "object",
                                "required": [
                                    "instId",
                                    "mgnMode"
